INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(51, 26, 'vn', '5 bí quyết chăm sóc da mặt của con gái Hàn', 'Để sở hữu làn da căng bóng, mịn màng, phụ nữ Hàn Quốc phải thực hiện quá trình chăm sóc da với nhiều bước quan trọng và nghiêm ngặt, đặc biệt phải thật chăm chỉ.', '<p>Để sở hữu l&agrave;n da căng b&oacute;ng, mịn m&agrave;ng, phụ nữ H&agrave;n Quốc phải thực hiện qu&aacute; tr&igrave;nh chăm s&oacute;c da với nhiều bước quan trọng v&agrave; nghi&ecirc;m ngặt, đặc biệt phải thật chăm chỉ.</p>\r\n\r\n<p style="text-align:center"><img alt="" height="500" src="/image/images/1/bi-quyet-cham-soc-da-5.jpg" width="800" /></p>\r\n', '', '', '', '', '', 0, '5-bi-quyet-cham-soc-da-mat-cua-con-gai-han', '5 bí quyết chăm sóc da mặt của con gái Hàn', 'Thông tin mô tả ngắn cho bài Khuyến mãi 5', ''),
(52, 26, 'en', 'Khuyến mãi 5', 'Thông tin mô tả ngắn cho bài Khuyến mãi 5', '<p>Th&ocirc;ng tin m&ocirc; tả ngắn cho b&agrave;i Khuyến m&atilde;i 5</p>\r\n', '', '', '', '', '', 0, 'khuyen-mai-5', 'Khuyến mãi 5', 'Thông tin mô tả ngắn cho bài Khuyến mãi 5', ''),
(53, 27, 'vn', '7 mẹo chăm sóc da dầu bạn gái nên biết', 'Bạn cần biết cách kiểm soát lượng dầu khiến khuôn mặt nhờn bóng, kém xinh khi sở hữu làn da dầu.', '<h3><strong>B&iacute; quyết l&agrave;m đẹp 1. Lu&ocirc;n lu&ocirc;n thử trước khi mua</strong></h3>\r\n\r\n<p>C&oacute; một số sản phẩm trang điểm, bạn thực sự cần phải thử trước khi mua ch&uacute;ng. Bạn c&oacute; thế mua ngay m&agrave; kh&ocirc;ng cần đắn đo như b&uacute;t kẻ mắt, phấn mắt v&agrave; son b&oacute;ng; nhưng bạn cần phải kiểm tra bột, son m&ocirc;i, phấn nền v&agrave; m&aacute; hồng. V&igrave; để an to&agrave;n cho l&agrave;n da cũng như gi&uacute;p bạn tỏa s&aacute;ng hơn, th&igrave; l&agrave;m kiểm tra với sản phẩm trang điểm kh&ocirc;ng phải l&agrave; thừa, đ&acirc;y cũng c&oacute; thể xem như 1 b&iacute; quyết l&agrave;m đẹp. Bởi v&igrave;, c&oacute; một số son hay phấn chứa c&aacute;c chất h&oacute;a học khiến cho da bạn trở n&ecirc;n tệ hơn khi trang điểm một thời gian d&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" height="533" src="/image/images/1/bi-quyet-nan-mun-khong-de-lai-seo2.jpg" width="800" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>B&iacute; quyết l&agrave;m đẹp 2. Kh&ocirc;ng n&ecirc;n bỏ qua bơ hạt mỡ (shea butter)</strong></h3>\r\n\r\n<p>Kh&ocirc;ng c&oacute; c&ocirc; g&aacute;i n&agrave;o th&iacute;ch l&agrave;m đẹp m&agrave; kh&ocirc;ng biết đến bơ hạt mỡ. N&oacute; được lưu trong b&iacute; quyết l&agrave;m đẹp của rất nhiều chị em. Bạn c&oacute; thể sử dụng bơ hạt mỡ gi&uacute;p loại bỏ lớp trang điểm, l&agrave;m son b&oacute;ng hoặc l&agrave;m mềm cho đ&ocirc;i m&ocirc;i của bạn. Bơ hạt mỡ l&agrave; nguy&ecirc;n liệu l&agrave;m mềm đ&ocirc;i tay v&agrave; b&agrave;n ch&acirc;n tuyệt vời, v&agrave;o những ng&agrave;y trời lạnh bạn kh&ocirc;ng phải lo lắng v&igrave; da kh&ocirc; nữa. Nhưng bạn h&atilde;y thận trọng trong liều lượng sử dụng đặc biệt l&agrave; với da mặt, bởi nếu sử dụng qu&aacute; nhiều c&oacute; thể l&agrave;m g&acirc;y tắc nghẽn lỗ ch&acirc;n l&ocirc;ng v&agrave; g&acirc;y ra mụn.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', 1, '7-meo-cham-soc-da-dau-ban-gai-nen-biet', '7 mẹo chăm sóc da dầu bạn gái nên biết', '', ''),
(54, 27, 'en', 'Khuyễn mãi 6', 'Thông tin mô tả ngắn cho bài Khuyến mãi 6', '<p>Thông tin mô tả ngắn cho bài Khuyến mãi 6</p>\r\n', '', '', '', '', '', 1, 'khuyen-mai-6', 'Khuyễn mãi 6', 'Thông tin mô tả ngắn cho bài Khuyến mãi 6', ''),
(61, 31, 'vn', '''Hot girl bánh mướt'' ở Nghệ An bỗng dưng nổi tiếng trên mạng', 'Cô gái xinh xắn, chăm chỉ bán bánh mướt ở chân cầu Nghệ An hiện khiến nhiều dân mạng tìm kiếm thông tin.', '<p>Ng&agrave;y 27/4, khoảnh khắc c&ocirc; g&aacute;i b&aacute;n b&aacute;nh mướt xinh đẹp ngồi dưới ch&acirc;n cầu ở Nghệ An được nhiều người quan t&acirc;m. Bức ảnh đơn giản, song c&oacute; sức h&uacute;t với người xem bởi nữ ch&iacute;nh kh&aacute; xinh xắn.</p>\r\n\r\n<p>Chỉ v&agrave;i giờ sau khi đăng tải, b&agrave;i đăng n&agrave;y đ&atilde; nhận được h&agrave;ng ngh&igrave;n like (th&iacute;ch), chia sẻ từ d&acirc;n mạng. Phần lớn mọi người d&agrave;nh nhiều lời khen cho c&ocirc; g&aacute;i.</p>\r\n\r\n<p>Theo đ&oacute;, nữ ch&iacute;nh c&ograve;n rất trẻ, sở hữu một khu&ocirc;n mặt ưa nh&igrave;n, t&oacute;c buộc gọn g&agrave;ng, l&agrave;n da trắng hồng, đang chăm chỉ b&aacute;n h&agrave;ng.</p>\r\n\r\n<p>C&ocirc; g&aacute;i Nghệ An b&aacute;n b&aacute;nh khiến nhiều người t&ograve; m&ograve; l&agrave; Nguyễn Thị Kim Kh&aacute;nh, 20 tuổi. Hiện c&ocirc; l&agrave;m việc tại một spa l&agrave;m đẹp ở TP Vinh, Nghệ An.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" height="801" src="/image/images/1/27751777_894114804097098_7991381483777644942_n.jpg" width="800" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kim Kh&aacute;nh cho&nbsp;<em>Zing.vn&nbsp;</em>hay c&ocirc; kh&aacute; bất ngờ khi bức ảnh được chia sẻ tr&ecirc;n mạng. 9X kh&ocirc;ng biết khoảnh khắc n&agrave;y được chụp từ khi n&agrave;o.</p>\r\n\r\n<p>&quot;Ng&agrave;y n&agrave;o m&igrave;nh cũng phụ mẹ b&aacute;n h&agrave;ng từ 8-9h s&aacute;ng. Thời gian trước, m&igrave;nh vẫn l&agrave;m từ s&aacute;ng tới trưa, rồi c&ugrave;ng mẹ dọn h&agrave;ng&quot;, Kh&aacute;nh n&oacute;i.</p>\r\n', '', '', '', '', '', 1, 'hot-girl-banh-muot-o-nghe-an-bong-dung-noi-tieng-tren-mang', '''Hot girl bánh mướt'' ở Nghệ An bỗng dưng nổi tiếng trên mạng', '', ''),
(62, 31, 'en', 'news 4', 'Mô tả tin tức 4', '<p>Nội dung tin tức 4 en</p>\r\n', '', '', '', '', '', 1, 'tin-tuc-4', 'Tin tức 4', '', ''),
(95, 48, 'vn', 'Ngày đầu khai trương, trung tâm làm đẹp của Hoa hậu Hải Dương chật kín khách', 'Sáng nay, trung tâm làm đẹp HD Korea Beauty Center của Hoa hậu Hải Dương đã chính thức khai trương tại địa chỉ 186A Nam kỳ Khởi Nghĩa Quận 3. ', '<h3 style="text-align:center"><img alt="" height="533" src="/image/images/1/img20171130115922356.jpg" width="800" /></h3>\r\n', 'Central swimming pool', '', '', '', '', 1, 'ngay-dau-khai-truong-trung-tam-lam-dep-cua-hoa-hau-hai-duong-chat-kin-khach', 'Ngày đầu khai trương, trung tâm làm đẹp của Hoa hậu Hải Dương chật kín khách', '', 'myichy, my ichy, công ty dược mỹ phẩm myichy, công ty dược mỹ phẩm my ichy'),
(96, 48, 'en', 'Tổng hợp những loại thực phẩm có lợi ích cho tuyến giáp của bạn', 'Tổng hợp những loại thực phẩm có lợi ích cho tuyến giáp của bạn', '<h3><strong>Ti&ecirc;u thụ c&aacute; hồi</strong></h3>\r\n\r\n<p>Những người mắc bệnh vi&ecirc;m tuyến gi&aacute;p Hashimoto thường c&oacute; nồng độ vitamin D trong cơ thể thấp hơn so với người b&igrave;nh thường. Thiếu hụt hợp chất n&agrave;y sẽ l&agrave;m tăng kh&aacute;ng thể tuyến gi&aacute;p. Những kh&aacute;ng thể n&agrave;y t&aacute;c động tới hệ miễn dịch, khiến bộ phận n&agrave;y tấn c&ocirc;ng c&aacute;c m&ocirc; tuyến gi&aacute;p. Lisa Markley, chuy&ecirc;n gia dinh dưỡng ki&ecirc;m đồng t&aacute;c giả của cuốn The Essential Essentials Thyroid Cookbook giải th&iacute;ch, hiện tượng n&agrave;y g&acirc;y vi&ecirc;m v&agrave; l&agrave;m giảm hoạt động của tuyến gi&aacute;p.</p>\r\n\r\n<p>Do đ&oacute;, mọi người cần cung cấp một lượng lớn vitamin D cho cơ thể mỗi ng&agrave;y để tuyến gi&aacute;p hoạt động tốt nhất. C&aacute; hồi l&agrave; loại thực phẩm h&agrave;ng đầu cung cấp nhiều chất n&agrave;y. Ngo&agrave;i ra, bạn c&oacute; thể dễ d&agrave;ng t&igrave;m thấy vitamin D trong nước cam &eacute;p, sữa v&agrave; trứng.</p>\r\n', '', '', '', '', '', 1, 'post-17', 'Tổng hợp những loại thực phẩm có lợi ích cho tuyến giáp của bạn', '', ''),
(97, 49, 'vn', 'Bí mật đằng sau nhan sắc tuyệt trần của tứ đại mỹ nhân lừng danh Trung Quốc', 'Người ta nói, nhan sắc của các mỹ nhân lừng danh này là một thứ "tài năng" thiên bẩm, sinh ra đã có, tuy nhiên mỗi người đều giữ cho mình một bí quyết riêng để giữ cho nhan sắc trời ban trường tồn với thời gian. ', '<p style="text-align:center"><img alt="" height="600" src="/image/images/1/photo-5-1513243570642.jpg" width="800" /></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" height="589" src="/image/images/1/20150320-015857-khan-gia-trung-quoc-dien-dao-vi-angela-baby-qua-xinh_520x383.png" width="800" /></p>\r\n', 'Central swimming pool', '', '', '', '', 1, 'bi-mat-dang-sau-nhan-sac-tuyet-tran-cua-tu-dai-my-nhan-lung-danh-trung-quoc', 'Bí mật đằng sau nhan sắc tuyệt trần của tứ đại mỹ nhân lừng danh Trung Quốc', '', ''),
(98, 49, 'en', '5 thói quen giúp người trẻ tránh xa bệnh tim mạch', '5 thói quen giúp người trẻ tránh xa bệnh tim mạch', '<p>Theo GS.TS.BS. Đỗ Do&atilde;n Lợi, Viện trưởng Viện Tim mạch Việt Nam, bệnh l&iacute; tim mạch đang ng&agrave;y c&agrave;ng trẻ h&oacute;a: Cứ 4 người trẻ Việt Nam từ 25 tuổi trở l&ecirc;n, c&oacute; &iacute;t nhất 1 người c&oacute; nguy cơ mắc bệnh tim mạch.</p>\r\n\r\n<p>Bệnh tim mạch để lại những hệ luỵ kh&ocirc;n lường, ảnh hưởng đến chất lượng cuộc sống.</p>\r\n\r\n<p>Để tr&aacute;nh xa bệnh tim mạch kh&ocirc;ng qu&aacute; kh&oacute;, 5 thay đổi nhỏ trong cuộc sống h&agrave;ng ng&agrave;y sau đ&acirc;y sẽ gi&uacute;p bạn.</p>\r\n\r\n<h3><strong>Ngủ đủ giấc</strong></h3>\r\n\r\n<p>Thức khuya, ngủ kh&ocirc;ng đủ giấc kh&ocirc;ng những l&agrave;m hệ thống miễn dịch của cơ thể suy yếu dần m&agrave; c&ograve;n tăng tỉ lệ mắc bệnh tim mạch. Khi thiếu ngủ, c&aacute;c mạch m&aacute;u co lại, huyết &aacute;p tăng, tạo &aacute;p lực l&ecirc;n tim nhiều hơn.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến c&aacute;c bệnh l&iacute; tim mạch như: bệnh mạch v&agrave;nh, xơ vữa động mạch, cao huyết &aacute;p...</p>\r\n\r\n<p>Ngủ đủ giấc từ 7 - 8 tiếng/ng&agrave;y l&agrave; thay đổi nhỏ đầu ti&ecirc;n v&agrave; cũng hết sức đơn giản gi&uacute;p ngăn ngừa bệnh l&iacute; tim mạch.</p>\r\n\r\n<p>Để c&oacute; giấc ngủ s&acirc;u v&agrave; ngon, duy tr&igrave; nhịp sinh học của cơ thể một c&aacute;ch c&acirc;n bằng, bạn n&ecirc;n đi ngủ đ&uacute;ng giờ, kh&ocirc;ng thức qu&aacute; khuya, tr&aacute;nh ăn no hoặc uống nhiều nước trước khi đi ngủ.</p>\r\n\r\n<h3><strong>Tập thể dục thường xuy&ecirc;n</strong></h3>\r\n\r\n<p>Người trẻ thường hay than thở thời gian ăn, ngủ, l&agrave;m việc c&ograve;n chưa đủ huống chi l&agrave; tập thể dục mỗi ng&agrave;y.</p>\r\n\r\n<p>Nhưng thật ra nếu biết c&aacute;ch sắp sếp c&ocirc;ng việc, bạn c&oacute; thể đi ngủ v&agrave;o 11g đ&ecirc;m, thức dậy v&agrave;o 6 - 7g s&aacute;ng h&ocirc;m sau để c&oacute; thời gian luyện tập thể dục trước khi c&oacute; mặt ở văn ph&ograve;ng l&agrave;m việc.</p>\r\n\r\n<p>GS.TS.BS. Đỗ Do&atilde;n Lợi chia sẻ &ldquo;N<em>gười trẻ cần d&agrave;nh thời gian hoạt động thể lực từ 30 - 60 ph&uacute;t/ng&agrave;y, &iacute;t nhất 4 ng&agrave;y/tuần, tốt nhất l&agrave; đều đặn h&agrave;ng ng&agrave;y để tăng cường sức khỏe tim mạch&rdquo;.</em></p>\r\n\r\n<p>Những b&agrave;i tập thể dục nhẹ nh&agrave;ng như: đi bộ, chạy bộ, đạp xe, bơi, yoga rất tốt cho sức khỏe tr&aacute;i tim, gi&uacute;p ph&ograve;ng tr&aacute;nh c&aacute;c bệnh mạch v&agrave;nh, tăng huyết &aacute;p, đột quỵ.</p>\r\n', '', '', '', '', '', 1, 'post-18', '5 thói quen giúp người trẻ tránh xa bệnh tim mạch', '', ''),
(99, 50, 'vn', 'Những mỹ nhân Việt chăm chỉ đi spa làm đẹp nhất showbiz', 'Luôn xuất hiện rạng ngời hay thậm chí để mặt mộc, không phải ngẫu nhiên mà các mỹ nhân này lại có thể tự tin đến như vậy. ', '<p>Mary Ellen Phipps, chuy&ecirc;n gia dinh dưỡng v&agrave; l&agrave; &ocirc;ng chủ của Milk &amp; Honey Nutrition cho biết: &quot;Điều quan trọng l&agrave; phải nhớ rằng mỗi cơ thể đều c&oacute; đặc t&iacute;nh ri&ecirc;ng v&agrave; kh&aacute;c biệt n&ecirc;n kh&ocirc;ng thể lấy phương ph&aacute;p của người n&agrave;y để &aacute;p dụng cho người kh&aacute;c&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" height="450" src="/image/images/1/diem-mat-loat-sao-viet-khien-fan-ngo-ngang-vi-giong-nhau-nhu-chi-em-that-lac-5bc09b25-1523186298-960-width1920height1080.jpg" width="800" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với kh&aacute;ch h&agrave;ng low-carb của m&igrave;nh, Phipps khuyến nghị chỉ n&ecirc;n ti&ecirc;u thụ 30-80g carbohydrate mỗi ng&agrave;y, t&ugrave;y thuộc v&agrave;o t&igrave;nh trạng cơ thể từng người. H&agrave;m lượng n&agrave;y chia đều c&aacute;c bữa như sau: Khoảng 12-25g cho bữa s&aacute;ng, 20-30g cho bữa trưa, 15-25g cho bữa tối v&agrave; 0-10g cho bữa ăn nhẹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Central swimming pool', '', '', '', '', 1, 'nhung-my-nhan-viet-cham-chi-di-spa-lam-dep-nhat-showbiz', 'Những mỹ nhân Việt chăm chỉ đi spa làm đẹp nhất showbiz', 'a', '1'),
(100, 50, 'en', '20 thực phẩm lành mạnh', '20 thực phẩm lành mạnh', '<p><strong>Lorem Ipsum</strong>&nbsp;is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry&#39;s standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.</p>\r\n\r\n<h2>Why do we use it?</h2>\r\n\r\n<p>It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using &#39;Content here, content here&#39;, making it look like readable English. Many desktop publishing packages and web page editors now use Lorem Ipsum as their default model text, and a search for &#39;lorem ipsum&#39; will uncover many web sites still in their infancy. Various versions have evolved over the years, sometimes by accident, sometimes on purpose (injected humour and the like).</p>\r\n', '', '', '', '', '', 1, 'post-19', '20 thực phẩm lành mạnh', 'b', '2'),
(101, 51, 'vn', 'Những cách trị mụn hiệu quả đơn giản ', 'Mụn là một bệnh lý thường gặp ở tất cả mọi người, nếu không được xử lý và điều trị kịp thời mụn sẽ để lại sẹo. Bài viết sau đây sẽ giới thiệu cho các bạn những cách trị mụn hiệu quả đơn giản hiện nay. ', '<p dir="ltr" style="text-align:justify"><span style="color:#000000"><strong><em>Mụn l&agrave; một bệnh l&yacute; thường gặp ở tất cả mọi người, nếu kh&ocirc;ng được xử l&yacute; v&agrave; điều trị kịp thời mụn sẽ để lại sẹo. B&agrave;i viết sau đ&acirc;y sẽ giới thiệu cho c&aacute;c bạn những c&aacute;ch trị mụn hiệu quả đơn giản hiện nay.</em></strong></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Những</span><a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#000000"> </span><span style="color:#2980b9"><strong>c&aacute;ch trị mụn hiệu quả</strong> </span></a><span style="color:#000000">đơn giản sau đ&acirc;y sẽ gi&uacute;p cho bạn c&oacute; thể loại bỏ ho&agrave;n to&agrave;n những ph&acirc;n v&acirc;n cũng như sự dai dẳng kh&oacute; chịu của c&aacute;c loại mụn tr&ecirc;n gương mặt của c&aacute;c bạn. &nbsp;</span></p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua" height="600" src="/image/images/cach-tri-mun-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><span style="color:#000000"><strong>Trị mụn hiệu quả bằng c&agrave; rốt</strong></span></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Một trong những </span><a href="http://myichy.vn/nhung-cach-tri-mun-hieu-qua-don-gian"><span style="color:#3498db"><strong>c&aacute;ch trị mụn hiệu quả</strong></span></a><span style="color:#000000"> ch&iacute;nh l&agrave; bằng c&agrave; rốt ch&iacute;nh với c&aacute;ch ăn trực tiếp mỗi ng&agrave;y hoặc d&ugrave;ng chung với những loại thực phẩm kh&aacute;c, việc ăn c&agrave; rốt sẽ lu&ocirc;n gi&uacute;p bạn trị được mụn rất hiệu quả.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">B&ecirc;n cạnh đ&oacute;, bạn cũng c&oacute; thể d&ugrave;ng c&agrave; rốt &eacute;p ra th&agrave;nh nước để uống, c&ograve;n phần x&aacute;c c&agrave; rốt th&igrave; d&ugrave;ng để đắp l&ecirc;n những v&ugrave;ng da đang bị mụn, mỗi ng&agrave;y bạn n&ecirc;n thực hiện một lần để mụn kh&ocirc;ng c&ograve;n sưng m&agrave; xẹp hẳn cũng kh&ocirc;ng c&ograve;n g&acirc;y ngứa.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><span style="color:#000000"><strong>Trị mụn hiệu quả bằng quả c&agrave; chua</strong></span></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch chữa trị mụn bằng c&agrave; chua l&agrave; một trong những phương ph&aacute;p rất đơn giản m&agrave; bạn c&oacute; thể &aacute;p dụng ở bất kỳ đ&acirc;u. Bạn c&oacute; thể d&ugrave;ng những quả c&agrave; chua rửa sạch sau đ&oacute; cắt th&agrave;nh những l&aacute;t mỏng để đắp l&ecirc;n những v&ugrave;ng da đang bị mụn mủ.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Việc đắp c&agrave; chua trực tiếp như vậy sẽ gi&uacute;p cho da hấp thu được những lượng dưỡng chất cũng như c&aacute;c vitamin c&oacute; trong quả c&agrave; chua được ho&agrave;n to&agrave;n hấp thụ qua l&agrave;n da cũng như c&oacute; thể hỗ trợ bạn ti&ecirc;u diệt được những loại vi khuẩn đang g&acirc;y n&ecirc;n mụn. Đồng thời cũng giảm được t&iacute;nh vi&ecirc;m nhiễm cũng như c&aacute;c nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave;m n&ecirc;n những mụn mủ tr&ecirc;n gương mặt bạn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua" height="382" src="/image/images/cach-tri-mun-hieu-qua%20(3).jpg" width="600" /></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:20px"><strong>Trị mụn hiệu quả bằng tỏi</strong></span></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Rất đơn giản l&agrave; bạn chỉ cần b&oacute;c vỏ tỏi rồi cắt nhỏ ra, ch&agrave; s&aacute;t trực tiếp l&ecirc;n v&ugrave;ng da bị mụn mủ, massage nhẹ nh&agrave;ng rồi rửa lại với nước sạch. T&igrave;nh trạng mụn mủ sẽ giảm hẳn sau khi thực hiện. Hoặc bạn cũng c&oacute; thể d&ugrave;ng c&aacute;ch l&agrave; xay nhuyễn v&agrave; &eacute;p lấy nước tỏi rồi đắp l&ecirc;n da, dung dịch nước tỏi sẽ thẩm thấu qua da tốt hơn, gi&uacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, l&agrave;m mềm da v&agrave; hỗ trợ cho da th&ecirc;m săn chắc, tr&aacute;nh t&igrave;nh trạng vi&ecirc;m nhiễm v&agrave; ngứa ng&aacute;y cho da.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua" height="450" src="/image/images/cach-tri-mun-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong><span style="font-size:20px">Trị mụn bằng kem ủ Face Saffron &amp; Cherry Nhật Bản</span></strong></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Kem ủ Face Saffron &amp; Cherry Nhật Bản được chiết xuất từ tinh hoa Cherry v&agrave; Saffron c&ugrave;ng những loại vitamin nhằm cung cấp cho da những dưỡng chất cần thiết nhất để nu&ocirc;i dưỡng những tế b&agrave;o da, l&agrave;m cho cho da sạch. B&ecirc;n cạnh đ&oacute; c&ograve;n gi&uacute;p cho da mịn m&agrave;ng, tươi trẻ v&agrave; lu&ocirc;n l&atilde;o h&oacute;a.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nếu bạn c&oacute; những thắc mắc n&agrave;o về c&aacute;c c&aacute;ch trị mụn hiệu quả hoặc sản phẩm chăm s&oacute;c da tr&ecirc;n đ&acirc;y h&atilde;y truy cập v&agrave;o trang web </span><a href="http://www.myichy.vn"><span style="color:#000000">www.myichy.vn</span></a><span style="color:#000000"> để được hỗ trợ v&agrave; tư vấn tốt nhất.</span></p>\r\n', '', '', '', '', '', 0, 'nhung-cach-tri-mun-hieu-qua-don-gian', 'Những cách trị mụn hiệu quả đơn giản ', 'Mụn là một bệnh lý thường gặp ở tất cả mọi người, nếu không được xử lý và điều trị kịp thời mụn sẽ để lại sẹo. Bài viết sau đây sẽ giới thiệu cho các bạn những cách trị mụn hiệu quả đơn giản hiện nay. ', 'cách trị mụn hiệu quả'),
(102, 51, 'en', 'english version Những cách trị mụn hiệu quả đơn giản ', 'english version Mụn là một bệnh lý thường gặp ở tất cả mọi người, nếu không được xử lý và điều trị kịp thời mụn sẽ để lại sẹo. Bài viết sau đây sẽ giới thiệu cho các bạn những cách trị mụn hiệu quả đơn giản hiện nay. ', 'english version <p dir="ltr" style="text-align:justify"><em><span style="color:#000000"><span style="font-size:16px">Mụn l&agrave; một bệnh l&yacute; thường gặp ở tất cả mọi người, nếu kh&ocirc;ng được xử l&yacute; v&agrave; điều trị kịp thời mụn sẽ để lại sẹo. B&agrave;i viết sau đ&acirc;y sẽ giới thiệu cho c&aacute;c bạn những c&aacute;ch trị mụn hiệu quả đơn giản hiện nay. </span></span></em></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><span style="font-size:16px">Những</span></span><span style="font-size:16px"><strong><a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#3498db"> c&aacute;ch trị mụn hiệu quả </span></a></strong></span><span style="color:#000000"><span style="font-size:16px">đơn giản sau đ&acirc;y sẽ gi&uacute;p cho bạn c&oacute; thể loại bỏ ho&agrave;n to&agrave;n những ph&acirc;n v&acirc;n cũng như sự dai dẳng kh&oacute; chịu của c&aacute;c loại mụn tr&ecirc;n gương mặt của c&aacute;c bạn. &nbsp;</span></span></p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua" height="600" src="/image/images/cach-tri-mun-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000"><span style="font-size:16px">Trị mụn hiệu quả bằng c&agrave; rốt</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">Một trong những <strong>c&aacute;ch trị mụn hiệu quả</strong> ch&iacute;nh l&agrave; bằng c&agrave; rốt ch&iacute;nh với c&aacute;ch ăn trực tiếp mỗi ng&agrave;y hoặc d&ugrave;ng chung với những loại thực phẩm kh&aacute;c, việc ăn c&agrave; rốt sẽ lu&ocirc;n gi&uacute;p bạn trị được mụn rất hiệu quả. </span></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">B&ecirc;n cạnh đ&oacute;, bạn cũng c&oacute; thể d&ugrave;ng c&agrave; rốt &eacute;p ra th&agrave;nh nước để uống, c&ograve;n phần x&aacute;c c&agrave; rốt th&igrave; d&ugrave;ng để đắp l&ecirc;n những v&ugrave;ng da đang bị mụn, mỗi ng&agrave;y bạn n&ecirc;n thực hiện một lần để mụn kh&ocirc;ng c&ograve;n sưng m&agrave; xẹp hẳn cũng kh&ocirc;ng c&ograve;n g&acirc;y ngứa.</span></span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000"><span style="font-size:16px">Trị mụn hiệu quả bằng quả c&agrave; chua</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">C&aacute;ch chữa trị mụn bằng c&agrave; chua l&agrave; một trong những phương ph&aacute;p rất đơn giản m&agrave; bạn c&oacute; thể &aacute;p dụng ở bất kỳ đ&acirc;u. Bạn c&oacute; thể d&ugrave;ng những quả c&agrave; chua rửa sạch sau đ&oacute; cắt th&agrave;nh những l&aacute;t mỏng để đắp l&ecirc;n những v&ugrave;ng da đang bị mụn mủ. </span></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">Việc đắp c&agrave; chua trực tiếp như vậy sẽ gi&uacute;p cho da hấp thu được những lượng dưỡng chất cũng như c&aacute;c vitamin c&oacute; trong quả c&agrave; chua được ho&agrave;n to&agrave;n hấp thụ qua l&agrave;n da cũng như c&oacute; thể hỗ trợ bạn ti&ecirc;u diệt được những loại vi khuẩn đang g&acirc;y n&ecirc;n mụn. Đồng thời cũng giảm được t&iacute;nh vi&ecirc;m nhiễm cũng như c&aacute;c nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave;m n&ecirc;n những mụn mủ tr&ecirc;n gương mặt bạn.</span></span></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><span style="font-size:16px"><img alt="cach-tri-mun-hieu-qua" height="382" src="/image/images/cach-tri-mun-hieu-qua%20(3).jpg" width="600" /></span></span></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000"><span style="font-size:16px">Trị mụn hiệu quả bằng tỏi</span></span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">Rất đơn giản l&agrave; bạn chỉ cần b&oacute;c vỏ tỏi rồi cắt nhỏ ra, ch&agrave; s&aacute;t trực tiếp l&ecirc;n v&ugrave;ng da bị mụn mủ, massage nhẹ nh&agrave;ng rồi rửa lại với nước sạch. T&igrave;nh trạng mụn mủ sẽ giảm hẳn sau khi thực hiện. Hoặc bạn cũng c&oacute; thể d&ugrave;ng c&aacute;ch l&agrave; xay nhuyễn v&agrave; &eacute;p lấy nước tỏi rồi đắp l&ecirc;n da, dung dịch nước tỏi sẽ thẩm thấu qua da tốt hơn, gi&uacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, l&agrave;m mềm da v&agrave; hỗ trợ cho da th&ecirc;m săn chắc, tr&aacute;nh t&igrave;nh trạng vi&ecirc;m nhiễm v&agrave; ngứa ng&aacute;y cho da.</span></span></p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua" height="450" src="/image/images/cach-tri-mun-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000"><span style="font-size:16px">Trị mụn bằng kem ủ Face Saffron &amp; Cherry Nhật Bản</span></span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000"><span style="font-size:16px">Kem ủ Face Saffron &amp; Cherry Nhật Bản được chiết xuất từ tinh hoa Cherry v&agrave; Saffron c&ugrave;ng những loại vitamin nhằm cung cấp cho da những dưỡng chất cần thiết nhất để nu&ocirc;i dưỡng những tế b&agrave;o da, l&agrave;m cho cho da sạch. B&ecirc;n cạnh đ&oacute; c&ograve;n gi&uacute;p cho da mịn m&agrave;ng, tươi trẻ v&agrave; lu&ocirc;n l&atilde;o h&oacute;a. </span></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="font-size:16px"><span style="color:#000000">Nếu bạn c&oacute; những thắc mắc n&agrave;o về c&aacute;c c&aacute;ch trị mụn hiệu quả hoặc sản phẩm chăm s&oacute;c da tr&ecirc;n đ&acirc;y h&atilde;y truy cập v&agrave;o trang web </span><strong><a href="http://www.myichy.vn"><span style="color:#3498db">www.myichy.vn</span></a></strong><span style="color:#000000"> để được hỗ trợ v&agrave; tư vấn tốt nhất. </span></span></p>\r\n', '', '', '', '', '', 0, 'en-nhung-cach-tri-mun-hieu-qua-don-gian', 'Những cách trị mụn hiệu quả đơn giản ', 'Mụn là một bệnh lý thường gặp ở tất cả mọi người, nếu không được xử lý và điều trị kịp thời mụn sẽ để lại sẹo. Bài viết sau đây sẽ giới thiệu cho các bạn những cách trị mụn hiệu quả đơn giản hiện nay. ', 'cách trị mụn hiệu quả'),
(103, 52, 'vn', 'Những cách trị mụn hiệu quả sau 1 đêm', 'Mụn luôn làm cho bạn khó chịu và đau đớn, bên cạnh đó còn ảnh hưởng đến nhan sắc của bạn. Chính vì vậy, bài viết sau đây chúng tôi sẽ chia sẻ những cách trị mụn hiệu quả sau 1 đêm đến với mọi người. ', '<p style="text-align:justify"><strong><em><span style="color:#000000">Mụn lu&ocirc;n l&agrave;m cho bạn kh&oacute; chịu v&agrave; đau đớn, b&ecirc;n cạnh đ&oacute; c&ograve;n ảnh hưởng đến nhan sắc của bạn. Ch&iacute;nh v&igrave; vậy, b&agrave;i viết sau đ&acirc;y ch&uacute;ng t&ocirc;i sẽ chia sẻ những c&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m đến với mọi người.</span></em></strong></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Mụn xuất hiện với nhiều nguy&ecirc;n nh&acirc;n kh&aacute;c nhau, tuy nhi&ecirc;n với những</span><a href="http://myichy.vn/bot-rua-mat"><span style="color:#3498db"> <strong>c&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m</strong></span></a><span style="color:#000000"> dưới đ&acirc;y, bạn c&oacute; thể ho&agrave;n to&agrave;n khoe da mặt của m&igrave;nh v&agrave;o s&aacute;ng h&ocirc;m sau.</span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua-sau-1-dem" height="392" src="/image/images/cach-tri-mun-hieu-qua-sau-1-dem%20(1).jpg" width="600" /></p>\r\n\r\n<p style="text-align:center"><em><span style="color:#000000">C&aacute;ch trị mụn bằng đ&aacute; lạnh</span></em></p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Đ&aacute; lạnh c&oacute; c&ocirc;ng dụng tăng tuần ho&agrave;n m&aacute;u v&agrave; c&oacute; thể chống oxy h&oacute;a hiệu quả. N&oacute; gi&uacute;p loại bỏ những chất bẩn để khỏi bị ảnh hưởng trong việc thắt chặt c&aacute;c lỗ ch&acirc;n l&ocirc;ng. Khi bạn chườm đ&aacute; l&ecirc;n mặt th&igrave; c&aacute;c loại mụn sẽ giảm r&otilde; rệt trong chốc l&aacute;t.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn h&atilde;y lấy một &iacute;t đ&aacute; lạnh sau đ&oacute; quấn v&agrave;o vải sạch, nhẹ nh&agrave;ng chườm đ&aacute; l&ecirc;n mụn trong v&ograve;ng 2 ph&uacute;t. Chờ 20 gi&acirc;y sau th&igrave; lặp lại, cứ như vậy l&agrave;m từ 5 đến 10 lần để c&oacute; kết quả tốt hơn v&agrave;o s&aacute;ng mai.</span></p>\r\n\r\n<h2 style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng đu đủ</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Đu đủ l&agrave; một nguy&ecirc;n liệu thi&ecirc;n nhi&ecirc;n c&oacute; thể trị mụn tốt nhất, n&oacute; c&oacute; thể gi&uacute;p cho bạn loại bỏ những tế b&agrave;o da chết, đảm bảo những v&ugrave;ng da c&oacute; mụn sẽ bị kh&ocirc; lại v&agrave; ngăn chặn việc tạo mủ. B&ecirc;n cạnh đ&oacute;, trong đu đủ c&ograve;n chứa enzyme l&agrave; papain c&oacute; t&aacute;c dụng giảm vi&ecirc;m v&agrave; chống sưng da.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn cần chuẩn bị miếng đu đủ xanh v&agrave; xay nhuyễn, sau đ&oacute; đắp mặt nạ l&ecirc;n chỗ bị mụn từ 15 ph&uacute;t đến 25 ph&uacute;t v&agrave; kh&ocirc;ng chạm tay v&agrave;o. Sau đ&oacute; bạn h&atilde;y rửa mặt bằng nước ấm v&agrave; lau kh&ocirc;.</span></p>\r\n\r\n<h2 style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng tr&agrave; xanh</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">C&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m tiếp theo ch&iacute;nh l&agrave; tr&agrave; xanh, v&igrave; trong đ&oacute; c&oacute; những ph&acirc;n tử polyphenols, chất n&agrave;y sẽ l&agrave;m mụn giảm vi&ecirc;m sưng v&agrave; giết chết c&aacute;c vi khuẩn nhiễm tr&ugrave;ng, loại bỏ c&aacute;c da chết cũng như giảm nguy cơ nhiễm tr&ugrave;ng do mụn.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn h&atilde;y d&ugrave;ng một nắm l&aacute; tr&agrave; xanh sau đ&oacute; v&ograve; n&aacute;t v&agrave; cho v&agrave;o ấm nước s&ocirc;i, đợi khi tr&agrave; mềm th&igrave; lấy b&atilde; đắp l&ecirc;n v&ugrave;ng da bị mụn trong v&ograve;ng 15 ph&uacute;t, sau đ&oacute; rửa mặt lại một lần nữa để s&aacute;t khuẩn. Cứ như vậy &aacute;p dụng 3 lần trong một ng&agrave;y để c&oacute; kết quả tốt v&agrave;o s&aacute;ng h&ocirc;m sau.</span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua-sau-1-dem" height="800" src="/image/images/cach-tri-mun-hieu-qua-sau-1-dem%20(2).jpg" width="600" /></p>\r\n\r\n<h2 style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng bột rửa mặt Cherry</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bột rửa mặt Cherry c&oacute; thể đ&aacute;nh bay lớp tế b&agrave;o ở tr&ecirc;n da, cung cấp c&aacute;c dưỡng chất tuyệt vời mang lại một l&agrave;n da trắng hồng, hương hoa hồng c&oacute; thể kết hợp Lavender mang đến một hương thơm tự nhi&ecirc;n tạo n&ecirc;n cảm gi&aacute;c thoải m&aacute;i v&agrave; tư gi&atilde;n.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Th&agrave;nh phẩn của bột rửa mặt Cherry gồm c&oacute; tinh chất lavender, bột ngọc trai, chiết xuất hoa hồng, nước tinh khiết, cabomer, Grycerin...</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin về </span><strong><a href="http://myichy.vn/nhung-cach-tri-mun-hieu-qua-sau-1-dem"><span style="color:#3498db">c&aacute;ch trị mụn hiệu quả sau một đ&ecirc;m</span></a></strong><span style="color:#000000">, nếu bạn c&oacute; những thắc mắc n&agrave;o h&atilde;y truy cập v&agrave;o </span><a href="http://myichy.vn/"><span style="color:#2980b9"><strong>www.myichy.vn</strong></span></a><span style="color:#000000"> để được hỗ trợ v&agrave; tư vấn tốt nhất.</span></p>\r\n\r\n<p dir="ltr">&nbsp;</p>\r\n', '', '', '', '', '', 0, 'nhung-cach-tri-mun-hieu-qua-sau-1-dem', 'Những cách trị mụn hiệu quả sau 1 đêm', 'Mụn luôn làm cho bạn khó chịu và đau đớn, bên cạnh đó còn ảnh hưởng đến nhan sắc của bạn. Chính vì vậy, bài viết sau đây chúng tôi sẽ chia sẻ những cách trị mụn hiệu quả sau 1 đêm đến với mọi người. ', 'cách trị mụn hiệu quả sau 1 đêm'),
(104, 52, 'en', 'english version Những cách trị mụn hiệu quả sau 1 đêm', 'english version Mụn luôn làm cho bạn khó chịu và đau đớn, bên cạnh đó còn ảnh hưởng đến nhan sắc của bạn. Chính vì vậy, bài viết sau đây chúng tôi sẽ chia sẻ những cách trị mụn hiệu quả sau 1 đêm đến với mọi người. ', 'english version <p style="text-align:justify"><strong><em><span style="color:#000000">Mụn lu&ocirc;n l&agrave;m cho bạn kh&oacute; chịu v&agrave; đau đớn, b&ecirc;n cạnh đ&oacute; c&ograve;n ảnh hưởng đến nhan sắc của bạn. Ch&iacute;nh v&igrave; vậy, b&agrave;i viết sau đ&acirc;y ch&uacute;ng t&ocirc;i sẽ chia sẻ những c&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m đến với mọi người.</span></em></strong></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Mụn xuất hiện với nhiều nguy&ecirc;n nh&acirc;n kh&aacute;c nhau, tuy nhi&ecirc;n với những</span><a href="http://myichy.vn/bot-rua-mat"><span style="color:#3498db"> <strong>c&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m</strong></span></a><span style="color:#000000"> dưới đ&acirc;y, bạn c&oacute; thể ho&agrave;n to&agrave;n khoe da mặt của m&igrave;nh v&agrave;o s&aacute;ng h&ocirc;m sau.</span></p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua-sau-1-dem" height="392" src="/image/images/cach-tri-mun-hieu-qua-sau-1-dem%20(1).jpg" width="600" /></p>\r\n\r\n<p style="text-align:center"><em><span style="color:#000000">C&aacute;ch trị mụn bằng đ&aacute; lạnh</span></em></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Đ&aacute; lạnh c&oacute; c&ocirc;ng dụng tăng tuần ho&agrave;n m&aacute;u v&agrave; c&oacute; thể chống oxy h&oacute;a hiệu quả. N&oacute; gi&uacute;p loại bỏ những chất bẩn để khỏi bị ảnh hưởng trong việc thắt chặt c&aacute;c lỗ ch&acirc;n l&ocirc;ng. Khi bạn chườm đ&aacute; l&ecirc;n mặt th&igrave; c&aacute;c loại mụn sẽ giảm r&otilde; rệt trong chốc l&aacute;t.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn h&atilde;y lấy một &iacute;t đ&aacute; lạnh sau đ&oacute; quấn v&agrave;o vải sạch, nhẹ nh&agrave;ng chườm đ&aacute; l&ecirc;n mụn trong v&ograve;ng 2 ph&uacute;t. Chờ 20 gi&acirc;y sau th&igrave; lặp lại, cứ như vậy l&agrave;m từ 5 đến 10 lần để c&oacute; kết quả tốt hơn v&agrave;o s&aacute;ng mai.</span></p>\r\n\r\n<h2 style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng đu đủ</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Đu đủ l&agrave; một nguy&ecirc;n liệu thi&ecirc;n nhi&ecirc;n c&oacute; thể trị mụn tốt nhất, n&oacute; c&oacute; thể gi&uacute;p cho bạn loại bỏ những tế b&agrave;o da chết, đảm bảo những v&ugrave;ng da c&oacute; mụn sẽ bị kh&ocirc; lại v&agrave; ngăn chặn việc tạo mủ. B&ecirc;n cạnh đ&oacute;, trong đu đủ c&ograve;n chứa enzyme l&agrave; papain c&oacute; t&aacute;c dụng giảm vi&ecirc;m v&agrave; chống sưng da.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn cần chuẩn bị miếng đu đủ xanh v&agrave; xay nhuyễn, sau đ&oacute; đắp mặt nạ l&ecirc;n chỗ bị mụn từ 15 ph&uacute;t đến 25 ph&uacute;t v&agrave; kh&ocirc;ng chạm tay v&agrave;o. Sau đ&oacute; bạn h&atilde;y rửa mặt bằng nước ấm v&agrave; lau kh&ocirc;.</span></p>\r\n\r\n<h2 style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng tr&agrave; xanh</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">C&aacute;ch trị mụn hiệu quả sau 1 đ&ecirc;m tiếp theo ch&iacute;nh l&agrave; tr&agrave; xanh, v&igrave; trong đ&oacute; c&oacute; những ph&acirc;n tử polyphenols, chất n&agrave;y sẽ l&agrave;m mụn giảm vi&ecirc;m sưng v&agrave; giết chết c&aacute;c vi khuẩn nhiễm tr&ugrave;ng, loại bỏ c&aacute;c da chết cũng như giảm nguy cơ nhiễm tr&ugrave;ng do mụn.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bạn h&atilde;y d&ugrave;ng một nắm l&aacute; tr&agrave; xanh sau đ&oacute; v&ograve; n&aacute;t v&agrave; cho v&agrave;o ấm nước s&ocirc;i, đợi khi tr&agrave; mềm th&igrave; lấy b&atilde; đắp l&ecirc;n v&ugrave;ng da bị mụn trong v&ograve;ng 15 ph&uacute;t, sau đ&oacute; rửa mặt lại một lần nữa để s&aacute;t khuẩn. Cứ như vậy &aacute;p dụng 3 lần trong một ng&agrave;y để c&oacute; kết quả tốt v&agrave;o s&aacute;ng h&ocirc;m sau.</span></p>\r\n\r\n<p style="text-align:center"><img alt="cach-tri-mun-hieu-qua-sau-1-dem" height="800" src="/image/images/cach-tri-mun-hieu-qua-sau-1-dem%20(2).jpg" width="600" /></p>\r\n\r\n<h2 style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch trị mụn bằng bột rửa mặt Cherry</span></strong></h2>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Bột rửa mặt Cherry c&oacute; thể đ&aacute;nh bay lớp tế b&agrave;o ở tr&ecirc;n da, cung cấp c&aacute;c dưỡng chất tuyệt vời mang lại một l&agrave;n da trắng hồng, hương hoa hồng c&oacute; thể kết hợp Lavender mang đến một hương thơm tự nhi&ecirc;n tạo n&ecirc;n cảm gi&aacute;c thoải m&aacute;i v&agrave; tư gi&atilde;n.</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Th&agrave;nh phẩn của bột rửa mặt Cherry gồm c&oacute; tinh chất lavender, bột ngọc trai, chiết xuất hoa hồng, nước tinh khiết, cabomer, Grycerin...</span></p>\r\n\r\n<p style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những th&ocirc;ng tin về c&aacute;ch trị mụn hiệu quả sau một đ&ecirc;m, nếu bạn c&oacute; những thắc mắc n&agrave;o h&atilde;y truy cập v&agrave;o </span><a href="http://myichy.vn/"><span style="color:#2980b9"><strong>www.myichy.vn</strong></span></a><span style="color:#000000"> để được hỗ trợ v&agrave; tư vấn tốt nhất.</span></p>\r\n\r\n<p dir="ltr">&nbsp;</p>\r\n', '', '', '', '', '', 0, 'en-nhung-cach-tri-mun-hieu-qua-sau-1-dem', 'Những cách trị mụn hiệu quả sau 1 đêm', 'Mụn luôn làm cho bạn khó chịu và đau đớn, bên cạnh đó còn ảnh hưởng đến nhan sắc của bạn. Chính vì vậy, bài viết sau đây chúng tôi sẽ chia sẻ những cách trị mụn hiệu quả sau 1 đêm đến với mọi người. ', 'cách trị mụn hiệu quả sau 1 đêm'),
(105, 53, 'vn', 'Những cách tẩy da chết mùa đông', 'Tham khảo ngay những cách tẩy da chết mùa đông được giới thiệu dưới đây để bạn có thể áp dụng tại nhà, giúp dưỡng da hiệu quả hơn', '<p dir="ltr" style="text-align:justify"><span style="color:#000000">V&agrave;o m&ugrave;a đ&ocirc;ng, thời tiết hanh kh&ocirc; n&ecirc;n lựa da chết h&igrave;nh th&agrave;nh nhiều hơn. Để dưỡng da hiệu quả bạn cần l&agrave;m sạch lượng da chết n&agrave;y. H&atilde;y tham khảo ngay những c&aacute;ch </span><a href="http://myichy.vn/sp-body-tuyet-lanh"><span style="color:#3498db"><strong>tẩy da chết m&ugrave;a đ&ocirc;ng</strong></span></a><span style="color:#000000"> được chia sẻ ngay sau đ&acirc;y nh&eacute;!</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><span style="color:#000000"><strong>Những c&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng hiệu quả</strong></span></span></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>1.Tẩy da chết với muối biển v&agrave; mật ong</strong></span></h3>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="291" src="/image/images/tay-da-chet-mua-dong%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><em>Tẩy da chết với muối biển v&agrave; mật ong</em></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">&Aacute;p dụng c&aacute;ch </span><a href="http://myichy.vn/nhung-cach-tay-da-chet-mua-dong"><span style="color:#3498db"><strong>tẩy da chết m&ugrave;a đ&ocirc;ng</strong></span></a><span style="color:#000000"> với muối biển c&ugrave;ng với mật ong l&agrave; sự lựa chọn tối ưu d&agrave;nh cho những ai sở hữu l&agrave;n da c&oacute; xuất hiện nhiều dấu hiệu l&atilde;o h&oacute;a như: sạm, n&aacute;m, c&oacute; lỗ ch&acirc;n l&ocirc;ng to, c&oacute; nếp nhăn&hellip;Bởi mật ong c&oacute; t&aacute;c dụng giữ ẩm, chống l&atilde;o h&oacute;a vầ bảo vệ da rất tốt.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nguy&ecirc;n liệu cần chuẩn bị:</span></p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">3 th&igrave;a muối biển</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">2 th&igrave;a mật ong</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch l&agrave;m như sau:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lấy 1 chiếc ch&eacute;n sạch v&agrave; cho muối biển c&ugrave;ng mật ong v&agrave;o rồi khuấy đều. Sau khi bạn rửa sạch da mặt th&igrave; h&atilde;y thoa nhẹ nh&agrave;ng hỗn hợp tr&ecirc;n l&ecirc;n mặt v&agrave; massage nhẹ nh&agrave;ng. Bạn thư gi&atilde;n khoảng 15 ph&uacute;t để dưỡng chất của hỗn hợp thấm v&agrave;o mặt, sau đ&oacute; rửa mặt sạch với nước. &Aacute;p dụng c&aacute;ch tẩy da chết n&agrave;y 1 lần mỗi tuần để sở hữu l&agrave;n da căng mịn hơn.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>2.Tẩy da chết bằng chanh v&agrave; l&ograve;ng trắng trứng g&agrave;</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Chanh c&oacute; t&aacute;c dụng l&agrave;m sạch v&agrave; s&aacute;ng da hiệu quả, bởi n&oacute; chứa axit citric gi&uacute;p loại bỏ tế b&agrave;o chết, đồng thời lượng vitamin C trong chanh gi&uacute;p l&agrave;m s&aacute;ng da.</span></p>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="319" src="/image/images/tay-da-chet-mua-dong%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><em>Tẩy da chết bằng chanh v&agrave; l&ograve;ng trắng trứng g&agrave;</em></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trộn đều 1 l&ograve;ng trắng trứng với 1-2 giọt nước cốt chanh. Sau đ&oacute; bạn thoa hỗn hợp l&ecirc;n mặt rồi massage nhẹ nh&agrave;ng trong 15 ph&uacute;t. Cuối c&ugrave;ng rửa mặt lại với nước ấm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng n&agrave;y gi&uacute;p loại bỏ tế b&agrave;o chết v&agrave; l&agrave;m se lỗ ch&ocirc;ng l&ocirc;ng, giảm nhờn cho l&agrave;n da dầu.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>3. Sử dụng sản phẩm tẩy da chết Hoa Hồng</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Ngo&agrave;i 2 c&aacute;ch tẩy da chết bằng nguồn nguy&ecirc;n liệu tự nhi&ecirc;n tr&ecirc;n th&igrave; bạn c&oacute; thể sử dụng sản phẩm tẩy da chết Hoa Hồng (</span><a href="http://myichy.vn/tay-da-chet-hoa-hong"><span style="color:#000000">http://myichy.vn/tay-da-chet-hoa-hong</span></a><span style="color:#000000">) dưới đ&acirc;y.</span></p>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="450" src="/image/images/tay-da-chet-mua-dong%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><em>Sản phẩm tẩy da chết Hoa hồng</em></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sản phẩm chứa th&agrave;nh phần gồm c&oacute; vitamin E, Purified Water, Carbomer 90, vitamin B3, EDTA-Na 2, Fragrance, PG, Glycerin, Methyl Paraben, Benzoate, Sodium v&agrave; Benzoyl Peroxil.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết Hoa hồng c&oacute; t&aacute;c dụng l&agrave;m sạch da, loại bỏ tế b&agrave;o da chết, gi&uacute;p cải thiện v&agrave; l&agrave;m s&aacute;ng l&agrave;n da. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n ngừa l&atilde;o h&oacute;a, l&agrave;m mờ vết th&acirc;m, n&aacute;m v&agrave; sẹo tr&ecirc;n da.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện tẩy da chết như sau:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 1: Rửa mặt sạch trước khi tẩy da chết. Sau đ&oacute;, lấy một lượng gel vừa đủ v&agrave; thoa đều l&ecirc;n mặt đồng thời massage nhẹ nh&agrave;ng theo chuyển động tr&ograve;n. Những tế b&agrave;o da chết sẽ được t&aacute;ch ra khỏi bề mặt của l&agrave;n da ở dạng hạt m&agrave;u trắng.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 2: Rửa sạch lại với nước.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bạn n&ecirc;n sử dụng 1-2 lần/ tuần để đạt được hiệu quả cao.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; 3 c&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng m&agrave; bạn n&ecirc;n thực hiện để qu&aacute; tr&igrave;nh dưỡng da tại nh&agrave; đạt hiệu quả cao nhất.</span></p>\r\n', '', '', '', '', '', 0, 'nhung-cach-tay-da-chet-mua-dong', 'Những cách tẩy da chết mùa đông', 'Tham khảo ngay những cách tẩy da chết mùa đông được giới thiệu dưới đây để bạn có thể áp dụng tại nhà, giúp dưỡng da hiệu quả hơn.', 'tẩy da chết mùa đông');
INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(106, 53, 'en', 'english version Những cách tẩy da chết mùa đông', 'english version Tham khảo ngay những cách tẩy da chết mùa đông được giới thiệu dưới đây để bạn có thể áp dụng tại nhà, giúp dưỡng da hiệu quả hơn', 'english version <p dir="ltr" style="text-align:justify">V&agrave;o m&ugrave;a đ&ocirc;ng, thời tiết hanh kh&ocirc; n&ecirc;n lựa da chết h&igrave;nh th&agrave;nh nhiều hơn. Để dưỡng da hiệu quả bạn cần l&agrave;m sạch lượng da chết n&agrave;y. H&atilde;y tham khảo ngay những c&aacute;ch <a href="http://myichy.vn/sp-body-tuyet-lanh"><span style="color:#2980b9"><strong>tẩy da chết m&ugrave;a đ&ocirc;ng</strong></span></a> được chia sẻ ngay sau đ&acirc;y nh&eacute;!</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong>Những c&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng hiệu quả</strong></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>1.Tẩy da chết với muối biển v&agrave; mật ong</strong></h3>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="291" src="/image/images/tay-da-chet-mua-dong%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Tẩy da chết với muối biển v&agrave; mật ong</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&Aacute;p dụng c&aacute;ch <strong>tẩy da chết m&ugrave;a đ&ocirc;ng</strong> với muối biển c&ugrave;ng với mật ong l&agrave; sự lựa chọn tối ưu d&agrave;nh cho những ai sở hữu l&agrave;n da c&oacute; xuất hiện nhiều dấu hiệu l&atilde;o h&oacute;a như: sạm, n&aacute;m, c&oacute; lỗ ch&acirc;n l&ocirc;ng to, c&oacute; nếp nhăn&hellip;Bởi mật ong c&oacute; t&aacute;c dụng giữ ẩm, chống l&atilde;o h&oacute;a vầ bảo vệ da rất tốt.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Nguy&ecirc;n liệu cần chuẩn bị:</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">3 th&igrave;a muối biển</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">2 th&igrave;a mật ong</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify">C&aacute;ch l&agrave;m như sau:</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Lấy 1 chiếc ch&eacute;n sạch v&agrave; cho muối biển c&ugrave;ng mật ong v&agrave;o rồi khuấy đều. Sau khi bạn rửa sạch da mặt th&igrave; h&atilde;y thoa nhẹ nh&agrave;ng hỗn hợp tr&ecirc;n l&ecirc;n mặt v&agrave; massage nhẹ nh&agrave;ng. Bạn thư gi&atilde;n khoảng 15 ph&uacute;t để dưỡng chất của hỗn hợp thấm v&agrave;o mặt, sau đ&oacute; rửa mặt sạch với nước. &Aacute;p dụng c&aacute;ch tẩy da chết n&agrave;y 1 lần mỗi tuần để sở hữu l&agrave;n da căng mịn hơn.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>2.Tẩy da chết bằng chanh v&agrave; l&ograve;ng trắng trứng g&agrave;</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Chanh c&oacute; t&aacute;c dụng l&agrave;m sạch v&agrave; s&aacute;ng da hiệu quả, bởi n&oacute; chứa axit citric gi&uacute;p loại bỏ tế b&agrave;o chết, đồng thời lượng vitamin C trong chanh gi&uacute;p l&agrave;m s&aacute;ng da.</p>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="319" src="/image/images/tay-da-chet-mua-dong%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Tẩy da chết bằng chanh v&agrave; l&ograve;ng trắng trứng g&agrave;</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">C&aacute;ch thực hiện:</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Trộn đều 1 l&ograve;ng trắng trứng với 1-2 giọt nước cốt chanh. Sau đ&oacute; bạn thoa hỗn hợp l&ecirc;n mặt rồi massage nhẹ nh&agrave;ng trong 15 ph&uacute;t. Cuối c&ugrave;ng rửa mặt lại với nước ấm.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">C&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng n&agrave;y gi&uacute;p loại bỏ tế b&agrave;o chết v&agrave; l&agrave;m se lỗ ch&ocirc;ng l&ocirc;ng, giảm nhờn cho l&agrave;n da dầu.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>3. Sử dụng sản phẩm tẩy da chết Hoa Hồng</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Ngo&agrave;i 2 c&aacute;ch tẩy da chết bằng nguồn nguy&ecirc;n liệu tự nhi&ecirc;n tr&ecirc;n th&igrave; bạn c&oacute; thể sử dụng sản phẩm tẩy da chết Hoa Hồng (<a href="http://myichy.vn/tay-da-chet-hoa-hong">http://myichy.vn/tay-da-chet-hoa-hong</a>) dưới đ&acirc;y.</p>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-mua-dong" height="450" src="/image/images/tay-da-chet-mua-dong%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Sản phẩm tẩy da chết Hoa hồng</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">Sản phẩm chứa th&agrave;nh phần gồm c&oacute; vitamin E, Purified Water, Carbomer 90, vitamin B3, EDTA-Na 2, Fragrance, PG, Glycerin, Methyl Paraben, Benzoate, Sodium v&agrave; Benzoyl Peroxil.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Tẩy da chết Hoa hồng c&oacute; t&aacute;c dụng l&agrave;m sạch da, loại bỏ tế b&agrave;o da chết, gi&uacute;p cải thiện v&agrave; l&agrave;m s&aacute;ng l&agrave;n da. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n ngừa l&atilde;o h&oacute;a, l&agrave;m mờ vết th&acirc;m, n&aacute;m v&agrave; sẹo tr&ecirc;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">C&aacute;ch thực hiện tẩy da chết như sau:</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Bước 1: Rửa mặt sạch trước khi tẩy da chết. Sau đ&oacute;, lấy một lượng gel vừa đủ v&agrave; thoa đều l&ecirc;n mặt đồng thời massage nhẹ nh&agrave;ng theo chuyển động tr&ograve;n. Những tế b&agrave;o da chết sẽ được t&aacute;ch ra khỏi bề mặt của l&agrave;n da ở dạng hạt m&agrave;u trắng.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Bước 2: Rửa sạch lại với nước.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n sử dụng 1-2 lần/ tuần để đạt được hiệu quả cao.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Tr&ecirc;n đ&acirc;y l&agrave; 3 c&aacute;ch tẩy da chết m&ugrave;a đ&ocirc;ng m&agrave; bạn n&ecirc;n thực hiện để qu&aacute; tr&igrave;nh dưỡng da tại nh&agrave; đạt hiệu quả cao nhất.</p>\r\n', '', '', '', '', '', 0, 'en-nhung-cach-tay-da-chet-mua-dong', 'Những cách tẩy da chết mùa đông', '', ''),
(107, 54, 'vn', 'Tẩy da chết có tác dụng gì?', 'Vì sao phải tẩy da chết cho mặt thường xuyên? Tẩy da chết có tác dụng gì? Hãy tham khảo ngay những thông tin tác dụng của việc tẩy da chết cho bạn tham khảo.', '<p dir="ltr" style="text-align:justify"><em><span style="color:#000000">V&igrave; sao phải tẩy da chết cho mặt thường xuy&ecirc;n? Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;? H&atilde;y tham khảo ngay những th&ocirc;ng tin t&aacute;c dụng của việc tẩy da chết cho bạn tham khảo.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong qu&aacute; tr&igrave;nh l&agrave;m đẹp th&igrave; việc tẩy da chết l&agrave; c&ocirc;ng đoạn bạn kh&ocirc;ng thể bỏ qua. Vậy</span><a href="http://myichy.vn/sp-body-tuyet-lanh"><span style="color:#2980b9"><strong> tẩy da chết c&oacute; t&aacute;c dụng g&igrave;</strong></span></a><span style="color:#000000">? Tẩy da chết mang tới 7 c&ocirc;ng dụng dưới đ&acirc;y. Bạn h&atilde;y tham khảo ngay nh&eacute;!</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;?</span></strong></span></h2>\r\n\r\n<p dir="ltr">&nbsp;</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">1.Gi&uacute;p l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;? Lợi &iacute;ch đầu ti&ecirc;n phải nhắc tới đ&oacute; l&agrave; t&aacute;c dụng l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng. Với những n&agrave;ng sở hữu l&agrave;m da kh&ocirc;, mụn th&igrave; việc tẩy da chết lại c&agrave;ng cần thiết hơn do đ&oacute; phải thực hiện c&ocirc;ng đoạn n&agrave;y h&agrave;ng tuần. Điều n&agrave;y gi&uacute;p loại bỏ tế b&agrave;o chết, hạn chế g&acirc;y b&iacute;t lỗ ch&acirc;n l&ocirc;ng nhằm giảm t&igrave;nh trạng xuất hiện mụn đầu đen hoặc mụn đầu trắng.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">2.Ngăn ngừa xuất hiện mụn</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Như đ&atilde; n&oacute;i ở tr&ecirc;n, việc tẩy da chết cho mặt gi&uacute;p lỗ ch&acirc;n l&ocirc;ng tho&aacute;ng hơn, từ đ&oacute; gi&uacute;p ngăn ngừa mụn xuất hiện. Bởi một trong những nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n mụn l&agrave; lượng da chết tồn đọng nhiều, g&acirc;y tắc lỗ ch&acirc;n l&ocirc;ng.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">3.Tăng hiệu quả sử dụng c&aacute;c sản phẩm l&agrave;m đẹp</span></strong></h3>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-co-tac-dung-gi" height="400" src="/image/images/tay-da-chet-co-tac-dung-gi%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tăng hiệu quả sử dụng c&aacute;c sản phẩm l&agrave;m đẹp</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết đều đặn gi&uacute;p lỗ ch&acirc;n l&ocirc;ng tho&aacute;ng, gi&uacute;p hấp thụ hiệu quả những dưỡng chất từ kem dưỡng da, serum hoặc từ mặt nạ. Bạn n&ecirc;n nhớ r&agrave;o cảm giữa l&agrave;n da v&agrave; những sản phẩm chăm s&oacute;c da ch&iacute;nh l&agrave; những tế b&agrave;o chết v&igrave; vậy bạn cần l&agrave;m sạch n&oacute;.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">4.Gi&uacute;p l&agrave;m s&aacute;ng da v&agrave; giảm vết th&acirc;m</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tế b&agrave;o chết cũng l&agrave; một trong những nguy&ecirc;n nh&acirc;n khiến l&agrave;n da của bạn trở n&ecirc;n x&aacute;m xịt v&agrave; xỉn m&agrave;u hơn. Để xử l&yacute; những vấn đề n&agrave;y bạn cần phải tẩy da chết đều đặn h&agrave;ng tuần, kết hợp với việc sử dụng những serum c&oacute; t&aacute;c dụng l&agrave;m s&aacute;ng da để l&agrave;m giảm vết th&acirc;m hiệu quả.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">5.Gi&uacute;p lưu th&ocirc;ng mạch m&aacute;u ở dưới da</span></strong></h3>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-co-tac-dung-gi" height="450" src="/image/images/tay-da-chet-co-tac-dung-gi%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Gi&uacute;p lưu th&ocirc;ng mạch m&aacute;u ở dưới da</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><a href="http://myichy.vn/tay-da-chet-co-tac-dung-gi"><span style="color:#3498db"><strong>Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;</strong></span></a><span style="color:#000000">? Trong qu&aacute; tr&igrave;nh thực hiện những động t&aacute;c massage khi tẩy da chết sẽ th&uacute;c đẩy m&aacute;u lưu th&ocirc;ng ở dưới da, đồng thời tăng cường oxy v&agrave; nu&ocirc;i dưỡng lớp da mới của bạn.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">6.Th&uacute;c đẩy t&aacute;i tạo tế b&agrave;o da mới</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Thực hiện tẩy da chết đều đặn sẽ tạo th&oacute;i quen cho l&agrave;n da của bạn sản sinh tế b&agrave;o mới. Đ&acirc;y ch&iacute;nh l&agrave; vấn đề cốt l&otilde;i gi&uacute;p bạn lu&ocirc;n sở hữu l&agrave;n da mịn m&agrave;ng v&agrave; tr&agrave;n đầy sức sống.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tuy nhi&ecirc;n bạn phải lựa chọn được sản phẩm tẩy da chết an to&agrave;n v&agrave; &aacute;p dụng tần suất sử dụng ph&ugrave; hợp. N&ecirc;n tẩy da chết từ 1-2 lần mỗi tuần.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify">&nbsp;</h4>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">7. K&iacute;ch th&iacute;ch tăng sinh collagen</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">T&aacute;c dụng cuối c&ugrave;ng của việc tẩy da chết mang lại đ&oacute; l&agrave; k&iacute;ch thước qu&aacute; tr&igrave;nh tổng hợp collagen nhằm cải thiện kết cấu của da, điều n&agrave;y gi&uacute;p l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p cho l&agrave;n da mịn m&agrave;ng hơn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Ttẩy da chết c&oacute; t&aacute;c dụng g&igrave;? Tr&ecirc;n đ&acirc;y l&agrave; 7 t&aacute;c dụng của việc tẩy da chết mỗi tuần. Để lựa chọn sản phẩm tẩy da chết an to&agrave;n v&agrave; hiệu quả bạn c&oacute; thể tham khảo sản phẩm Tẩy Da Chết Hoa Hồng (</span><strong><a href="http://myichy.vn/tay-da-chet-hoa-hong"><span style="color:#2980b9">http://myichy.vn/tay-da-chet-hoa-hong</span></a></strong><span style="color:#000000">) cho l&agrave;n da của m&igrave;nh. Sản phẩm n&agrave;y hiện đang b&aacute;n chạy nhất tr&ecirc;n thị trường hiện nay.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết hoa hồng My-ichy kh&ocirc;ng chỉ tẩy da chết an to&agrave;n hiệu quả, đồng thời bổ sung dinh dưỡng cho l&agrave;n da căng tr&agrave;n sức sống</span></p>\r\n', '', '', '', '', '', 0, 'tay-da-chet-co-tac-dung-gi', 'Tẩy da chết có tác dụng gì?', 'Vì sao phải tẩy da chết cho mặt thường xuyên? Tẩy da chết có tác dụng gì? Hãy tham khảo ngay những thông tin tác dụng của việc tẩy da chết cho bạn tham khảo.', 'tẩy da chết có tác dụng gì'),
(108, 54, 'en', 'english version Tẩy da chết có tác dụng gì?', 'english version Vì sao phải tẩy da chết cho mặt thường xuyên? Tẩy da chết có tác dụng gì? Hãy tham khảo ngay những thông tin tác dụng của việc tẩy da chết cho bạn tham khảo.', 'english version <p dir="ltr" style="text-align:justify"><em><span style="color:#000000">V&igrave; sao phải tẩy da chết cho mặt thường xuy&ecirc;n? Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;? H&atilde;y tham khảo ngay những th&ocirc;ng tin t&aacute;c dụng của việc tẩy da chết cho bạn tham khảo.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong qu&aacute; tr&igrave;nh l&agrave;m đẹp th&igrave; việc tẩy da chết l&agrave; c&ocirc;ng đoạn bạn kh&ocirc;ng thể bỏ qua. Vậy</span><a href="http://myichy.vn/sp-body-tuyet-lanh"><span style="color:#2980b9"><strong> tẩy da chết c&oacute; t&aacute;c dụng g&igrave;</strong></span></a><span style="color:#000000">? Tẩy da chết mang tới 7 c&ocirc;ng dụng dưới đ&acirc;y. Bạn h&atilde;y tham khảo ngay nh&eacute;!</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;?</span></strong></span></h2>\r\n\r\n<p dir="ltr">&nbsp;</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">1.Gi&uacute;p l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;? Lợi &iacute;ch đầu ti&ecirc;n phải nhắc tới đ&oacute; l&agrave; t&aacute;c dụng l&agrave;m tho&aacute;ng lỗ ch&acirc;n l&ocirc;ng. Với những n&agrave;ng sở hữu l&agrave;m da kh&ocirc;, mụn th&igrave; việc tẩy da chết lại c&agrave;ng cần thiết hơn do đ&oacute; phải thực hiện c&ocirc;ng đoạn n&agrave;y h&agrave;ng tuần. Điều n&agrave;y gi&uacute;p loại bỏ tế b&agrave;o chết, hạn chế g&acirc;y b&iacute;t lỗ ch&acirc;n l&ocirc;ng nhằm giảm t&igrave;nh trạng xuất hiện mụn đầu đen hoặc mụn đầu trắng.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">2.Ngăn ngừa xuất hiện mụn</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Như đ&atilde; n&oacute;i ở tr&ecirc;n, việc tẩy da chết cho mặt gi&uacute;p lỗ ch&acirc;n l&ocirc;ng tho&aacute;ng hơn, từ đ&oacute; gi&uacute;p ngăn ngừa mụn xuất hiện. Bởi một trong những nguy&ecirc;n nh&acirc;n g&acirc;y n&ecirc;n mụn l&agrave; lượng da chết tồn đọng nhiều, g&acirc;y tắc lỗ ch&acirc;n l&ocirc;ng.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">3.Tăng hiệu quả sử dụng c&aacute;c sản phẩm l&agrave;m đẹp</span></h4>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-co-tac-dung-gi" height="400" src="/image/images/tay-da-chet-co-tac-dung-gi%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tăng hiệu quả sử dụng c&aacute;c sản phẩm l&agrave;m đẹp</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết đều đặn gi&uacute;p lỗ ch&acirc;n l&ocirc;ng tho&aacute;ng, gi&uacute;p hấp thụ hiệu quả những dưỡng chất từ kem dưỡng da, serum hoặc từ mặt nạ. Bạn n&ecirc;n nhớ r&agrave;o cảm giữa l&agrave;n da v&agrave; những sản phẩm chăm s&oacute;c da ch&iacute;nh l&agrave; những tế b&agrave;o chết v&igrave; vậy bạn cần l&agrave;m sạch n&oacute;.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">4.Gi&uacute;p l&agrave;m s&aacute;ng da v&agrave; giảm vết th&acirc;m</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tế b&agrave;o chết cũng l&agrave; một trong những nguy&ecirc;n nh&acirc;n khiến l&agrave;n da của bạn trở n&ecirc;n x&aacute;m xịt v&agrave; xỉn m&agrave;u hơn. Để xử l&yacute; những vấn đề n&agrave;y bạn cần phải tẩy da chết đều đặn h&agrave;ng tuần, kết hợp với việc sử dụng những serum c&oacute; t&aacute;c dụng l&agrave;m s&aacute;ng da để l&agrave;m giảm vết th&acirc;m hiệu quả.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">5.Gi&uacute;p lưu th&ocirc;ng mạch m&aacute;u ở dưới da</span></h4>\r\n\r\n<p style="text-align:center"><img alt="tay-da-chet-co-tac-dung-gi" height="450" src="/image/images/tay-da-chet-co-tac-dung-gi%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Gi&uacute;p lưu th&ocirc;ng mạch m&aacute;u ở dưới da</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết c&oacute; t&aacute;c dụng g&igrave;? Trong qu&aacute; tr&igrave;nh thực hiện những động t&aacute;c massage khi tẩy da chết sẽ th&uacute;c đẩy m&aacute;u lưu th&ocirc;ng ở dưới da, đồng thời tăng cường oxy v&agrave; nu&ocirc;i dưỡng lớp da mới của bạn.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">6.Th&uacute;c đẩy t&aacute;i tạo tế b&agrave;o da mới</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Thực hiện tẩy da chết đều đặn sẽ tạo th&oacute;i quen cho l&agrave;n da của bạn sản sinh tế b&agrave;o mới. Đ&acirc;y ch&iacute;nh l&agrave; vấn đề cốt l&otilde;i gi&uacute;p bạn lu&ocirc;n sở hữu l&agrave;n da mịn m&agrave;ng v&agrave; tr&agrave;n đầy sức sống.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tuy nhi&ecirc;n bạn phải lựa chọn được sản phẩm tẩy da chết an to&agrave;n v&agrave; &aacute;p dụng tần suất sử dụng ph&ugrave; hợp. N&ecirc;n tẩy da chết từ 1-2 lần mỗi tuần.</span></p>\r\n\r\n<h4 dir="ltr" style="text-align:justify"><span style="color:#000000">7. K&iacute;ch th&iacute;ch tăng sinh collagen</span></h4>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">T&aacute;c dụng cuối c&ugrave;ng của việc tẩy da chết mang lại đ&oacute; l&agrave; k&iacute;ch thước qu&aacute; tr&igrave;nh tổng hợp collagen nhằm cải thiện kết cấu của da, điều n&agrave;y gi&uacute;p l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, gi&uacute;p cho l&agrave;n da mịn m&agrave;ng hơn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Ttẩy da chết c&oacute; t&aacute;c dụng g&igrave;? Tr&ecirc;n đ&acirc;y l&agrave; 7 t&aacute;c dụng của việc tẩy da chết mỗi tuần. Để lựa chọn sản phẩm tẩy da chết an to&agrave;n v&agrave; hiệu quả bạn c&oacute; thể tham khảo sản phẩm Tẩy Da Chết Hoa Hồng (</span><strong><a href="http://myichy.vn/tay-da-chet-hoa-hong"><span style="color:#2980b9">http://myichy.vn/tay-da-chet-hoa-hong</span></a></strong><span style="color:#000000">) cho l&agrave;n da của m&igrave;nh. Sản phẩm n&agrave;y hiện đang b&aacute;n chạy nhất tr&ecirc;n thị trường hiện nay.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tẩy da chết hoa hồng My-ichy kh&ocirc;ng chỉ tẩy da chết an to&agrave;n hiệu quả, đồng thời bổ sung dinh dưỡng cho l&agrave;n da căng tr&agrave;n sức sống</span></p>\r\n', '', '', '', '', '', 0, 'en-tay-da-chet-co-tac-dung-gi', 'Tẩy da chết có tác dụng gì?', 'Vì sao phải tẩy da chết cho mặt thường xuyên? Tẩy da chết có tác dụng gì? Hãy tham khảo ngay những thông tin tác dụng của việc tẩy da chết cho bạn tham khảo.', 'tẩy da chết có tác dụng gì'),
(109, 55, 'vn', 'Kem tắm trắng loại nào hiệu quả', 'Kem tắm trắng loại nào hiệu quả, có chứng nhận từ bộ Y tế, đảm bảo an toàn hiệu quả không tác dụng phụ mang lại cho bạn làn da trắng sáng tự nhiên.', '<p dir="ltr" style="text-align:justify"><strong><span style="color:#000000"><em>Kem tắm trắng loại n&agrave;o hiệu quả, c&oacute; chứng nhận từ bộ Y tế, đảm bảo an to&agrave;n hiệu quả kh&ocirc;ng t&aacute;c dụng phụ mang lại cho bạn l&agrave;n da trắng s&aacute;ng tự nhi&ecirc;n.</em></span></strong></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Phụ nữ kh&ocirc;ng c&oacute; ai xấu, chỉ c&oacute; phụ nữ chưa biết c&aacute;ch l&agrave;m đẹp. Tuy nhi&ecirc;n kh&ocirc;ng phải ai cũng c&oacute; điều kiện về kinh tế, thời gian để đến l&agrave;m đẹp tại c&aacute;c trung t&acirc;m l&agrave;m đẹp, spa... Nắm bắt được t&acirc;m l&yacute; muốn được l&agrave;m đẹp m&agrave; lại kh&ocirc;ng tốn nhiều thời gian vừa tiện lợi m&agrave; kh&ocirc;ng tốn k&eacute;m của c&aacute;c chị em, n&ecirc;n rất c&aacute;c sản phẩm kem tắm trắng đ&atilde; ra đời đ&aacute;p ứng nhu cầu sử dụng của chị em. Tuy nhi&ecirc;n việc lựa chọn </span><a href="http://myichy.vn/u-face-saffon-cherry-nhat-ban"><span style="color:#2980b9"><strong>loại kem tắm trắng loại n&agrave;o hiệu quả </strong></span></a><span style="color:#000000">kh&ocirc;ng phải ai cũng c&oacute; đủ kinh nghiệm, thời gian để t&igrave;m hiểu. Trong b&agrave;i viết n&agrave;y ch&uacute;ng t&ocirc;i xin giới thiệu đến c&aacute;c chị em sản phẩm Tắm trắng Bạch Hoa Tuyết của c&ocirc;ng ty dược mỹ phẩm cao cấp my-ichy.</span></p>\r\n\r\n<p style="text-align:center"><img alt="kem-tam-trang-loai-nao-hieu-qua" height="400" src="/image/images/kem-tam-trang-loai-nao-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Bạn kh&ocirc;ng biết lựa chọn sản phẩm kem tắm trắng n&agrave;o cho tốt?</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Giới thiệu về Tắm trắng Bạch Hoa Tuyết</span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Th&agrave;nh phần của Tắm trắng Bạch Hoa Tuyết gồm: Cabomer, chiết suất hoa hồng, nước tinh khiết, bột ngọc trai, tinh chất Lavender, alphabutin, Grycerin,&hellip;</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000">Bộ sản phẩm Tắm trắng Bạch Hoa Tuyết gồm 3 g&oacute;i:</span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 1: Dung dịch tắm trắng hoa hồng</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 2: Bột ngọc trai</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 3: Serum Lavender</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Tại sao kem tắm trắng loại n&agrave;o tốt lại n&ecirc;n chọn Tắm trắng Bạch Hoa Tuyết</span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng dụng tuyệt vời của Tắm trắng Bạch Hoa Tuyết: đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da trắng hồng, săn chắc, c&ugrave;ng với hương thơm nồng n&agrave;n từ Lavender sẽ tăng th&ecirc;m sự l&ocirc;i cuốn cho chị em</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng đơn giản: Trộn g&oacute;i 1 v&agrave; g&oacute;i 2 sau đ&oacute; thoa hỗn hợp l&ecirc;n da, massager cho da khoảng 15 ph&uacute;t th&igrave; rửa sạch bằng nước rồi lau kh&ocirc;. Sau đ&oacute; thoa serum Lavender l&ecirc;n to&agrave;n th&acirc;n</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&iacute;a cả ưu đ&atilde;i: Trọn bộ sản phẩm Tắm trắng Bạch Hoa Tuyết c&oacute; gi&aacute; 190.000 đ</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">K&iacute;ch ứng da: Kh&ocirc;ng đ&aacute;ng kể-Chưa ghi nhận trường hợp n&agrave;o sử dụng sản phẩm bị k&iacute;ch ứng da</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Ưu đ&atilde;i lớn: Miễn ph&iacute; giao h&agrave;ng tr&ecirc;n to&agrave;n quốc với đơn h&agrave;ng tr&ecirc;n 500.000đ</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bảo quản đơn giản: Để ở nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp-ch&uacute; &yacute; để xa tầm tay của trẻ em.</span></p>\r\n\r\n<p style="text-align:center"><img alt="kem-tam-trang-loai-nao-hieu-qua" height="450" src="/image/images/kem-tam-trang-loai-nao-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết giải ph&aacute;p cho l&agrave;n da trắng mịn hồng h&agrave;o</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c th&ocirc;ng tin ở tr&ecirc;n đ&atilde; chia sẻ cho bạn biết việc lựa chọn</span><a href="http://myichy.vn/kem-tam-trang-loai-nao-hieu-qua"><span style="color:#2980b9"> <strong>kem tắm trắng loại n&agrave;o hiệu quả </strong></span></a><span style="color:#000000">rồi chứ. Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm Tắm trắng Bạch Hoa Tuyết cũng như sản c&aacute;c sản phẩm kh&aacute;c của C&ocirc;ng ty dược mỹ phẩm myichy c&aacute;c bạn truy cập v&agrave;o website:</span><a href="http://myichy.vn/"><span style="color:#27ae60"><strong> </strong></span><span style="color:#2980b9"><strong>http://myichy.vn</strong></span><span style="color:#27ae60"><strong> &nbsp;</strong></span></a><span style="color:#000000">Ch&uacute;c c&aacute;c chị em chọn được sản phẩm ph&ugrave; hợp cho m&igrave;nh. </span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', 0, 'kem-tam-trang-loai-nao-hieu-qua', 'Kem tắm trắng loại nào hiệu quả', 'Kem tắm trắng loại nào hiệu quả, có chứng nhận từ bộ Y tế, đảm bảo an toàn hiệu quả không tác dụng phụ mang lại cho bạn làn da trắng sáng tự nhiên.', 'kem tắm trắng loại nào hiệu quả'),
(110, 55, 'en', 'english version Kem tắm trắng loại nào hiệu quả', 'english version Kem tắm trắng loại nào hiệu quả, có chứng nhận từ bộ Y tế, đảm bảo an toàn hiệu quả không tác dụng phụ mang lại cho bạn làn da trắng sáng tự nhiên.', 'english version <p dir="ltr" style="text-align:justify"><em>Kem tắm trắng loại n&agrave;o hiệu quả, c&oacute; chứng nhận từ bộ Y tế, đảm bảo an to&agrave;n hiệu quả kh&ocirc;ng t&aacute;c dụng phụ mang lại cho bạn l&agrave;n da trắng s&aacute;ng tự nhi&ecirc;n.</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Phụ nữ kh&ocirc;ng c&oacute; ai xấu, chỉ c&oacute; phụ nữ chưa biết c&aacute;ch l&agrave;m đẹp. Tuy nhi&ecirc;n kh&ocirc;ng phải ai cũng c&oacute; điều kiện về kinh tế, thời gian để đến l&agrave;m đẹp tại c&aacute;c trung t&acirc;m l&agrave;m đẹp, spa... Nắm bắt được t&acirc;m l&yacute; muốn được l&agrave;m đẹp m&agrave; lại kh&ocirc;ng tốn nhiều thời gian vừa tiện lợi m&agrave; kh&ocirc;ng tốn k&eacute;m của c&aacute;c chị em, n&ecirc;n rất c&aacute;c sản phẩm kem tắm trắng đ&atilde; ra đời đ&aacute;p ứng nhu cầu sử dụng của chị em. Tuy nhi&ecirc;n việc lựa chọn </span><a href="http://myichy.vn/u-face-saffon-cherry-nhat-ban"><span style="color:#2980b9"><strong>loại kem tắm trắng loại n&agrave;o hiệu quả </strong></span></a><span style="color:#000000">kh&ocirc;ng phải ai cũng c&oacute; đủ kinh nghiệm, thời gian để t&igrave;m hiểu. Trong b&agrave;i viết n&agrave;y ch&uacute;ng t&ocirc;i xin giới thiệu đến c&aacute;c chị em sản phẩm Tắm trắng Bạch Hoa Tuyết của c&ocirc;ng ty dược mỹ phẩm cao cấp my-ichy.</span></p>\r\n\r\n<p style="text-align:center"><img alt="kem-tam-trang-loai-nao-hieu-qua" height="400" src="/image/images/kem-tam-trang-loai-nao-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Bạn kh&ocirc;ng biết lựa chọn sản phẩm kem tắm trắng n&agrave;o cho tốt?</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Giới thiệu về Tắm trắng Bạch Hoa Tuyết</span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Th&agrave;nh phần của Tắm trắng Bạch Hoa Tuyết gồm: Cabomer, chiết suất hoa hồng, nước tinh khiết, bột ngọc trai, tinh chất Lavender, alphabutin, Grycerin,&hellip;</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000">Bộ sản phẩm Tắm trắng Bạch Hoa Tuyết gồm 3 g&oacute;i:</span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 1: Dung dịch tắm trắng hoa hồng</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 2: Bột ngọc trai</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&oacute;i 3: Serum Lavender</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Tại sao kem tắm trắng loại n&agrave;o tốt lại n&ecirc;n chọn Tắm trắng Bạch Hoa Tuyết</span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng dụng tuyệt vời của Tắm trắng Bạch Hoa Tuyết: đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da trắng hồng, săn chắc, c&ugrave;ng với hương thơm nồng n&agrave;n từ Lavender sẽ tăng th&ecirc;m sự l&ocirc;i cuốn cho chị em</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng đơn giản: Trộn g&oacute;i 1 v&agrave; g&oacute;i 2 sau đ&oacute; thoa hỗn hợp l&ecirc;n da, massager cho da khoảng 15 ph&uacute;t th&igrave; rửa sạch bằng nước rồi lau kh&ocirc;. Sau đ&oacute; thoa serum Lavender l&ecirc;n to&agrave;n th&acirc;n</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">G&iacute;a cả ưu đ&atilde;i: Trọn bộ sản phẩm Tắm trắng Bạch Hoa Tuyết c&oacute; gi&aacute; 190.000 đ</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">K&iacute;ch ứng da: Kh&ocirc;ng đ&aacute;ng kể-Chưa ghi nhận trường hợp n&agrave;o sử dụng sản phẩm bị k&iacute;ch ứng da</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Ưu đ&atilde;i lớn: Miễn ph&iacute; giao h&agrave;ng tr&ecirc;n to&agrave;n quốc với đơn h&agrave;ng tr&ecirc;n 500.000đ</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bảo quản đơn giản: Để ở nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp-ch&uacute; &yacute; để xa tầm tay của trẻ em.</span></p>\r\n\r\n<p style="text-align:center"><img alt="kem-tam-trang-loai-nao-hieu-qua" height="450" src="/image/images/kem-tam-trang-loai-nao-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết giải ph&aacute;p cho l&agrave;n da trắng mịn hồng h&agrave;o</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c th&ocirc;ng tin ở tr&ecirc;n đ&atilde; chia sẻ cho bạn biết việc lựa chọn <strong>kem tắm trắng loại n&agrave;o hiệu quả </strong>rồi chứ. Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm Tắm trắng Bạch Hoa Tuyết cũng như sản c&aacute;c sản phẩm kh&aacute;c của C&ocirc;ng ty dược mỹ phẩm myichy c&aacute;c bạn truy cập v&agrave;o website:</span><a href="http://myichy.vn/"><span style="color:#27ae60"><strong> </strong></span><span style="color:#2980b9"><strong>http://myichy.vn</strong></span><span style="color:#27ae60"><strong> &nbsp;</strong></span></a><span style="color:#000000">Ch&uacute;c c&aacute;c chị em chọn được sản phẩm ph&ugrave; hợp cho m&igrave;nh. </span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', 0, 'en-kem-tam-trang-loai-nao-hieu-qua', 'Kem tắm trắng loại nào hiệu quả', 'Kem tắm trắng loại nào hiệu quả, có chứng nhận từ bộ Y tế, đảm bảo an toàn hiệu quả không tác dụng phụ mang lại cho bạn làn da trắng sáng tự nhiên.', 'kem tắm trắng loại nào hiệu quả'),
(111, 56, 'vn', 'Cách chọn màu kem trang điểm tốt nhất hiện nay phù hợp với màu của làn da', 'Trên thị trường có rất nhiều loại kem trang điểm khác nhau. Vậy nên chọn kem trang điểm tốt nhất hiện nay như thế nào để phù hợp với màu da của mình?', '<p dir="ltr" style="text-align:justify"><span style="color:#000000"><strong><em>Tr&ecirc;n thị trường c&oacute; rất nhiều loại kem trang điểm kh&aacute;c nhau. Vậy n&ecirc;n chọn kem trang điểm tốt nhất hiện nay như thế n&agrave;o để ph&ugrave; hợp với m&agrave;u da của m&igrave;nh?</em></strong></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lựa chọn được loại kem trang điểm tốt nhất hiện nay ph&ugrave; hợp với m&agrave;u da gi&uacute;p bạn trang điểm tự nhi&ecirc;n v&agrave; nhẹ nh&agrave;ng nhưng vẫn đẹp v&agrave; cuốn h&uacute;t. H&atilde;y tham khảo ngay những th&ocirc;ng tin dưới đ&acirc;y m&agrave; ch&uacute;ng t&ocirc;i cung cấp cho bạn.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>C&aacute;ch chọn kem trang điểm tốt nhất hiện nay ph&ugrave; hợp với m&agrave;u da</strong></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để c&oacute; được gương mặt đẹp tự nhi&ecirc;n, trang điểm kh&ocirc;ng qu&aacute; lố th&igrave; việc lựa chọn sản phẩm an to&agrave;n bạn n&ecirc;n quan t&acirc;m tới m&agrave;u sắc của kem nền sao cho ph&ugrave; hợp với l&agrave;n da của m&igrave;nh. Lựa chọn m&agrave;u </span><strong><a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#2980b9">kem trang điểm tốt nhất hiện nay</span></a><span style="color:#000000"> </span></strong><span style="color:#000000">ph&ugrave; hợp với m&agrave;u của da l&agrave; bước cực kỳ quan trọng. Bởi v&igrave; nếu bạn sử dụng kem trang điểm c&oacute; độ che phủ tốt nhưng kh&ocirc;ng hợp với m&agrave;u da sẽ khiến cho da mặt bị c&ocirc; lập so với da cổ, da tay, da ngực.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do đ&oacute; h&atilde;y tham khảo ngay 2 c&aacute;ch lựa chọn kem nền ph&ugrave; hợp với m&agrave;u da của bạn ngay dưới đ&acirc;y:</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>1.Chọn kem trang điểm dựa tr&ecirc;n m&agrave;u mạch m&aacute;u cổ tay</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">M&agrave;u da mặt c&oacute; thể thay đổi do sự t&aacute;c động của thời gian, &aacute;nh nắng mặt trời&hellip;tuy nhi&ecirc;n m&agrave;u sắc của mạch m&aacute;u lại kh&ocirc;ng bao giờ thay đổi. Do đ&oacute; bạn c&oacute; thể lựa chọn m&agrave;u của kem n&ecirc;n tr&ugrave;ng với m&agrave;u mạch m&aacute;u ở tay để gi&uacute;p cho v&ugrave;ng da mặt đều m&agrave;u với l&agrave;n da của to&agrave;n body khi trang điểm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch x&aacute;c định m&agrave;u kem n&ecirc;n dựa tr&ecirc;n m&agrave;u mạch m&aacute;u như sau:</span></p>\r\n\r\n<p style="text-align:center"><img alt="kem-trang-diem-tot-nhat-hien-nay" height="524" src="/image/images/kem-trang-diem-tot-nhat-hien-nay.jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><em>C&aacute;ch x&aacute;c định m&agrave;u kem trang điểm dựa tr&ecirc;n m&agrave;u mạch m&aacute;u cổ tay</em></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">+T&ocirc;ng lạnh</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Biểu hiện của t&ocirc;ng lạnh của da l&agrave; mạch m&aacute;u c&oacute; m&agrave;u t&iacute;m hoặc xanh t&iacute;m. Khi nh&igrave;n dưới nắng mặt trời da c&oacute; m&agrave;u xanh dương.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">+T&ocirc;ng trung t&iacute;nh</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">L&agrave;n da thuộc t&ocirc;ng trung t&iacute;nh th&igrave; mạch m&aacute;u dưới cổ tay sẽ c&oacute; 2 m&agrave;u xanh dương v&agrave; xanh l&aacute;.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">+T&ocirc;ng ấm</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nếu mạch m&aacute;u dưới cổ tay c&oacute; m&agrave;u xanh nhạt hoặc oliu th&igrave; da của bạn thuộc t&ocirc;ng ấm. N&ecirc;n lựa chọn kem trang điểm theo m&agrave;u n&agrave;y.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="color:#000000"><strong>2.Chọn m&agrave;u kem nền dựa theo v&ugrave;ng da ở mặt &ndash; cổ</strong></span></h3>\r\n\r\n<p style="text-align:center"><img alt="kem-trang-diem-tot-nhat-hien-nay" height="516" src="/image/images/kem-trang-diem-tot-nhat-hien-nay%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><span style="color:#000000"><em>C&aacute;ch chọn m&agrave;u kem nền dựa theo v&ugrave;ng da ở mặt &ndash; cổ</em></span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bạn n&ecirc;n lựa chọn m&agrave;u của kem n&ecirc;n ph&ugrave; hợp với v&ugrave;ng da ở cổ-ngực-mặt. Sau đ&oacute; trộn đều 3 tone m&agrave;u v&agrave; thoa cho v&ugrave;ng mặt. M&agrave;u sắc trung tinh sẽ mang tới sự h&agrave;i h&ograve;a cho v&ugrave;ng da mặt v&agrave; mang tới sự c&acirc;n bằng với những v&ugrave;ng c&ograve;n lại. Tuy nhi&ecirc;n, lựa chọn m&agrave;u của kem nền theo c&aacute;ch n&agrave;y sẽ tốn k&eacute;m v&agrave; tốn thời gian.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; 2 c&aacute;ch lựa chọn m&agrave;u</span><strong><a href="http://myichy.vn/cach-chon-mau-kem-trang-diem-tot-nhat-hien-nay-phu-hop-voi-mau-cua-lan-da"><span style="color:#2980b9"> kem trang điểm tốt nhất hiện na</span></a></strong><span style="color:#000000"><strong>y</strong> ph&ugrave; hợp với m&agrave;u của lan da, gi&uacute;p bạn sở hữu gương mặt trang điểm tự nhi&ecirc;n, nhẹ nh&agrave;ng v&agrave; cuốn h&uacute;t người đối diện.</span></p>\r\n', '', '', '', '', '', 0, 'cach-chon-mau-kem-trang-diem-tot-nhat-hien-nay-phu-hop-voi-mau-cua-lan-da', 'Cách chọn màu kem trang điểm tốt nhất hiện nay phù hợp với màu của làn da', 'Trên thị trường có rất nhiều loại kem trang điểm khác nhau. Vậy nên chọn kem trang điểm tốt nhất hiện nay như thế nào để phù hợp với màu da của mình?', 'kem trang điểm tốt nhất hiện nay'),
(112, 56, 'en', 'english version Cách chọn màu kem trang điểm tốt nhất hiện nay phù hợp với màu của làn da', 'english version Trên thị trường có rất nhiều loại kem trang điểm khác nhau. Vậy nên chọn kem trang điểm tốt nhất hiện nay như thế nào để phù hợp với màu da của mình?', 'english version <p dir="ltr" style="text-align:justify"><strong><em>Tr&ecirc;n thị trường c&oacute; rất nhiều loại kem trang điểm kh&aacute;c nhau. Vậy n&ecirc;n chọn kem trang điểm tốt nhất hiện nay như thế n&agrave;o để ph&ugrave; hợp với m&agrave;u da của m&igrave;nh?</em></strong></p>\r\n\r\n<p dir="ltr" style="text-align:justify">Lựa chọn được loại kem trang điểm tốt nhất hiện nay ph&ugrave; hợp với m&agrave;u da gi&uacute;p bạn trang điểm tự nhi&ecirc;n v&agrave; nhẹ nh&agrave;ng nhưng vẫn đẹp v&agrave; cuốn h&uacute;t. H&atilde;y tham khảo ngay những th&ocirc;ng tin dưới đ&acirc;y m&agrave; ch&uacute;ng t&ocirc;i cung cấp cho bạn.</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong>C&aacute;ch chọn kem trang điểm tốt nhất hiện nay ph&ugrave; hợp với m&agrave;u da</strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify">Để c&oacute; được gương mặt đẹp tự nhi&ecirc;n, trang điểm kh&ocirc;ng qu&aacute; lố th&igrave; việc lựa chọn sản phẩm an to&agrave;n bạn n&ecirc;n quan t&acirc;m tới m&agrave;u sắc của kem nền sao cho ph&ugrave; hợp với l&agrave;n da của m&igrave;nh. Lựa chọn m&agrave;u <strong><a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#2980b9">kem trang điểm tốt nhất hiện nay</span></a> </strong>ph&ugrave; hợp với m&agrave;u của da l&agrave; bước cực kỳ quan trọng. Bởi v&igrave; nếu bạn sử dụng kem trang điểm c&oacute; độ che phủ tốt nhưng kh&ocirc;ng hợp với m&agrave;u da sẽ khiến cho da mặt bị c&ocirc; lập so với da cổ, da tay, da ngực.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Do đ&oacute; h&atilde;y tham khảo ngay 2 c&aacute;ch lựa chọn kem nền ph&ugrave; hợp với m&agrave;u da của bạn ngay dưới đ&acirc;y:</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>1.Chọn kem trang điểm dựa tr&ecirc;n m&agrave;u mạch m&aacute;u cổ tay</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">M&agrave;u da mặt c&oacute; thể thay đổi do sự t&aacute;c động của thời gian, &aacute;nh nắng mặt trời&hellip;tuy nhi&ecirc;n m&agrave;u sắc của mạch m&aacute;u lại kh&ocirc;ng bao giờ thay đổi. Do đ&oacute; bạn c&oacute; thể lựa chọn m&agrave;u của kem n&ecirc;n tr&ugrave;ng với m&agrave;u mạch m&aacute;u ở tay để gi&uacute;p cho v&ugrave;ng da mặt đều m&agrave;u với l&agrave;n da của to&agrave;n body khi trang điểm.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">C&aacute;ch x&aacute;c định m&agrave;u kem n&ecirc;n dựa tr&ecirc;n m&agrave;u mạch m&aacute;u như sau:</p>\r\n\r\n<p style="text-align:center"><img alt="kem-trang-diem-tot-nhat-hien-nay" height="524" src="/image/images/kem-trang-diem-tot-nhat-hien-nay.jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>C&aacute;ch x&aacute;c định m&agrave;u kem trang điểm dựa tr&ecirc;n m&agrave;u mạch m&aacute;u cổ tay</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">+T&ocirc;ng lạnh</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Biểu hiện của t&ocirc;ng lạnh của da l&agrave; mạch m&aacute;u c&oacute; m&agrave;u t&iacute;m hoặc xanh t&iacute;m. Khi nh&igrave;n dưới nắng mặt trời da c&oacute; m&agrave;u xanh dương.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">+T&ocirc;ng trung t&iacute;nh</p>\r\n\r\n<p dir="ltr" style="text-align:justify">L&agrave;n da thuộc t&ocirc;ng trung t&iacute;nh th&igrave; mạch m&aacute;u dưới cổ tay sẽ c&oacute; 2 m&agrave;u xanh dương v&agrave; xanh l&aacute;.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">+T&ocirc;ng ấm</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Nếu mạch m&aacute;u dưới cổ tay c&oacute; m&agrave;u xanh nhạt hoặc oliu th&igrave; da của bạn thuộc t&ocirc;ng ấm. N&ecirc;n lựa chọn kem trang điểm theo m&agrave;u n&agrave;y.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>2.Chọn m&agrave;u kem nền dựa theo v&ugrave;ng da ở mặt &ndash; cổ</strong></h3>\r\n\r\n<p style="text-align:center"><img alt="kem-trang-diem-tot-nhat-hien-nay" height="516" src="/image/images/kem-trang-diem-tot-nhat-hien-nay%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>C&aacute;ch chọn m&agrave;u kem nền dựa theo v&ugrave;ng da ở mặt &ndash; cổ</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n lựa chọn m&agrave;u của kem n&ecirc;n ph&ugrave; hợp với v&ugrave;ng da ở cổ-ngực-mặt. Sau đ&oacute; trộn đều 3 tone m&agrave;u v&agrave; thoa cho v&ugrave;ng mặt. M&agrave;u sắc trung tinh sẽ mang tới sự h&agrave;i h&ograve;a cho v&ugrave;ng da mặt v&agrave; mang tới sự c&acirc;n bằng với những v&ugrave;ng c&ograve;n lại. Tuy nhi&ecirc;n, lựa chọn m&agrave;u của kem nền theo c&aacute;ch n&agrave;y sẽ tốn k&eacute;m v&agrave; tốn thời gian.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Tr&ecirc;n đ&acirc;y l&agrave; 2 c&aacute;ch lựa chọn m&agrave;u<strong> kem trang điểm tốt nhất hiện nay</strong> ph&ugrave; hợp với m&agrave;u của lan da, gi&uacute;p bạn sở hữu gương mặt trang điểm tự nhi&ecirc;n, nhẹ nh&agrave;ng v&agrave; cuốn h&uacute;t người đối diện.</p>\r\n', '', '', '', '', '', 0, 'en-cach-chon-mau-kem-trang-diem-tot-nhat-hien-nay-phu-hop-voi-mau-cua-lan-da', 'Cách chọn màu kem trang điểm tốt nhất hiện nay phù hợp với màu của làn da', 'Trên thị trường có rất nhiều loại kem trang điểm khác nhau. Vậy nên chọn kem trang điểm tốt nhất hiện nay như thế nào để phù hợp với màu da của mình?', 'kem trang điểm tốt nhất hiện nay');
INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(113, 57, 'vn', 'Hướng dẫn các cách dưỡng trắng da mặt hiệu quả', 'Bạn hãy áp dụng ngay những cách dưỡng trắng da mặt hiệu quả được giới thiệu dưới đây để cải thiện làn da của mình trở nên trắng sáng và trẻ đẹp hơn.\r\n', '<p dir="ltr" style="text-align:justify"><em><span style="color:#000000">Bạn h&atilde;y &aacute;p dụng ngay những c&aacute;ch dưỡng trắng da mặt hiệu quả được giới thiệu dưới đ&acirc;y để cải thiện l&agrave;n da của m&igrave;nh trở n&ecirc;n trắng s&aacute;ng v&agrave; trẻ đẹp hơn.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&oacute; rất nhiều c&aacute;ch để</span><a href="http://myichy.vn/san-pham-tam-trang"><span style="color:#2980b9"><strong> dưỡng trắng da mặt hiệu quả</strong></span></a><span style="color:#000000"> tại nh&agrave; gi&uacute;p bạn cải thiện l&agrave;n da của m&igrave;nh trở n&ecirc;n trẻ trung hơn. Tuy nhi&ecirc;n, ngay sau đ&acirc;y ch&uacute;ng t&ocirc;i sẽ hướng dẫn những c&aacute;ch dưỡng trắng da mặt hiệu quả nhất v&agrave; an to&agrave;n nhất d&agrave;nh cho bạn.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Những c&aacute;ch dưỡng trắng da mặt hiệu quả tại nh&agrave;</span></strong></span></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">1.Dưỡng trắng da mặt với dưa leo v&agrave; trứng g&agrave;</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong dưa leo c&oacute; rất nhiều loại vitamin cần thiết cho da, đồng thời ch&uacute;ng cũng chứa lượng axit folic lớn. Với c&aacute;ch dưỡng trắng da mặt hiệu quả với dưa leo v&agrave; trứng g&agrave; sẽ gi&uacute;p l&agrave;m sạch dầu nhờn, dưỡng trắng da hiệu quả từ b&ecirc;n trong. Bởi l&ograve;ng trắng trứng g&agrave; c&oacute; nhiều vitamin nh&oacute;m B, amide của vitamin B3, niacinamide c&oacute; t&aacute;c dụng dưỡng ẩm, bổ sung &nbsp;collagen v&agrave; l&agrave;m s&aacute;ng da hiệu quả.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="450" src="/image/images/duong-trang-da-mat-hieu-qua%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da mặt với dưa leo v&agrave; trứng g&agrave;</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sử dụng 2 th&igrave;a nước &eacute;p dưa leo trộn đều với 1 l&ograve;ng trắng trứng g&agrave;. Sử dụng đũa để đ&aacute;nh hỗn hợp n&agrave;y thật đều v&agrave; thoa l&ecirc;n mặt. Đợi mặt na kh&ocirc; sẽ rửa lại bằng nước ấm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lưu &yacute;: c&aacute;ch dưỡng trắng n&agrave;y ph&ugrave; hợp với những bạn c&oacute; l&agrave;n da hỗn hợp. L&agrave;n da c&oacute; chỗ kh&ocirc;, chỗ nhờn. Khi chăm s&oacute;c da mặt bạn n&ecirc;n sử dụng sữa rửa mặt d&agrave;nh cho da nhờn trước khi đắp mặt nạ.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">2.Dưỡng trắng da mặt bằng d&acirc;u t&acirc;y</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">D&acirc;u t&acirc;y c&oacute; nhiều vitamin C gi&uacute;p kh&aacute;ng khuẩn v&agrave; chống l&atilde;o h&oacute;a rất tốt. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n gi&uacute;p cải thiện sắc tố da, dưỡng da trắng hồng từ b&ecirc;n trong.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="315" src="/image/images/duong-trang-da-mat-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da mặt bằng d&acirc;u t&acirc;y</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Rửa sạch v&agrave; xay nhuyễn 5 tr&aacute;i d&acirc;y t&acirc;y, sau đ&oacute; trộn chung với 2 th&igrave;a mật ong. Thoa hỗn hợp tr&ecirc;n l&ecirc;n mặt trong khoảng 20-30 ph&uacute;t. Sau đ&oacute;, bạn rửa mặt lại bằng nước lạnh. Mặt nạ d&acirc;u t&acirc;y kh&ocirc;ng chỉ gi&uacute;p dưỡng trắng da m&agrave; c&ograve;n khiến cho l&agrave;n da khỏe mạnh v&agrave; căng mịn hơn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lưu &yacute;: kh&ocirc;ng n&ecirc;n b&ocirc;i gần mắt v&igrave; d&acirc;u t&acirc;y c&oacute; thể g&acirc;y k&iacute;ch ứng cho mắt. Mặt nạ n&agrave;y ph&ugrave; hợp sử dụng cho l&agrave;n da thường.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">3.Dưỡng trắng da với Face Đ&agrave;o Ti&ecirc;n Sứ</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Một trong những </span><a href="http://myichy.vn/huong-dan-cac-cach-duong-trang-da-mat-hieu-qua"><span style="color:#3498db"><strong>c&aacute;ch dưỡng trắng da mặt hiệu quả</strong></span></a><span style="color:#000000"> d&agrave;nh cho mọi loại da đ&oacute; l&agrave; sử dụng sản phẩm dưỡng trắng Face Đ&agrave;o Ti&ecirc;n Sứ. Th&agrave;nh phần gồm c&oacute; Nước tinh khiết, Nha đam, Vaseline v&agrave; Vitamin E.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="450" src="/image/images/duong-trang-da-mat-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da với Face Đ&agrave;o Ti&ecirc;n Sứ</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">&nbsp;Sản phẩm n&agrave;y c&oacute; c&ocirc;ng dung cung cấp những chất cần thiết cho da, gi&uacute;p l&agrave;m da trở n&ecirc;n mềm mại, hồng h&agrave;o v&agrave; chắc khỏe hơn. B&ecirc;n cạnh đ&oacute; Face Đ&agrave;o Ti&ecirc;n Sứ c&ograve;n bổ sung những dưỡng chất gi&uacute;p trẻ h&oacute;a l&agrave;n da, cải thiện v&agrave; dưỡng trắng da hiệu quả.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Khi sử dụng Face Đ&agrave;o Ti&ecirc;n Sứ c&oacute; hương thơm dịu d&agrave;ng gi&uacute;p mang tới cảm gi&aacute;c sảng kho&aacute;i khi sử dụng. Thoa kem dưỡng trắng n&agrave;y s&aacute;ng tối để c&oacute; hiệu quả dưỡng da tốt nhất.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; c&aacute;c c&aacute;ch dưỡng trắng da mặt hiệu quả tại nh&agrave;, bạn n&ecirc;n tham khảo v&agrave; &aacute;p dụng ngay để c&oacute; l&agrave;n da đẹp, trẻ trung.</span></p>\r\n', '', '', '', '', '', 0, 'huong-dan-cac-cach-duong-trang-da-mat-hieu-qua', 'Hướng dẫn các cách dưỡng trắng da mặt hiệu quả', 'Bạn hãy áp dụng ngay những cách dưỡng trắng da mặt hiệu quả được giới thiệu dưới đây để cải thiện làn da của mình trở nên trắng sáng và trẻ đẹp hơn.', ' dưỡng trắng da mặt hiệu quả '),
(114, 57, 'en', 'english version Hướng dẫn các cách dưỡng trắng da mặt hiệu quả', 'english version Bạn hãy áp dụng ngay những cách dưỡng trắng da mặt hiệu quả được giới thiệu dưới đây để cải thiện làn da của mình trở nên trắng sáng và trẻ đẹp hơn.\r\n', 'english version <p dir="ltr" style="text-align:justify"><span style="color:#000000">Bạn h&atilde;y &aacute;p dụng ngay những c&aacute;ch dưỡng trắng da mặt hiệu quả được giới thiệu dưới đ&acirc;y để cải thiện l&agrave;n da của m&igrave;nh trở n&ecirc;n trắng s&aacute;ng v&agrave; trẻ đẹp hơn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&oacute; rất nhiều c&aacute;ch để</span><a href="http://myichy.vn/san-pham-tam-trang"><span style="color:#2980b9"><strong> dưỡng trắng da mặt hiệu quả</strong></span></a><span style="color:#000000"> tại nh&agrave; gi&uacute;p bạn cải thiện l&agrave;n da của m&igrave;nh trở n&ecirc;n trẻ trung hơn. Tuy nhi&ecirc;n, ngay sau đ&acirc;y ch&uacute;ng t&ocirc;i sẽ hướng dẫn những c&aacute;ch dưỡng trắng da mặt hiệu quả nhất v&agrave; an to&agrave;n nhất d&agrave;nh cho bạn.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Những c&aacute;ch dưỡng trắng da mặt hiệu quả tại nh&agrave;</span></strong></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">1.Dưỡng trắng da mặt với dưa leo v&agrave; trứng g&agrave;</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong dưa leo c&oacute; rất nhiều loại vitamin cần thiết cho da, đồng thời ch&uacute;ng cũng chứa lượng axit folic lớn. Với c&aacute;ch dưỡng trắng da mặt hiệu quả với dưa leo v&agrave; trứng g&agrave; sẽ gi&uacute;p l&agrave;m sạch dầu nhờn, dưỡng trắng da hiệu quả từ b&ecirc;n trong. Bởi l&ograve;ng trắng trứng g&agrave; c&oacute; nhiều vitamin nh&oacute;m B, amide của vitamin B3, niacinamide c&oacute; t&aacute;c dụng dưỡng ẩm, bổ sung &nbsp;collagen v&agrave; l&agrave;m s&aacute;ng da hiệu quả.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="450" src="/image/images/duong-trang-da-mat-hieu-qua%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da mặt với dưa leo v&agrave; trứng g&agrave;</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sử dụng 2 th&igrave;a nước &eacute;p dưa leo trộn đều với 1 l&ograve;ng trắng trứng g&agrave;. Sử dụng đũa để đ&aacute;nh hỗn hợp n&agrave;y thật đều v&agrave; thoa l&ecirc;n mặt. Đợi mặt na kh&ocirc; sẽ rửa lại bằng nước ấm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lưu &yacute;: c&aacute;ch dưỡng trắng n&agrave;y ph&ugrave; hợp với những bạn c&oacute; l&agrave;n da hỗn hợp. L&agrave;n da c&oacute; chỗ kh&ocirc;, chỗ nhờn. Khi chăm s&oacute;c da mặt bạn n&ecirc;n sử dụng sữa rửa mặt d&agrave;nh cho da nhờn trước khi đắp mặt nạ.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">2.Dưỡng trắng da mặt bằng d&acirc;u t&acirc;y</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">D&acirc;u t&acirc;y c&oacute; nhiều vitamin C gi&uacute;p kh&aacute;ng khuẩn v&agrave; chống l&atilde;o h&oacute;a rất tốt. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n gi&uacute;p cải thiện sắc tố da, dưỡng da trắng hồng từ b&ecirc;n trong.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="315" src="/image/images/duong-trang-da-mat-hieu-qua%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da mặt bằng d&acirc;u t&acirc;y</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch thực hiện:</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Rửa sạch v&agrave; xay nhuyễn 5 tr&aacute;i d&acirc;y t&acirc;y, sau đ&oacute; trộn chung với 2 th&igrave;a mật ong. Thoa hỗn hợp tr&ecirc;n l&ecirc;n mặt trong khoảng 20-30 ph&uacute;t. Sau đ&oacute;, bạn rửa mặt lại bằng nước lạnh. Mặt nạ d&acirc;u t&acirc;y kh&ocirc;ng chỉ gi&uacute;p dưỡng trắng da m&agrave; c&ograve;n khiến cho l&agrave;n da khỏe mạnh v&agrave; căng mịn hơn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Lưu &yacute;: kh&ocirc;ng n&ecirc;n b&ocirc;i gần mắt v&igrave; d&acirc;u t&acirc;y c&oacute; thể g&acirc;y k&iacute;ch ứng cho mắt. Mặt nạ n&agrave;y ph&ugrave; hợp sử dụng cho l&agrave;n da thường.</span></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">3.Dưỡng trắng da với Face Đ&agrave;o Ti&ecirc;n Sứ</span></strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Một trong những c&aacute;ch dưỡng trắng da mặt hiệu quả d&agrave;nh cho mọi loại da đ&oacute; l&agrave; sử dụng sản phẩm dưỡng trắng Face Đ&agrave;o Ti&ecirc;n Sứ. Th&agrave;nh phần gồm c&oacute; Nước tinh khiết, Nha đam, Vaseline v&agrave; Vitamin E.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-mat-hieu-qua" height="450" src="/image/images/duong-trang-da-mat-hieu-qua%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da với Face Đ&agrave;o Ti&ecirc;n Sứ</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">&nbsp;Sản phẩm n&agrave;y c&oacute; c&ocirc;ng dung cung cấp những chất cần thiết cho da, gi&uacute;p l&agrave;m da trở n&ecirc;n mềm mại, hồng h&agrave;o v&agrave; chắc khỏe hơn. B&ecirc;n cạnh đ&oacute; Face Đ&agrave;o Ti&ecirc;n Sứ c&ograve;n bổ sung những dưỡng chất gi&uacute;p trẻ h&oacute;a l&agrave;n da, cải thiện v&agrave; dưỡng trắng da hiệu quả.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Khi sử dụng Face Đ&agrave;o Ti&ecirc;n Sứ c&oacute; hương thơm dịu d&agrave;ng gi&uacute;p mang tới cảm gi&aacute;c sảng kho&aacute;i khi sử dụng. Thoa kem dưỡng trắng n&agrave;y s&aacute;ng tối để c&oacute; hiệu quả dưỡng da tốt nhất.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; c&aacute;c c&aacute;ch dưỡng trắng da mặt hiệu quả tại nh&agrave;, bạn n&ecirc;n tham khảo v&agrave; &aacute;p dụng ngay để c&oacute; l&agrave;n da đẹp, trẻ trung.</span></p>\r\n', '', '', '', '', '', 0, 'en-huong-dan-cac-cach-duong-trang-da-mat-hieu-qua', 'Hướng dẫn các cách dưỡng trắng da mặt hiệu quả', 'Bạn hãy áp dụng ngay những cách dưỡng trắng da mặt hiệu quả được giới thiệu dưới đây để cải thiện làn da của mình trở nên trắng sáng và trẻ đẹp hơn.', ' dưỡng trắng da mặt hiệu quả '),
(115, 58, 'vn', 'Dưỡng trắng da toàn thân tự nhiên tại nhà', 'Dưỡng trắng da toàn thân tự nhiên tại nhà bằng các phương pháp đơn giản, hiệu quả tiện lợi, tiết kiệm là những gì chúng tôi sẽ mang tới cho chị em trong bài viết này.', '<p dir="ltr" style="text-align:justify"><em><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; bằng c&aacute;c phương ph&aacute;p đơn giản, hiệu quả tiện lợi, tiết kiệm l&agrave; những g&igrave; ch&uacute;ng t&ocirc;i sẽ mang tới cho chị em trong b&agrave;i viết n&agrave;y.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c chị em ai cũng mong muốn c&oacute; được l&agrave;n da săn chắc, trắng s&aacute;ng tự nhi&ecirc;n nhưng kh&ocirc;ng phải ai cũng c&oacute; điều kiện về kinh tế, điều kiện về thời gian để c&oacute; thể đến spa, trung t&acirc;m l&agrave;m đẹp&hellip;Vậy h&atilde;y để ch&uacute;ng t&ocirc;i chia sẻ cho c&aacute;c chị em những phương ph&aacute;p </span><a href="http://myichy.vn/san-pham-tri-mun"><span style="color:#2980b9"><strong>dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; </strong></span></a><span style="color:#000000">đơn giản, hiệu quả v&agrave; tiết kiệm.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; với nha đam (l&ocirc; hội)</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c th&agrave;nh phần c&oacute; trong nha đam: Canxi, Kẽm, Natri&hellip; đ&acirc;y đề l&agrave; c&aacute;c chất rất tốt cho da.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nguy&ecirc;n liệu: 2-3 l&aacute; nha đam-chọn những l&aacute; to, mọng nước</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch l&agrave;m: &nbsp;B&oacute;c vỏ l&aacute; nha đam, đem rửa sạch với nước muối rồi &eacute;p l&agrave; nha đam để lấy nước</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch d&ugrave;ng: Thoa nước &eacute;p từ nha đam l&ecirc;n to&agrave;n th&acirc;n chờ khoảng 15 ph&uacute;t th&igrave; tắm lại bằng nước sạch</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="400" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Nha đam- nguy&ecirc;n liệu l&agrave;m trắng da từ tự nhi&ecirc;n</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Đ&acirc;y l&agrave; phương ph&aacute;p đơn giản, tuy nhi&ecirc;n để đạt được kết quả mong đợi c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">C&aacute;ch dưỡng trắng da bằng chanh tươi</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong chanh c&oacute; chứa h&agrave;m lượng lớn Vitamin C, gi&uacute;p tăng sức đề kh&aacute;ng v&agrave; ngăn ngừa qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a cho da. Đặc biệt lượng axit tự nhi&ecirc;n trong chanh rất hiệu quả trong việc tẩy da chết gi&uacute;p da lấy lại n&eacute;t trắng s&aacute;ng tự nhi&ecirc;n.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch l&agrave;m v&agrave; c&aacute;ch d&ugrave;ng: Bạn chỉ cần vắt 3-4 quả chanh tươi rồi thoa l&ecirc;n da trước khi tắm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bạn sẽ thấy hiệu quả bất ngờ với phương ph&aacute;p n&agrave;y trong thời gian ngắn, tuy nhi&ecirc;n bạn kh&ocirc;ng n&ecirc;n lạm dụng &nbsp;c&aacute;ch n&agrave;y qu&aacute; thường xuy&ecirc;n v&igrave; axit trong chanh c&oacute; thể l&agrave;m mỏng da của bạn</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="400" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da với chanh tươi</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; bằng Body Tuyết Lạnh</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để c&oacute; thể dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; bằng c&aacute;c phương ph&aacute;p tự nhi&ecirc;n th&igrave; c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i, c&aacute;ch l&agrave;m tuy đơn giản nhưng kh&aacute; bất tiện khi phải thoa l&ecirc;n to&agrave;n th&acirc;n v&agrave; kh&aacute; tốn thời gian. C&aacute;c chị em hẳn muốn c&oacute; một c&aacute;ch thực hiện đơn giản, hiệu quả, tiện lợi, tiết kiệm v&agrave; nhanh ch&oacute;ng cho hiệu quả. Hiểu được t&acirc;m l&yacute; của c&aacute;c chị em, C&ocirc;ng ty dược mỹ phẩm cao cấp My-ichy đ&atilde; cho ra đời sản phẩm Body Tuyết Lạnh</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Body Tuyết Lạnh với những th&agrave;nh phần rất tốt cho da như cabomer, chiết xuất Aloe Babadensis, nước tinh khiết, bột ngọc trai, PEG-100, alphabutin, Grycerin, rượu Cetyl&hellip;c&oacute; c&ocirc;ng dụng cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da săn chắc, trắng hồng tự nhi&ecirc;n, đồng tời gi&uacute;p phục hồi da hư tổn, l&agrave;m trẻ h&oacute;a da từ b&ecirc;n trong c&ugrave;ng với hương thơm dịu nhẹ từ tự nhi&ecirc;n mang lại cảm gi&aacute;c thoải m&aacute;i, thư gi&atilde;n.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng của Body Tuyết Lạnh: Sau khi l&agrave;m sạch da, sau đ&oacute; thoa đều kem l&ecirc;n to&agrave;n bộ cơ thể (kh&ocirc;ng thoa l&ecirc;n mặt). Massage cho c&aacute;c dưỡng chất thấm đều v&agrave;o da</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="450" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Body Tuyết Lạnh</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sản phẩm c&oacute; thể d&ugrave;ng được c&aacute;c thời điểm trong ng&agrave;y v&agrave; d&ugrave;ng thường xuy&ecirc;n hằng ng&agrave;y sẽ mang lại hiệu quả cao</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm c&aacute;c bạn h&atilde;y truy cập v&agrave;o website: </span><strong><a href="http://myichy.vn"><span style="color:#2980b9">http://myichy.vn</span></a></strong><span style="color:#000000"> </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch gi&uacute;p chị em<strong> </strong></span><strong><a href="http://myichy.vn/duong-trang-da-toan-than-tu-nhien-tai-nha"><span style="color:#3498db">dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave;</span></a></strong><a href="http://myichy.vn/duong-trang-da-toan-than-tu-nhien-tai-nha"><span style="color:#3498db"> </span></a><span style="color:#000000">m&agrave; kh&ocirc;ng cần tốn tiền bạc v&agrave; thời gian đến spa, trung t&acirc;m l&agrave;m đẹp&hellip;m&agrave; vẫn mang lại hiệu quả. C&aacute;c chị em h&atilde;y &aacute;p dụng v&agrave; chia sẻ với bạn b&egrave; ngay th&ocirc;i n&agrave;o. Ch&uacute;c chị em c&oacute; một l&agrave;n da như &yacute;!</span></p>\r\n', '', '', '', '', '', 0, 'duong-trang-da-toan-than-tu-nhien-tai-nha', 'Dưỡng trắng da toàn thân tự nhiên tại nhà', 'Dưỡng trắng da toàn thân tự nhiên tại nhà bằng các phương pháp đơn giản, hiệu quả tiện lợi, tiết kiệm là những gì chúng tôi sẽ mang tới cho chị em trong bài viết này.', ' dưỡng trắng da toàn thân tự nhiên tại nhà'),
(116, 58, 'en', 'english version Dưỡng trắng da toàn thân tự nhiên tại nhà', 'english version Dưỡng trắng da toàn thân tự nhiên tại nhà bằng các phương pháp đơn giản, hiệu quả tiện lợi, tiết kiệm là những gì chúng tôi sẽ mang tới cho chị em trong bài viết này.', 'english version <p dir="ltr" style="text-align:justify"><em><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; bằng c&aacute;c phương ph&aacute;p đơn giản, hiệu quả tiện lợi, tiết kiệm l&agrave; những g&igrave; ch&uacute;ng t&ocirc;i sẽ mang tới cho chị em trong b&agrave;i viết n&agrave;y.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c chị em ai cũng mong muốn c&oacute; được l&agrave;n da săn chắc, trắng s&aacute;ng tự nhi&ecirc;n nhưng kh&ocirc;ng phải ai cũng c&oacute; điều kiện về kinh tế, điều kiện về thời gian để c&oacute; thể đến spa, trung t&acirc;m l&agrave;m đẹp&hellip;Vậy h&atilde;y để ch&uacute;ng t&ocirc;i chia sẻ cho c&aacute;c chị em những phương ph&aacute;p </span><a href="http://myichy.vn/san-pham-tri-mun"><span style="color:#2980b9"><strong>dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; </strong></span></a><span style="color:#000000">đơn giản, hiệu quả v&agrave; tiết kiệm.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; với nha đam (l&ocirc; hội)</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c th&agrave;nh phần c&oacute; trong nha đam: Canxi, Kẽm, Natri&hellip; đ&acirc;y đề l&agrave; c&aacute;c chất rất tốt cho da.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nguy&ecirc;n liệu: 2-3 l&aacute; nha đam-chọn những l&aacute; to, mọng nước</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch l&agrave;m: &nbsp;B&oacute;c vỏ l&aacute; nha đam, đem rửa sạch với nước muối rồi &eacute;p l&agrave; nha đam để lấy nước</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch d&ugrave;ng: Thoa nước &eacute;p từ nha đam l&ecirc;n to&agrave;n th&acirc;n chờ khoảng 15 ph&uacute;t th&igrave; tắm lại bằng nước sạch</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="400" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Nha đam- nguy&ecirc;n liệu l&agrave;m trắng da từ tự nhi&ecirc;n</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Đ&acirc;y l&agrave; phương ph&aacute;p đơn giản, tuy nhi&ecirc;n để đạt được kết quả mong đợi c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">C&aacute;ch dưỡng trắng da bằng chanh tươi</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Trong chanh c&oacute; chứa h&agrave;m lượng lớn Vitamin C, gi&uacute;p tăng sức đề kh&aacute;ng v&agrave; ngăn ngừa qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a cho da. Đặc biệt lượng axit tự nhi&ecirc;n trong chanh rất hiệu quả trong việc tẩy da chết gi&uacute;p da lấy lại n&eacute;t trắng s&aacute;ng tự nhi&ecirc;n.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch l&agrave;m v&agrave; c&aacute;ch d&ugrave;ng: Bạn chỉ cần vắt 3-4 quả chanh tươi rồi thoa l&ecirc;n da trước khi tắm.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bạn sẽ thấy hiệu quả bất ngờ với phương ph&aacute;p n&agrave;y trong thời gian ngắn, tuy nhi&ecirc;n bạn kh&ocirc;ng n&ecirc;n lạm dụng &nbsp;c&aacute;ch n&agrave;y qu&aacute; thường xuy&ecirc;n v&igrave; axit trong chanh c&oacute; thể l&agrave;m mỏng da của bạn</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="400" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Dưỡng trắng da với chanh tươi</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave; bằng Body Tuyết Lạnh</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để c&oacute; thể dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; bằng c&aacute;c phương ph&aacute;p tự nhi&ecirc;n th&igrave; c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i, c&aacute;ch l&agrave;m tuy đơn giản nhưng kh&aacute; bất tiện khi phải thoa l&ecirc;n to&agrave;n th&acirc;n v&agrave; kh&aacute; tốn thời gian. C&aacute;c chị em hẳn muốn c&oacute; một c&aacute;ch thực hiện đơn giản, hiệu quả, tiện lợi, tiết kiệm v&agrave; nhanh ch&oacute;ng cho hiệu quả. Hiểu được t&acirc;m l&yacute; của c&aacute;c chị em, C&ocirc;ng ty dược mỹ phẩm cao cấp My-ichy đ&atilde; cho ra đời sản phẩm Body Tuyết Lạnh</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Body Tuyết Lạnh với những th&agrave;nh phần rất tốt cho da như cabomer, chiết xuất Aloe Babadensis, nước tinh khiết, bột ngọc trai, PEG-100, alphabutin, Grycerin, rượu Cetyl&hellip;c&oacute; c&ocirc;ng dụng cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da săn chắc, trắng hồng tự nhi&ecirc;n, đồng tời gi&uacute;p phục hồi da hư tổn, l&agrave;m trẻ h&oacute;a da từ b&ecirc;n trong c&ugrave;ng với hương thơm dịu nhẹ từ tự nhi&ecirc;n mang lại cảm gi&aacute;c thoải m&aacute;i, thư gi&atilde;n.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng của Body Tuyết Lạnh: Sau khi l&agrave;m sạch da, sau đ&oacute; thoa đều kem l&ecirc;n to&agrave;n bộ cơ thể (kh&ocirc;ng thoa l&ecirc;n mặt). Massage cho c&aacute;c dưỡng chất thấm đều v&agrave;o da</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-tu-nhien-tai-nha" height="450" src="/image/images/duong-trang-da-toan-than-tu-nhien-tai-nha%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Body Tuyết Lạnh</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sản phẩm c&oacute; thể d&ugrave;ng được c&aacute;c thời điểm trong ng&agrave;y v&agrave; d&ugrave;ng thường xuy&ecirc;n hằng ng&agrave;y sẽ mang lại hiệu quả cao</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm c&aacute;c bạn h&atilde;y truy cập v&agrave;o website: </span><strong><a href="http://myichy.vn"><span style="color:#2980b9">http://myichy.vn</span></a></strong><span style="color:#000000"> </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch gi&uacute;p chị em<strong> dưỡng trắng da to&agrave;n th&acirc;n tự nhi&ecirc;n tại nh&agrave;</strong> m&agrave; kh&ocirc;ng cần tốn tiền bạc v&agrave; thời gian đến spa, trung t&acirc;m l&agrave;m đẹp&hellip;m&agrave; vẫn mang lại hiệu quả. C&aacute;c chị em h&atilde;y &aacute;p dụng v&agrave; chia sẻ với bạn b&egrave; ngay th&ocirc;i n&agrave;o. Ch&uacute;c chị em c&oacute; một l&agrave;n da như &yacute;!</span></p>\r\n', '', '', '', '', '', 0, 'en-duong-trang-da-toan-than-tu-nhien-tai-nha', 'Dưỡng trắng da toàn thân tự nhiên tại nhà', 'Dưỡng trắng da toàn thân tự nhiên tại nhà bằng các phương pháp đơn giản, hiệu quả tiện lợi, tiết kiệm là những gì chúng tôi sẽ mang tới cho chị em trong bài viết này.', ' dưỡng trắng da toàn thân tự nhiên tại nhà'),
(117, 59, 'vn', 'Cách dưỡng trắng da toàn thân mùa hè', 'Mùa hè nóng bức đã tới, các chị em chắc chắn ai cũng muốn diện những bộ bikini, áo trễ vai, đầm váy ngắn. Nhưng lại sợ bị đen da, cháy nắng. Bài viết này sẽ chia sẻ tới các chị em  cách dưỡng trắng da toàn thân mùa hè.', '<p dir="ltr" style="text-align:justify"><em><span style="color:#000000">M&ugrave;a h&egrave; n&oacute;ng bức đ&atilde; tới, c&aacute;c chị em chắc chắn ai cũng muốn diện những bộ bikini, &aacute;o trễ vai, đầm v&aacute;y ngắn. Nhưng lại sợ bị đen da, ch&aacute;y nắng. B&agrave;i viết n&agrave;y sẽ chia sẻ tới c&aacute;c chị em &nbsp;c&aacute;ch dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">M&ugrave;a h&egrave; l&agrave; m&ugrave;a m&agrave; c&aacute;c chị em sẽ diện bikini đi tắm biển, diện &aacute;o trễ vai, đầm v&aacute;y ngắn đi dạo phố để xua tan đi c&aacute;i n&oacute;ng oi bức của m&ugrave;a h&egrave;. Chắc hẳn c&aacute;c chị em ai cũng muốn sở hữu l&agrave;n da trắng s&aacute;ng, săn chắc để diện những trang phục m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch. B&agrave;i viết n&agrave;y sẽ chia sẻ với c&aacute;c chị em c&aacute;c c&aacute;ch </span><a href="http://myichy.vn/u-face-saffon-cherry-nhat-ban"><span style="color:#2980b9"><strong>dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;</strong></span></a><span style="color:#000000">.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; với nha đam (l&ocirc; hội)</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nha đam c&oacute; chứa nhiều chất rất tốt cho da như Canxi, Kẽm, Natri&hellip;C&aacute;ch dưỡng trắng da từ nha đam hết sức đơn giản. C&aacute;c chị em chỉ cần lấy từ 2-3 l&aacute; nha đam, đem b&oacute;c vỏ rửa sạch với muối, sau đ&oacute; đem &eacute;p lấy nước. D&ugrave;ng dung dịch n&agrave;y thoa đều to&agrave;n th&acirc;n khoảng 15 ph&uacute;t th&igrave; tắm lại bằng nước sạch. Để đạt hiệu quả với phương ph&aacute;p n&agrave;y cần phải thực hiện ki&ecirc;n tr&igrave; trong một thời gian d&agrave;i.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="397" src="/image/images/duong-trang-da-toan-than-mua-he%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">L&aacute; nha đam c&oacute; t&aacute;c dụng l&agrave;m trắng da</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">C&aacute;ch dưỡng trắng da với sữa chua v&agrave; mật ong</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sữa chua l&agrave; một thực phẩm ch&uacute;ng ta ăn h&agrave;ng ng&agrave;y rất tốt cho hệ ti&ecirc;u h&oacute;a. Ngo&agrave;i tốt cho đường ruột th&igrave; sữa chua cũng rất tốt cho l&agrave;n da, do trong sữa chua chứa rất nhiều vitamin tốt cho da như Vitamin A, D, E&hellip;Đặc biệt trong sữa chua c&ograve;n chứa axit Lactic l&agrave; chất l&agrave;m sạch da tự nhi&ecirc;n, cho c&aacute;c chị em một l&agrave;n da trắng s&aacute;ng, mịn m&agrave;ng. C&aacute;c chị em chỉ cần trộn hỗn hợp sữa chua v&agrave; mật ong sau đ&oacute; thoa l&ecirc;n da. Ch&uacute; &yacute; loại sữa chua l&agrave; sữa chua kh&ocirc;ng đường nh&eacute;!</span></p>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="450" src="/image/images/duong-trang-da-toan-than-mua-he%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Hỗn hợp l&agrave;m trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; từ sữa chua v&agrave; mật ong</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Dưỡng trắng to&agrave;n th&acirc;n m&ugrave;a h&egrave; với Tắm trắng Bạch Hoa Tuyết</span></strong></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để c&oacute; thể dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; bằng c&aacute;c phương ph&aacute;p tự nhi&ecirc;n th&igrave; c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i, c&aacute;ch l&agrave;m tuy đơn giản nhưng kh&aacute; bất tiện khi phải thoa l&ecirc;n to&agrave;n th&acirc;n v&agrave; kh&aacute; tốn k&eacute;m. C&aacute;c chị em hẳn muốn c&oacute; một c&aacute;ch thực hiện đơn giản, hiệu quả, tiện lợi, tiết kiệm v&agrave; nhanh ch&oacute;ng cho hiệu quả. Hiểu được t&acirc;m l&yacute; của c&aacute;c chị em, C&ocirc;ng ty dược mỹ phẩm cao cấp My-ichy đ&atilde; cho ra đời sản phẩm Tắm trắng Bạch Hoa Tuyết.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết với những th&agrave;nh phần rất tốt cho da như cabomer, chiết xuất hoa hồng, nước tinh khiết, bột ngọc trai, tinh chất Lavender, alphabutin, Grycerin,&hellip;c&oacute; c&ocirc;ng dụng đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da trắng hồng, săn chắc, c&ugrave;ng với hương thơm nồng n&agrave;n từ Lavender sẽ tăng th&ecirc;m sự l&ocirc;i cuốn cho chị em</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng của Kem tắm trắng Bạch Hoa Tuyết </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bộ sản phẩm gồm 3 g&oacute;i: g&oacute;i 1-dung dịch tắm trắng hoa hồng; g&oacute;i 2-Bột ngọc trai; g&oacute;i 3-serum Lavender</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 1: Trộn g&oacute;i 1 v&agrave; g&oacute;i 2 sau đ&oacute; thoa hỗn hợp l&ecirc;n da, massager cho da khoảng 15 ph&uacute;t th&igrave; rửa sạch bằng nước rồi lau kh&ocirc;</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 2: Thoa serum Lavender.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="450" src="/image/images/duong-trang-da-toan-than-mua-he%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm c&aacute;c bạn h&atilde;y truy cập v&agrave;o website: </span><a href="http://myichy.vn/"><span style="color:#3498db"><strong>http://myichy.vn</strong></span></a><span style="color:#000000"> </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch gi&uacute;p chị em</span><a href="http://myichy.vn/cach-duong-trang-da-toan-than-mua-he"><span style="color:#2980b9"><strong> dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;</strong></span></a><span style="color:#000000">. C&aacute;c chị em h&atilde;y &aacute;p dụng v&agrave; chia sẻ với bạn b&egrave; ngay th&ocirc;i n&agrave;o. Ch&uacute;c chị em c&oacute; một l&agrave;n da như &yacute;!</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', 0, 'cach-duong-trang-da-toan-than-mua-he', 'Cách dưỡng trắng da toàn thân mùa hè', 'Mùa hè nóng bức đã tới, các chị em chắc chắn ai cũng muốn diện những bộ bikini, áo trễ vai, đầm váy ngắn. Nhưng lại sợ bị đen da, cháy nắng. Bài viết này sẽ chia sẻ tới các chị em  cách dưỡng trắng da toàn thân mùa hè.', 'dưỡng trắng da toàn thân mùa hè'),
(118, 59, 'en', 'english version Cách dưỡng trắng da toàn thân mùa hè', 'english version Mùa hè nóng bức đã tới, các chị em chắc chắn ai cũng muốn diện những bộ bikini, áo trễ vai, đầm váy ngắn. Nhưng lại sợ bị đen da, cháy nắng. Bài viết này sẽ chia sẻ tới các chị em  cách dưỡng trắng da toàn thân mùa hè.', 'english version <p dir="ltr" style="text-align:justify"><em><span style="color:#000000">M&ugrave;a h&egrave; n&oacute;ng bức đ&atilde; tới, c&aacute;c chị em chắc chắn ai cũng muốn diện những bộ bikini, &aacute;o trễ vai, đầm v&aacute;y ngắn. Nhưng lại sợ bị đen da, ch&aacute;y nắng. B&agrave;i viết n&agrave;y sẽ chia sẻ tới c&aacute;c chị em &nbsp;c&aacute;ch dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;.</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">M&ugrave;a h&egrave; l&agrave; m&ugrave;a m&agrave; c&aacute;c chị em sẽ diện bikini đi tắm biển, diện &aacute;o trễ vai, đầm v&aacute;y ngắn đi dạo phố để xua tan đi c&aacute;i n&oacute;ng oi bức của m&ugrave;a h&egrave;. Chắc hẳn c&aacute;c chị em ai cũng muốn sở hữu l&agrave;n da trắng s&aacute;ng, săn chắc để diện những trang phục m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch. B&agrave;i viết n&agrave;y sẽ chia sẻ với c&aacute;c chị em c&aacute;c c&aacute;ch </span><a href="http://myichy.vn/u-face-saffon-cherry-nhat-ban"><span style="color:#2980b9"><strong>dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;</strong></span></a><span style="color:#000000">.</span></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; với nha đam (l&ocirc; hội)</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Nha đam c&oacute; chứa nhiều chất rất tốt cho da như Canxi, Kẽm, Natri&hellip;C&aacute;ch dưỡng trắng da từ nha đam hết sức đơn giản. C&aacute;c chị em chỉ cần lấy từ 2-3 l&aacute; nha đam, đem b&oacute;c vỏ rửa sạch với muối, sau đ&oacute; đem &eacute;p lấy nước. D&ugrave;ng dung dịch n&agrave;y thoa đều to&agrave;n th&acirc;n khoảng 15 ph&uacute;t th&igrave; tắm lại bằng nước sạch. Để đạt hiệu quả với phương ph&aacute;p n&agrave;y cần phải thực hiện ki&ecirc;n tr&igrave; trong một thời gian d&agrave;i.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="397" src="/image/images/duong-trang-da-toan-than-mua-he%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">L&aacute; nha đam c&oacute; t&aacute;c dụng l&agrave;m trắng da</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">C&aacute;ch dưỡng trắng da với sữa chua v&agrave; mật ong</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sữa chua l&agrave; một thực phẩm ch&uacute;ng ta ăn h&agrave;ng ng&agrave;y rất tốt cho hệ ti&ecirc;u h&oacute;a. Ngo&agrave;i tốt cho đường ruột th&igrave; sữa chua cũng rất tốt cho l&agrave;n da, do trong sữa chua chứa rất nhiều vitamin tốt cho da như Vitamin A, D, E&hellip;Đặc biệt trong sữa chua c&ograve;n chứa axit Lactic l&agrave; chất l&agrave;m sạch da tự nhi&ecirc;n, cho c&aacute;c chị em một l&agrave;n da trắng s&aacute;ng, mịn m&agrave;ng. C&aacute;c chị em chỉ cần trộn hỗn hợp sữa chua v&agrave; mật ong sau đ&oacute; thoa l&ecirc;n da. Ch&uacute; &yacute; loại sữa chua l&agrave; sữa chua kh&ocirc;ng đường nh&eacute;!</span></p>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="450" src="/image/images/duong-trang-da-toan-than-mua-he%20(3).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Hỗn hợp l&agrave;m trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; từ sữa chua v&agrave; mật ong</span></em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Dưỡng trắng to&agrave;n th&acirc;n m&ugrave;a h&egrave; với Tắm trắng Bạch Hoa Tuyết</span></strong></span></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để c&oacute; thể dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave; bằng c&aacute;c phương ph&aacute;p tự nhi&ecirc;n th&igrave; c&aacute;c chị em phải ki&ecirc;n nhẫn thực hiện trong một thời gian d&agrave;i, c&aacute;ch l&agrave;m tuy đơn giản nhưng kh&aacute; bất tiện khi phải thoa l&ecirc;n to&agrave;n th&acirc;n v&agrave; kh&aacute; tốn k&eacute;m. C&aacute;c chị em hẳn muốn c&oacute; một c&aacute;ch thực hiện đơn giản, hiệu quả, tiện lợi, tiết kiệm v&agrave; nhanh ch&oacute;ng cho hiệu quả. Hiểu được t&acirc;m l&yacute; của c&aacute;c chị em, C&ocirc;ng ty dược mỹ phẩm cao cấp My-ichy đ&atilde; cho ra đời sản phẩm Tắm trắng Bạch Hoa Tuyết.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết với những th&agrave;nh phần rất tốt cho da như cabomer, chiết xuất hoa hồng, nước tinh khiết, bột ngọc trai, tinh chất Lavender, alphabutin, Grycerin,&hellip;c&oacute; c&ocirc;ng dụng đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời, mang lại cho chị em một l&agrave;n da trắng hồng, săn chắc, c&ugrave;ng với hương thơm nồng n&agrave;n từ Lavender sẽ tăng th&ecirc;m sự l&ocirc;i cuốn cho chị em</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng của Kem tắm trắng Bạch Hoa Tuyết </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bộ sản phẩm gồm 3 g&oacute;i: g&oacute;i 1-dung dịch tắm trắng hoa hồng; g&oacute;i 2-Bột ngọc trai; g&oacute;i 3-serum Lavender</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 1: Trộn g&oacute;i 1 v&agrave; g&oacute;i 2 sau đ&oacute; thoa hỗn hợp l&ecirc;n da, massager cho da khoảng 15 ph&uacute;t th&igrave; rửa sạch bằng nước rồi lau kh&ocirc;</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Bước 2: Thoa serum Lavender.</span></p>\r\n\r\n<p style="text-align:center"><img alt="duong-trang-da-toan-than-mua-he" height="450" src="/image/images/duong-trang-da-toan-than-mua-he%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Tắm trắng Bạch Hoa Tuyết</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm c&aacute;c bạn h&atilde;y truy cập v&agrave;o website: </span><a href="http://myichy.vn/"><span style="color:#3498db"><strong>http://myichy.vn</strong></span></a><span style="color:#000000"> </span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch gi&uacute;p chị em dưỡng trắng da to&agrave;n th&acirc;n m&ugrave;a h&egrave;. C&aacute;c chị em h&atilde;y &aacute;p dụng v&agrave; chia sẻ với bạn b&egrave; ngay th&ocirc;i n&agrave;o. Ch&uacute;c chị em c&oacute; một l&agrave;n da như &yacute;!</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '', '', '', '', '', 0, 'en-cach-duong-trang-da-toan-than-mua-he', 'Cách dưỡng trắng da toàn thân mùa hè', 'Mùa hè nóng bức đã tới, các chị em chắc chắn ai cũng muốn diện những bộ bikini, áo trễ vai, đầm váy ngắn. Nhưng lại sợ bị đen da, cháy nắng. Bài viết này sẽ chia sẻ tới các chị em  cách dưỡng trắng da toàn thân mùa hè.', 'dưỡng trắng da toàn thân mùa hè');
INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(119, 60, 'vn', 'Cách chăm sóc và điều trị da mụn', 'Chăm sóc và điều trị da mụn hiệu quả, không để lại tác dụng phụ, xóa bỏ vết thâm lấy lại làn da sạch mụn, trắng sáng', '<p dir="ltr" style="text-align:justify"><em><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn hiệu quả, kh&ocirc;ng để lại t&aacute;c dụng phụ, x&oacute;a bỏ vết th&acirc;m lấy lại l&agrave;n da sạch mụn, trắng s&aacute;ng</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c chị em gh&eacute;t nhất l&agrave; thấy mụn tr&ecirc;n khu&ocirc;n mặt m&igrave;nh, việc n&agrave;y l&agrave;m cho c&aacute;c chị em cảm thấy mất tự tin khi đối diện với mọi người. Khi bị mụn da rất nhạy cảm, nếu bạn bị mụn m&agrave; kh&ocirc;ng biết c&aacute;ch n&agrave;o để chăm s&oacute;c đ&uacute;ng nhất cho da, việc n&agrave;y l&agrave;m cho da bị vi&ecirc;m nhiễm l&agrave;m cho mụn c&agrave;ng trở l&ecirc;n nghi&ecirc;m trọng hơn, mụn ng&agrave;y mọc nhiều hơn. Ch&uacute;ng t&ocirc;i sẽ chia sẻ cho bạn những mẹo nhỏ để</span><a href="http://myichy.vn/sp-face-dao-tien-su"><span style="color:#2980b9"><strong> chăm s&oacute;c v&agrave; điều trị da mụn </strong></span></a><span style="color:#000000">v&ocirc; c&ugrave;ng đơn giản, hiệu quả v&agrave; tiện lợi.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">Nguy&ecirc;n nh&acirc;n dẫn đến da mặt bị mụn.</span></strong></span></h2>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do yếu tố m&ocirc;i trường bị nhiễm bẩn, bụi, vi khuẩn&hellip; x&acirc;m nhập g&acirc;y hại cho da</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do chăm s&oacute;c da mặt kh&ocirc;ng đ&uacute;ng c&aacute;ch, kh&ocirc;ng vệ sinh thường xuy&ecirc;n cho da</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do nội tiết tố thay đổi, do dậy th&igrave;, mang thai&hellip;</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do việc thực hiện chế độ ăn uống kh&ocirc;ng đồng đều, thường d&ugrave;ng c&aacute;c thực phẩm c&oacute; t&iacute;nh n&oacute;ng</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do lạm dụng c&aacute;c loại mỹ phẩm k&iacute;ch ứng da&hellip;</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-va-dieu-tri-da-mun" height="428" src="/image/images/cham-soc-va-dieu-tri-da-mun%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Lo lắng khi mặt nổi nhiều mụn</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong><span style="color:#000000">C&aacute;ch chăm s&oacute;c v&agrave; điều trị da mụn</span></strong></span></h2>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Vệ sinh mặt thường xuy&ecirc;n: bạn n&ecirc;n thực hiện c&aacute;ch n&agrave;y khoảng 2 lần/ng&agrave;y. Đối với những người tiếp x&uacute;c với m&ocirc;i trường bẩn th&igrave; thực hiện nhiều hơn</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Hạn chế trang điểm, sau khi trang điểm n&ecirc;n thực hiện việc tẩy trang vệ sinh da mặt ngay. Ch&uacute; &yacute; sử dụng c&aacute;c loại sản phẩm an to&agrave;n, c&oacute; xuất xứ r&otilde; r&agrave;ng.</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Chế độ dinh dưỡng hợp l&yacute;: Bổ sung c&aacute;c thực phẩm tốt cho da như: c&agrave; rốt, cam, qu&yacute;t, bắp cải&hellip;Bổ sung c&aacute;c vitamin: Vitamin A, E, B12&hellip;</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Giữ trạng th&aacute;i t&acirc;m l&yacute; thoải m&aacute;i, vui vẻ</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sử dụng c&aacute;c loại kem v&agrave; mặt nạ trị mụn</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn đ&uacute;ng c&aacute;ch v&agrave; hợp l&yacute; sẽ ngăn chặn t&igrave;nh trạng l&acirc;y lan của mụn, loại bỏ mụn v&agrave; c&aacute;c biến chứng của mụn tr&ecirc;n khu&ocirc;n mặt.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn bằng Kem mụn Acne Clearing Cream</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng ty sản xuất: C&ocirc;ng ty dược mỹ phẩm My-ichy</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Th&agrave;nh phần: Vitamin E, Vitamin B5, Glycerin, Lactic Acid, rượu,&hellip;</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng &nbsp;dụng: Ngăn ngừa mụn, x&oacute;a bỏ vết th&acirc;m, kh&aacute;ng vi&ecirc;m, ngừa rỗ, t&agrave;n nhang l&agrave;m s&aacute;ng da, t&aacute;i tạo l&agrave;n da bị hư tổn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng: L&agrave;m sạch da với nước sạch rồi thoa một lớp kem mỏng l&ecirc;n l&agrave;n da bị mụn rồi massage nhẹ nh&agrave;ng cho kem thấm v&agrave;o da</span></p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-va-dieu-tri-da-mun" height="450" src="/image/images/cham-soc-va-dieu-tri-da-mun%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Kem mụn Acen Clearing Cream</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; một số kinh nghiệm<strong> chăm s&oacute;c v&agrave; điều trị da mụn</strong>. Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm Kem mụn Acen Clearing Cream v&agrave; c&aacute;c sản phẩm chăm s&oacute;c da cao cấp kh&aacute;c của C&ocirc;ng ty dược mỹ phẩm My-ichy mời c&aacute;c bạn truy cập v&agrave;o website: </span><strong><a href="http://myichy.vn/"><span style="color:#2980b9">http://myichy.vn</span></a></strong><span style="color:#000000"> </span></p>\r\n', '', '', '', '', '', 0, 'cach-cham-soc-va-dieu-tri-da-mun', 'Cách chăm sóc và điều trị da mụn', 'Chăm sóc và điều trị da mụn hiệu quả, không để lại tác dụng phụ, xóa bỏ vết thâm lấy lại làn da sạch mụn, trắng sáng', 'chăm sóc và điều trị da mụn myichy'),
(120, 60, 'en', 'english version Cách chăm sóc và điều trị da mụn', 'english version Chăm sóc và điều trị da mụn hiệu quả, không để lại tác dụng phụ, xóa bỏ vết thâm lấy lại làn da sạch mụn, trắng sáng', 'english version <p dir="ltr" style="text-align:justify"><em><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn hiệu quả, kh&ocirc;ng để lại t&aacute;c dụng phụ, x&oacute;a bỏ vết th&acirc;m lấy lại l&agrave;n da sạch mụn, trắng s&aacute;ng</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;c chị em gh&eacute;t nhất l&agrave; thấy mụn tr&ecirc;n khu&ocirc;n mặt m&igrave;nh, việc n&agrave;y l&agrave;m cho c&aacute;c chị em cảm thấy mất tự tin khi đối diện với mọi người. Khi bị mụn da rất nhạy cảm, nếu bạn bị mụn m&agrave; kh&ocirc;ng biết c&aacute;ch n&agrave;o để chăm s&oacute;c đ&uacute;ng nhất cho da, việc n&agrave;y l&agrave;m cho da bị vi&ecirc;m nhiễm l&agrave;m cho mụn c&agrave;ng trở l&ecirc;n nghi&ecirc;m trọng hơn, mụn ng&agrave;y mọc nhiều hơn. Ch&uacute;ng t&ocirc;i sẽ chia sẻ cho bạn những mẹo nhỏ để</span><a href="http://myichy.vn/sp-face-dao-tien-su"><span style="color:#2980b9"><strong> chăm s&oacute;c v&agrave; điều trị da mụn </strong></span></a><span style="color:#000000">v&ocirc; c&ugrave;ng đơn giản, hiệu quả v&agrave; tiện lợi.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">Nguy&ecirc;n nh&acirc;n dẫn đến da mặt bị mụn.</span></strong></h2>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do yếu tố m&ocirc;i trường bị nhiễm bẩn, bụi, vi khuẩn&hellip; x&acirc;m nhập g&acirc;y hại cho da</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do chăm s&oacute;c da mặt kh&ocirc;ng đ&uacute;ng c&aacute;ch, kh&ocirc;ng vệ sinh thường xuy&ecirc;n cho da</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do nội tiết tố thay đổi, do dậy th&igrave;, mang thai&hellip;</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do việc thực hiện chế độ ăn uống kh&ocirc;ng đồng đều, thường d&ugrave;ng c&aacute;c thực phẩm c&oacute; t&iacute;nh n&oacute;ng</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Do lạm dụng c&aacute;c loại mỹ phẩm k&iacute;ch ứng da&hellip;</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-va-dieu-tri-da-mun" height="428" src="/image/images/cham-soc-va-dieu-tri-da-mun%20(2).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Lo lắng khi mặt nổi nhiều mụn</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="color:#000000">C&aacute;ch chăm s&oacute;c v&agrave; điều trị da mụn</span></strong></h2>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Vệ sinh mặt thường xuy&ecirc;n: bạn n&ecirc;n thực hiện c&aacute;ch n&agrave;y khoảng 2 lần/ng&agrave;y. Đối với những người tiếp x&uacute;c với m&ocirc;i trường bẩn th&igrave; thực hiện nhiều hơn</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Hạn chế trang điểm, sau khi trang điểm n&ecirc;n thực hiện việc tẩy trang vệ sinh da mặt ngay. Ch&uacute; &yacute; sử dụng c&aacute;c loại sản phẩm an to&agrave;n, c&oacute; xuất xứ r&otilde; r&agrave;ng.</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Chế độ dinh dưỡng hợp l&yacute;: Bổ sung c&aacute;c thực phẩm tốt cho da như: c&agrave; rốt, cam, qu&yacute;t, bắp cải&hellip;Bổ sung c&aacute;c vitamin: Vitamin A, E, B12&hellip;</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Giữ trạng th&aacute;i t&acirc;m l&yacute; thoải m&aacute;i, vui vẻ</span></p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify"><span style="color:#000000">Sử dụng c&aacute;c loại kem v&agrave; mặt nạ trị mụn</span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn đ&uacute;ng c&aacute;ch v&agrave; hợp l&yacute; sẽ ngăn chặn t&igrave;nh trạng l&acirc;y lan của mụn, loại bỏ mụn v&agrave; c&aacute;c biến chứng của mụn tr&ecirc;n khu&ocirc;n mặt.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify">&nbsp;</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong><span style="font-size:20px"><span style="color:#000000">Chăm s&oacute;c v&agrave; điều trị da mụn bằng Kem mụn Acne Clearing Cream</span></span></strong></h2>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng ty sản xuất: C&ocirc;ng ty dược mỹ phẩm My-ichy</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Th&agrave;nh phần: Vitamin E, Vitamin B5, Glycerin, Lactic Acid, rượu,&hellip;</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&ocirc;ng &nbsp;dụng: Ngăn ngừa mụn, x&oacute;a bỏ vết th&acirc;m, kh&aacute;ng vi&ecirc;m, ngừa rỗ, t&agrave;n nhang l&agrave;m s&aacute;ng da, t&aacute;i tạo l&agrave;n da bị hư tổn.</span></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">C&aacute;ch sử dụng: L&agrave;m sạch da với nước sạch rồi thoa một lớp kem mỏng l&ecirc;n l&agrave;n da bị mụn rồi massage nhẹ nh&agrave;ng cho kem thấm v&agrave;o da</span></p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-va-dieu-tri-da-mun" height="450" src="/image/images/cham-soc-va-dieu-tri-da-mun%20(1).jpg" width="600" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em><span style="color:#000000">Kem mụn Acen Clearing Cream</span></em></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><span style="color:#000000">Tr&ecirc;n đ&acirc;y l&agrave; một số kinh nghiệm<strong> chăm s&oacute;c v&agrave; điều trị da mụn</strong>. Để biết th&ecirc;m th&ocirc;ng tin về sản phẩm Kem mụn Acen Clearing Cream v&agrave; c&aacute;c sản phẩm chăm s&oacute;c da cao cấp kh&aacute;c của C&ocirc;ng ty dược mỹ phẩm My-ichy mời c&aacute;c bạn truy cập v&agrave;o website: </span><strong><a href="http://myichy.vn/"><span style="color:#2980b9">http://myichy.vn</span></a></strong><span style="color:#000000"> </span></p>\r\n', '', '', '', '', '', 0, 'en-cach-cham-soc-va-dieu-tri-da-mun', 'Cách chăm sóc và điều trị da mụn', 'Chăm sóc và điều trị da mụn hiệu quả, không để lại tác dụng phụ, xóa bỏ vết thâm lấy lại làn da sạch mụn, trắng sáng', 'chăm sóc và điều trị da mụn '),
(121, 61, 'vn', 'Bí quyết chăm sóc da mặt có lỗ chân lông to', 'Lỗ chân lông to là tình trạng mà nhiều bạn thường gặp phải do nhiều nguyên nhân khác nhau. Vậy làm sao để chăm sóc da mặt có lỗ chân lông to hiệu quả nhất?', '<p dir="ltr" style="text-align:justify">Theo nhiều nghi&ecirc;n cứu, những người c&oacute; l&agrave;n da tối m&agrave;u v&agrave; c&oacute; nhiều mụn đầu đen sẽ dễ gặp t&igrave;nh trạng lỗ ch&acirc;n l&ocirc;ng to hơn. Lỗ ch&acirc;n l&ocirc;ng to g&acirc;y tiết nhiều b&atilde; nhờn khiến da mặt của bạn trở n&ecirc;n k&eacute;m xinh hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Trong b&agrave;i viết dưới đ&acirc;y, h&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i t&igrave;m hiểu những b&iacute; quyết <strong>chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to </strong>để bạn đọc tham khảo v&agrave; &aacute;p dụng với l&agrave;n da của m&igrave;nh. Đồng thời, ch&uacute;ng t&ocirc;i sẽ đề xuất một sản phẩm chăm s&oacute;c da mặt tốt v&agrave; hiệu quả h&agrave;ng đầu tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-co-lo-chan-long-to" height="523" src="/image/images/cham-soc-da-mat-co-lo-chan-long-to%20(2).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Đ&aacute; lạnh gi&uacute;p da mặt của bạn được se kh&iacute;t hiệu quả hơn</em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong>B&iacute; quyết chăm s&oacute;c da mặt với lỗ ch&acirc;n l&ocirc;ng to cho bạn g&aacute;i:</strong></span></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>Chườm đ&aacute; lạnh &ndash; gi&uacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả:</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Nếu bạn muốn lỗ ch&acirc;n l&ocirc;ng của m&igrave;nh thu nhỏ lại trong khoảng thời gian nhanh ch&oacute;ng, bạn n&ecirc;n sử dụng đ&aacute; lạnh. Chỉ cần d&ugrave;ng v&agrave;i vi&ecirc;n đ&aacute; lạnh được bọc trong một chiếc khăn d&agrave;y v&agrave; chườm l&ecirc;n mặt, bạn c&oacute; thể l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng của m&igrave;nh trong v&agrave;i ph&uacute;t.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Tuy nhi&ecirc;n, lỗ ch&acirc;n l&ocirc;ng khi sử dụng c&aacute;ch n&agrave;y chỉ c&oacute; thể se kh&iacute;t trong một khoảng thời gian ngắn v&agrave; kh&ocirc;ng c&oacute; hiệu quả l&acirc;u d&agrave;i.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>Sử dụng chanh se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng:</strong></span></h3>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-co-lo-chan-long-to" height="500" src="/image/images/cham-soc-da-mat-co-lo-chan-long-to%20(1).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Chanh c&oacute; thể gi&uacute;p bạn c&oacute; l&agrave;n da mịn m&agrave;ng kh&ocirc;ng t&igrave; vết</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">Chanh l&agrave; một trong những phương ph&aacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả nhất hiện nay. D&ugrave; vậy, Axit citric c&oacute; b&ecirc;n trong chanh c&oacute; thể khiến cho l&agrave;n da của bạn g&aacute;i gặp t&igrave;nh trạng ứng đỏ, đổi m&agrave;u da hoặc l&agrave;m lộ khuyết điểm kh&aacute;c tr&ecirc;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Để l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng bằng chanh, bạn c&oacute; thể sử dụng c&aacute;c c&ocirc;ng thức như sau:</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Trộn chanh c&ugrave;ng nước &eacute;p c&agrave; chua với tỷ lệ l&agrave; 1:1 sau đ&oacute; d&ugrave;ng b&ocirc;ng thoa đều hỗn hợp l&ecirc;n da, sau 10 ph&uacute;t, bạn rửa sạch hỗn hợp n&agrave;y với nước.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Sử dụng vỏ chanh c&ugrave;ng với đường, thoa đều l&ecirc;n da. Giữ trong 10 ph&uacute;t sau đ&oacute; rửa mặt sạch với nước.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify">2 c&aacute;ch tr&ecirc;n đều c&oacute; hiệu quả gi&uacute;p lỗ ch&acirc;n l&ocirc;ng của bạn nhỏ v&agrave; l&agrave;n da giảm dầu nhờn hơn.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>Sử dụng c&aacute;c mỹ phẩm chăm s&oacute;c da chuy&ecirc;n biệt:</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Đối với những người gặp t&igrave;nh trạng da nhờn, lỗ ch&acirc;n l&ocirc;ng to, n&ecirc;n sử dụng c&aacute;c loại mỹ phẩm, c&aacute;c sản phẩm chăm s&oacute;c da chuy&ecirc;n biệt v&agrave; thật cẩn thận trong khi lựa chọn. Bởi nếu như sử dụng kh&ocirc;ng đ&uacute;ng sản phẩm, l&agrave;n da của bạn g&aacute;i c&oacute; thể gặp nhiều t&igrave;nh trạng nghi&ecirc;m trọng hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Với những tư vấn tr&ecirc;n đ&acirc;y, hy vọng bạn g&aacute;i c&oacute; thể <strong>chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to</strong> tốt nhất. Nếu bạn g&aacute;i c&oacute; nhu cầu muốn t&igrave;m những sản phẩm chăm s&oacute;c da tốt, chất lượng h&agrave;ng đầu, đừng qu&ecirc;n tham khảo ngay sản phẩm<a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#2980b9"><strong> kem ủ face saffron cherry </strong></span></a>Nhật Bản tại My-ichy. &nbsp;Sản phẩm cung cấp dưỡng chất cần thiết nu&ocirc;i dưỡng cơ thể, l&agrave;m sạch da, lấy đi lớp tế b&agrave;o chết v&agrave; thu gọn lỗ ch&acirc;n l&ocirc;ng</p>\r\n\r\n<p dir="ltr" style="text-align:justify">H&atilde;y li&ecirc;n hệ ngay với <a href="http://myichy.vn/"><span style="color:#2980b9"><strong>myichy.vn </strong></span></a>để nhận được sự tư vấn kỹ c&agrave;ng v&agrave; chi tiết hơn về sản phẩm.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Xem th&ecirc;m:&nbsp;<strong><a href="http://myichy.vn/cach-cham-soc-va-dieu-tri-da-mun">C&aacute;ch chăm s&oacute;c v&agrave; điều trị da mụn</a></strong></p>\r\n', '', '', '', '', '', 0, 'bi-quyet-cham-soc-da-mat-co-lo-chan-long-to', 'Bí quyết chăm sóc da mặt có lỗ chân lông to', 'Lỗ chân lông to là tình trạng mà nhiều bạn thường gặp phải do nhiều nguyên nhân khác nhau. Vậy làm sao để chăm sóc da mặt có lỗ chân lông to hiệu quả nhất?', 'chăm sóc da mặt có lỗ chân lông to'),
(122, 61, 'en', 'english version Bí quyết chăm sóc da mặt có lỗ chân lông to', 'english version Lỗ chân lông to là tình trạng mà nhiều bạn thường gặp phải do nhiều nguyên nhân khác nhau. Vậy làm sao để chăm sóc da mặt có lỗ chân lông to hiệu quả nhất?', 'english version <p dir="ltr" style="text-align:justify">Theo nhiều nghi&ecirc;n cứu, những người c&oacute; l&agrave;n da tối m&agrave;u v&agrave; c&oacute; nhiều mụn đầu đen sẽ dễ gặp t&igrave;nh trạng lỗ ch&acirc;n l&ocirc;ng to hơn. Lỗ ch&acirc;n l&ocirc;ng to g&acirc;y tiết nhiều b&atilde; nhờn khiến da mặt của bạn trở n&ecirc;n k&eacute;m xinh hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Trong b&agrave;i viết dưới đ&acirc;y, h&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i t&igrave;m hiểu những b&iacute; quyết <strong>chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to </strong>để bạn đọc tham khảo v&agrave; &aacute;p dụng với l&agrave;n da của m&igrave;nh. Đồng thời, ch&uacute;ng t&ocirc;i sẽ đề xuất một sản phẩm chăm s&oacute;c da mặt tốt v&agrave; hiệu quả h&agrave;ng đầu tr&ecirc;n thị trường hiện nay.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-co-lo-chan-long-to" height="523" src="/image/images/cham-soc-da-mat-co-lo-chan-long-to%20(2).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Đ&aacute; lạnh gi&uacute;p da mặt của bạn được se kh&iacute;t hiệu quả hơn</em></p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong>B&iacute; quyết chăm s&oacute;c da mặt với lỗ ch&acirc;n l&ocirc;ng to cho bạn g&aacute;i:</strong></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Chườm đ&aacute; lạnh &ndash; gi&uacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Nếu bạn muốn lỗ ch&acirc;n l&ocirc;ng của m&igrave;nh thu nhỏ lại trong khoảng thời gian nhanh ch&oacute;ng, bạn n&ecirc;n sử dụng đ&aacute; lạnh. Chỉ cần d&ugrave;ng v&agrave;i vi&ecirc;n đ&aacute; lạnh được bọc trong một chiếc khăn d&agrave;y v&agrave; chườm l&ecirc;n mặt, bạn c&oacute; thể l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng của m&igrave;nh trong v&agrave;i ph&uacute;t.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Tuy nhi&ecirc;n, lỗ ch&acirc;n l&ocirc;ng khi sử dụng c&aacute;ch n&agrave;y chỉ c&oacute; thể se kh&iacute;t trong một khoảng thời gian ngắn v&agrave; kh&ocirc;ng c&oacute; hiệu quả l&acirc;u d&agrave;i.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Sử dụng chanh se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng:</strong></h3>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-co-lo-chan-long-to" height="500" src="/image/images/cham-soc-da-mat-co-lo-chan-long-to%20(1).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Chanh c&oacute; thể gi&uacute;p bạn c&oacute; l&agrave;n da mịn m&agrave;ng kh&ocirc;ng t&igrave; vết</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">Chanh l&agrave; một trong những phương ph&aacute;p se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng hiệu quả nhất hiện nay. D&ugrave; vậy, Axit citric c&oacute; b&ecirc;n trong chanh c&oacute; thể khiến cho l&agrave;n da của bạn g&aacute;i gặp t&igrave;nh trạng ứng đỏ, đổi m&agrave;u da hoặc l&agrave;m lộ khuyết điểm kh&aacute;c tr&ecirc;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Để l&agrave;m se kh&iacute;t lỗ ch&acirc;n l&ocirc;ng bằng chanh, bạn c&oacute; thể sử dụng c&aacute;c c&ocirc;ng thức như sau:</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Trộn chanh c&ugrave;ng nước &eacute;p c&agrave; chua với tỷ lệ l&agrave; 1:1 sau đ&oacute; d&ugrave;ng b&ocirc;ng thoa đều hỗn hợp l&ecirc;n da, sau 10 ph&uacute;t, bạn rửa sạch hỗn hợp n&agrave;y với nước.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Sử dụng vỏ chanh c&ugrave;ng với đường, thoa đều l&ecirc;n da. Giữ trong 10 ph&uacute;t sau đ&oacute; rửa mặt sạch với nước.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify">2 c&aacute;ch tr&ecirc;n đều c&oacute; hiệu quả gi&uacute;p lỗ ch&acirc;n l&ocirc;ng của bạn nhỏ v&agrave; l&agrave;n da giảm dầu nhờn hơn.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Sử dụng c&aacute;c mỹ phẩm chăm s&oacute;c da chuy&ecirc;n biệt:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">Đối với những người gặp t&igrave;nh trạng da nhờn, lỗ ch&acirc;n l&ocirc;ng to, n&ecirc;n sử dụng c&aacute;c loại mỹ phẩm, c&aacute;c sản phẩm chăm s&oacute;c da chuy&ecirc;n biệt v&agrave; thật cẩn thận trong khi lựa chọn. Bởi nếu như sử dụng kh&ocirc;ng đ&uacute;ng sản phẩm, l&agrave;n da của bạn g&aacute;i c&oacute; thể gặp nhiều t&igrave;nh trạng nghi&ecirc;m trọng hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Với những tư vấn tr&ecirc;n đ&acirc;y, hy vọng bạn g&aacute;i c&oacute; thể <strong>chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to</strong> tốt nhất. Nếu bạn g&aacute;i c&oacute; nhu cầu muốn t&igrave;m những sản phẩm chăm s&oacute;c da tốt, chất lượng h&agrave;ng đầu, đừng qu&ecirc;n tham khảo ngay sản phẩm<a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><span style="color:#2980b9"><strong> kem ủ face saffron cherry </strong></span></a>Nhật Bản tại My-ichy. &nbsp;Sản phẩm cung cấp dưỡng chất cần thiết nu&ocirc;i dưỡng cơ thể, l&agrave;m sạch da, lấy đi lớp tế b&agrave;o chết v&agrave; thu gọn lỗ ch&acirc;n l&ocirc;ng</p>\r\n\r\n<p dir="ltr" style="text-align:justify">H&atilde;y li&ecirc;n hệ ngay với <a href="http://myichy.vn/"><span style="color:#2980b9"><strong>myichy.vn </strong></span></a>để nhận được sự tư vấn kỹ c&agrave;ng v&agrave; chi tiết hơn về sản phẩm.</p>\r\n', '', '', '', '', '', 0, 'en-bi-quyet-cham-soc-da-mat-co-lo-chan-long-to', 'Bí quyết chăm sóc da mặt có lỗ chân lông to', 'Lỗ chân lông to là tình trạng mà nhiều bạn thường gặp phải do nhiều nguyên nhân khác nhau. Vậy làm sao để chăm sóc da mặt có lỗ chân lông to hiệu quả nhất?', 'chăm sóc da mặt có lỗ chân lông to'),
(123, 62, 'vn', 'Cách chăm sóc da mặt cho nữ giới đúng cách nhất', 'Chăm sóc da mặt cho nữ luôn là vấn đề quan tâm hàng đầu của nữ giới hiện nay. Da mặt đẹp ở đâu bạn cũng tự tin tỏa sáng.', '<p dir="ltr" style="text-align:justify">Khi x&atilde; hội ng&agrave;y c&agrave;ng ph&aacute;t triển th&igrave; nhu cầu l&agrave;m đẹp của con người cũng bắt đầu được quan t&acirc;m cao hơn. Ph&aacute;i nữ c&agrave;ng quan trọng điều n&agrave;y v&agrave; lu&ocirc;n t&igrave;m phương ph&aacute;p chăm s&oacute;c da mặt cho nữ để chăm s&oacute;c cũng như bảo vệ da tốt nhất. Chắc chắn những phương ph&aacute;p m&agrave; ch&uacute;ng t&ocirc;i đưa ra dưới đ&acirc;y sẽ gi&uacute;p l&agrave;n da trở n&ecirc;n s&aacute;ng hơn, loại bỏ được những vết bẩn kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify">&nbsp;</h2>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><span style="font-size:20px"><strong>C&aacute;ch chăm s&oacute;c da mặt cho nữ giới</strong></span></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>L&agrave;m sạch da với sữa rửa mặt</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Với những bạn nữ giới th&igrave; việc chăm s&oacute;c da mặt rất l&agrave; quan trọng, bạn nữ giới phải hiểu về c&aacute;c sản phẩm để lựa chọn cho bản th&acirc;n m&igrave;nh những sản phẩm ph&ugrave; hợp với l&agrave;n da m&igrave;nh nhất, tăng hiệu quả v&agrave; tr&aacute;nh được những k&iacute;ch ứng cho l&agrave;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Bạn n&ecirc;n l&agrave;m sạch l&agrave;n da của m&igrave;nh bằng sữa rửa mặt l&agrave; bước đầu ti&ecirc;n trong qu&aacute; tr&igrave;nh chăm s&oacute;c da mặt, bạn n&ecirc;n sử dụng sữa rửa mặt 2 lần trong 1 ng&agrave;y cũng kh&ocirc;ng n&ecirc;n rửa qu&aacute; nhiều sẽ dẫn đến t&igrave;nh trạng da bị kh&ocirc;. V&igrave; trong da lu&ocirc;n chứa c&aacute;c tiết chất l&agrave;m ẩm tự nhi&ecirc;n n&ecirc;n việc sử dụng sữa rửa mặt 2 lần 1 ng&agrave;y sẽ gi&uacute;p lấy đi những tinh dầu tr&ecirc;n l&agrave;n da của bạn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Bạn n&ecirc;n ch&uacute; &yacute; khi rửa mặt chỉ n&ecirc;n d&ugrave;ng nước ấm hoặc nước m&aacute;t, cũng n&ecirc;n t&igrave;m c&aacute;c sản phẩm chứa c&aacute;c th&agrave;nh phần từ tự nhi&ecirc;n để c&oacute; thể sử dụng 1 c&aacute;ch hiệu quả nhất. Đ&acirc;y cũng l&agrave; c&aacute;ch chăm s&oacute;c da mặt cho nữ để mọi người n&ecirc;n biết.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>Tẩy da chết</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Việc tẩy da chết l&agrave; việc v&ocirc; c&ugrave;ng quan trọng n&ecirc;n bạn muốn sở hữu cho m&igrave;nh 1 l&agrave;n da trắng s&aacute;ng, mịn m&agrave;ng. Bạn n&ecirc;n tẩy da chết 1 tuần 2 lần để ph&aacute;t huy được hiệu quả mang lại l&agrave;n da s&aacute;ng b&oacute;ng cho nữ giới.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-cho-nu" height="533" src="/image/images/cham-soc-da-mat-cho-nu%20(2).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Sau khi rửa sạch mặt n&ecirc;n b&ocirc;i một lớp tẩy tế b&agrave;o chết l&ecirc;n tr&ecirc;n mặt</em></p>\r\n\r\n<p dir="ltr" style="text-align:center">&nbsp;</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Khi l&agrave;n da của bạn kh&ocirc;ng trắng s&aacute;ng v&agrave; kh&ocirc;ng c&oacute; mụn th&igrave; bạn sẽ c&oacute; 1 th&ecirc;m sự tự tin khi giao tiếp.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><span style="font-size:16px"><strong>Dưỡng ẩm hằng ng&agrave;y</strong></span></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Với những bạn nữ giới th&igrave; n&ecirc;n lựa chọn những sản phẩm dưỡng ẩm l&agrave;n da để c&oacute; thể chăm s&oacute;c l&agrave;n da ng&agrave;y c&agrave;ng tốt hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Chăm s&oacute;c da mặt cho nữ rất tốt khi bạn đ&atilde; sở hữu 1 serum dưỡng ẩm để c&oacute; thể tiết kiệm thời gian khi chăm s&oacute;c da cho nữ giới.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Ủ Face Saffron &amp; Cherry Nhật Bản</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Th&agrave;nh phần:</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Trong<a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><strong> kem Ủ Face Saffron &amp; Cherry Nhật Bản</strong></a> c&oacute; chứa những th&agrave;nh phần ch&iacute;nh như Purified water, Lactic acid, Glutathione, vitamin E, Allantoine, Vaseline, Saffron, Benzoyl peroxid, Triethanolamine, Glycerin, Aloe vera extract, Alcohol cetearyl, Triethanolamine, Sodium benzoate, Ethanol, Methyl paraben, PEG400.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-cho-nu" height="600" src="/image/images/cham-soc-da-mat-cho-nu%20(1).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:center"><em>Ủ Face Saffron &amp; Cherry Nhật Bản một sản phẩm rất tốt cho chăm s&oacute;c da mặt</em></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>C&ocirc;ng dụng:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Whitening Face Mask l&agrave; sản phẩm được chiết xuất từ c&aacute;c tinh chất cherry v&agrave; saffron v&agrave; kết hợp với vitamin để c&oacute; thể cung cấp c&aacute;c dưỡng chất cần thiết cho l&agrave;n da v&agrave; nu&ocirc;i dưỡng da v&agrave; gi&uacute;p lấy c&aacute;c tế b&agrave;o chết tr&ecirc;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Gi&uacute;p l&agrave;n da của bạn trở n&ecirc;n s&aacute;ng b&oacute;ng, mịn m&agrave;ng v&agrave; lu&ocirc;n giữ được sự tươi trẻ cho bản th&acirc;n, chống l&atilde;o h&oacute;a cho l&agrave;n da 1 c&aacute;ch tốt nhất.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify">&nbsp;</h3>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Hướng dẫn sử dụng:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- G&oacute;i 1 l&agrave; loại kem face mask với th&agrave;nh phần ch&iacute;nh l&agrave; hoa cherry.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- G&oacute;i 2 l&agrave; loại kem face mask với c&aacute;c th&agrave;nh phần ch&iacute;nh từ saffron.</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n rửa mặt bằng nước thật sạch v&agrave; sau đ&oacute; lau kh&ocirc;.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n lấy 1 lượng kem ở g&oacute;i 1 thoa đều l&ecirc;n l&agrave;n da mặt massage nhẹ nh&agrave;ng sau đ&oacute; ủ 15 ph&uacute;t Khi đ&atilde; ho&agrave;n th&agrave;nh th&igrave; bạn n&ecirc;n rửa sạch với nước v&agrave; lau kh&ocirc; da mặt.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n lấy 1 lượng kem ở g&oacute;i 2 thoa đều l&ecirc;n l&agrave;n da mặt massage nhẹ nh&agrave;ng sau đ&oacute; ủ trong 10 ph&uacute;t để c&oacute; được hiệu quả tốt nhất.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify">Đ&acirc;y l&agrave; c&aacute;ch <strong>chăm s&oacute;c da mặt cho nữ</strong> m&agrave; mọi người n&ecirc;n biết để c&oacute; thể nhanh ch&oacute;ng ph&aacute;t huy tối đa hiệu quả. Kh&ocirc;ng hề kh&oacute; để chăm s&oacute;c cho n&ecirc;n mọi người c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Xem th&ecirc;m:&nbsp;<a href="http://myichy.vn/bi-quyet-cham-soc-da-mat-co-lo-chan-long-to"><strong>B&iacute; quyết chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to</strong></a></p>\r\n', '', '', '', '', '', 0, 'cach-cham-soc-da-mat-cho-nu-gioi-dung-cach-nhat', 'Cách chăm sóc da mặt cho nữ giới đúng cách nhất', 'Chăm sóc da mặt cho nữ luôn là vấn đề quan tâm hàng đầu của nữ giới hiện nay. Da mặt đẹp ở đâu bạn cũng tự tin tỏa sáng.', ' chăm sóc da mặt cho nữ'),
(124, 62, 'en', 'english version Cách chăm sóc da mặt cho nữ giới đúng cách nhất', 'english version Chăm sóc da mặt cho nữ luôn là vấn đề quan tâm hàng đầu của nữ giới hiện nay. Da mặt đẹp ở đâu bạn cũng tự tin tỏa sáng.', 'english version <p dir="ltr" style="text-align:justify">Khi x&atilde; hội ng&agrave;y c&agrave;ng ph&aacute;t triển th&igrave; nhu cầu l&agrave;m đẹp của con người cũng bắt đầu được quan t&acirc;m cao hơn. Ph&aacute;i nữ c&agrave;ng quan trọng điều n&agrave;y v&agrave; lu&ocirc;n t&igrave;m phương ph&aacute;p chăm s&oacute;c da mặt cho nữ để chăm s&oacute;c cũng như bảo vệ da tốt nhất. Chắc chắn những phương ph&aacute;p m&agrave; ch&uacute;ng t&ocirc;i đưa ra dưới đ&acirc;y sẽ gi&uacute;p l&agrave;n da trở n&ecirc;n s&aacute;ng hơn, loại bỏ được những vết bẩn kh&ocirc;ng đ&aacute;ng c&oacute;.</p>\r\n\r\n<h2 dir="ltr" style="text-align:justify"><strong>C&aacute;ch chăm s&oacute;c da mặt cho nữ giới</strong></h2>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>L&agrave;m sạch da với sữa rửa mặt</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Với những bạn nữ giới th&igrave; việc chăm s&oacute;c da mặt rất l&agrave; quan trọng, bạn nữ giới phải hiểu về c&aacute;c sản phẩm để lựa chọn cho bản th&acirc;n m&igrave;nh những sản phẩm ph&ugrave; hợp với l&agrave;n da m&igrave;nh nhất, tăng hiệu quả v&agrave; tr&aacute;nh được những k&iacute;ch ứng cho l&agrave;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Bạn n&ecirc;n l&agrave;m sạch l&agrave;n da của m&igrave;nh bằng sữa rửa mặt l&agrave; bước đầu ti&ecirc;n trong qu&aacute; tr&igrave;nh chăm s&oacute;c da mặt, bạn n&ecirc;n sử dụng sữa rửa mặt 2 lần trong 1 ng&agrave;y cũng kh&ocirc;ng n&ecirc;n rửa qu&aacute; nhiều sẽ dẫn đến t&igrave;nh trạng da bị kh&ocirc;. V&igrave; trong da lu&ocirc;n chứa c&aacute;c tiết chất l&agrave;m ẩm tự nhi&ecirc;n n&ecirc;n việc sử dụng sữa rửa mặt 2 lần 1 ng&agrave;y sẽ gi&uacute;p lấy đi những tinh dầu tr&ecirc;n l&agrave;n da của bạn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Bạn n&ecirc;n ch&uacute; &yacute; khi rửa mặt chỉ n&ecirc;n d&ugrave;ng nước ấm hoặc nước m&aacute;t, cũng n&ecirc;n t&igrave;m c&aacute;c sản phẩm chứa c&aacute;c th&agrave;nh phần từ tự nhi&ecirc;n để c&oacute; thể sử dụng 1 c&aacute;ch hiệu quả nhất. Đ&acirc;y cũng l&agrave; c&aacute;ch chăm s&oacute;c da mặt cho nữ để mọi người n&ecirc;n biết.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Tẩy da chết</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Việc tẩy da chết l&agrave; việc v&ocirc; c&ugrave;ng quan trọng n&ecirc;n bạn muốn sở hữu cho m&igrave;nh 1 l&agrave;n da trắng s&aacute;ng, mịn m&agrave;ng. Bạn n&ecirc;n tẩy da chết 1 tuần 2 lần để ph&aacute;t huy được hiệu quả mang lại l&agrave;n da s&aacute;ng b&oacute;ng cho nữ giới.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-cho-nu" height="533" src="/image/images/cham-soc-da-mat-cho-nu%20(2).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><em>Sau khi rửa sạch mặt n&ecirc;n b&ocirc;i một lớp tẩy tế b&agrave;o chết l&ecirc;n tr&ecirc;n mặt</em></p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Khi l&agrave;n da của bạn kh&ocirc;ng trắng s&aacute;ng v&agrave; kh&ocirc;ng c&oacute; mụn th&igrave; bạn sẽ c&oacute; 1 th&ecirc;m sự tự tin khi giao tiếp.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Dưỡng ẩm hằng ng&agrave;y</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Với những bạn nữ giới th&igrave; n&ecirc;n lựa chọn những sản phẩm dưỡng ẩm l&agrave;n da để c&oacute; thể chăm s&oacute;c l&agrave;n da ng&agrave;y c&agrave;ng tốt hơn.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Chăm s&oacute;c da mặt cho nữ rất tốt khi bạn đ&atilde; sở hữu 1 serum dưỡng ẩm để c&oacute; thể tiết kiệm thời gian khi chăm s&oacute;c da cho nữ giới.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Ủ Face Saffron &amp; Cherry Nhật Bản</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Th&agrave;nh phần:</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Trong<a href="http://myichy.vn/sp-tam-trang-bach-hoa-tuyet"><strong> kem Ủ Face Saffron &amp; Cherry Nhật Bản</strong></a> c&oacute; chứa những th&agrave;nh phần ch&iacute;nh như Purified water, Lactic acid, Glutathione, vitamin E, Allantoine, Vaseline, Saffron, Benzoyl peroxid, Triethanolamine, Glycerin, Aloe vera extract, Alcohol cetearyl, Triethanolamine, Sodium benzoate, Ethanol, Methyl paraben, PEG400.</p>\r\n\r\n<p style="text-align:center"><img alt="cham-soc-da-mat-cho-nu" height="600" src="/image/images/cham-soc-da-mat-cho-nu%20(1).jpg" width="800" /></p>\r\n\r\n<p dir="ltr" style="text-align:justify"><em>Ủ Face Saffron &amp; Cherry Nhật Bản một sản phẩm rất tốt cho chăm s&oacute;c da mặt</em></p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>C&ocirc;ng dụng:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- Whitening Face Mask l&agrave; sản phẩm được chiết xuất từ c&aacute;c tinh chất cherry v&agrave; saffron v&agrave; kết hợp với vitamin để c&oacute; thể cung cấp c&aacute;c dưỡng chất cần thiết cho l&agrave;n da v&agrave; nu&ocirc;i dưỡng da v&agrave; gi&uacute;p lấy c&aacute;c tế b&agrave;o chết tr&ecirc;n da.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- Gi&uacute;p l&agrave;n da của bạn trở n&ecirc;n s&aacute;ng b&oacute;ng, mịn m&agrave;ng v&agrave; lu&ocirc;n giữ được sự tươi trẻ cho bản th&acirc;n, chống l&atilde;o h&oacute;a cho l&agrave;n da 1 c&aacute;ch tốt nhất.</p>\r\n\r\n<h3 dir="ltr" style="text-align:justify"><strong>Hướng dẫn sử dụng:</strong></h3>\r\n\r\n<p dir="ltr" style="text-align:justify">- G&oacute;i 1 l&agrave; loại kem face mask với th&agrave;nh phần ch&iacute;nh l&agrave; hoa cherry.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">- G&oacute;i 2 l&agrave; loại kem face mask với c&aacute;c th&agrave;nh phần ch&iacute;nh từ saffron.</p>\r\n\r\n<ul>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n rửa mặt bằng nước thật sạch v&agrave; sau đ&oacute; lau kh&ocirc;.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n lấy 1 lượng kem ở g&oacute;i 1 thoa đều l&ecirc;n l&agrave;n da mặt massage nhẹ nh&agrave;ng sau đ&oacute; ủ 15 ph&uacute;t Khi đ&atilde; ho&agrave;n th&agrave;nh th&igrave; bạn n&ecirc;n rửa sạch với nước v&agrave; lau kh&ocirc; da mặt.</p>\r\n	</li>\r\n	<li dir="ltr">\r\n	<p dir="ltr" style="text-align:justify">Bạn n&ecirc;n lấy 1 lượng kem ở g&oacute;i 2 thoa đều l&ecirc;n l&agrave;n da mặt massage nhẹ nh&agrave;ng sau đ&oacute; ủ trong 10 ph&uacute;t để c&oacute; được hiệu quả tốt nhất.</p>\r\n	</li>\r\n</ul>\r\n\r\n<p dir="ltr" style="text-align:justify">Đ&acirc;y l&agrave; c&aacute;ch <strong>chăm s&oacute;c da mặt cho nữ</strong> m&agrave; mọi người n&ecirc;n biết để c&oacute; thể nhanh ch&oacute;ng ph&aacute;t huy tối đa hiệu quả. Kh&ocirc;ng hề kh&oacute; để chăm s&oacute;c cho n&ecirc;n mọi người c&oacute; thể ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m.</p>\r\n\r\n<p dir="ltr" style="text-align:justify">Xem th&ecirc;m:&nbsp;<a href="http://myichy.vn/bi-quyet-cham-soc-da-mat-co-lo-chan-long-to"><strong>B&iacute; quyết chăm s&oacute;c da mặt c&oacute; lỗ ch&acirc;n l&ocirc;ng to</strong></a></p>\r\n', '', '', '', '', '', 0, 'en-cach-cham-soc-da-mat-cho-nu-gioi-dung-cach-nhat', 'Cách chăm sóc da mặt cho nữ giới đúng cách nhất', 'Chăm sóc da mặt cho nữ luôn là vấn đề quan tâm hàng đầu của nữ giới hiện nay. Da mặt đẹp ở đâu bạn cũng tự tin tỏa sáng.', ' chăm sóc da mặt cho nữ');
INSERT INTO `news_languages` (`id`, `news_id`, `languages_code`, `lang_news_name`, `lang_news_des`, `lang_news_content`, `lang_news_sub_info1`, `lang_news_sub_info2`, `lang_news_sub_info3`, `lang_news_sub_info4`, `lang_news_sub_info5`, `edit_state`, `friendly_url`, `title_seo`, `des_seo`, `keyword`) VALUES
(127, 64, 'vn', 'Chia sẻ bí quyết chăm sóc da hoàn hảo', 'Chia sẻ bí quyết chăm sóc da hoàn hảo với sữa rửa mặt hoa anh đào myichy', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Chia sẻ b&iacute; quyết chăm s&oacute;c da ho&agrave;n hảo</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc chăm s&oacute;c da tốt c&oacute; thể đẩy l&ugrave;i qu&aacute; tr&igrave;nh l&atilde;o h&oacute;a tự nhi&ecirc;n v&agrave; ph&ograve;ng chống những vấn đề về da liễu. Tuy nhi&ecirc;n, đ&ocirc;i khi bạn lại kh&ocirc;ng c&oacute; đủ thời gian để chăm s&oacute;c l&agrave;n da một c&aacute;ch chuy&ecirc;n s&acirc;u? Đừng qu&aacute; lo lắng, h&atilde;y c&ugrave;ng Tatu Luxury tham khảo những lời khuy&ecirc;n về chăm s&oacute;c da mặt cực kỳ hiệu quả sau nh&eacute;!</p>\r\n\r\n<p>Chăm s&oacute;c l&agrave;m sạch da mặt n&ecirc;n được thực hiện thường xuy&ecirc;n mỗi ng&agrave;y &iacute;t nhất một lần v&agrave;o buổi tối v&agrave; th&ecirc;m một lần v&agrave;o buổi s&aacute;ng nếu c&oacute; d&ugrave;ng dưỡng ẩm da ban đ&ecirc;m. với c&aacute;c bước chăm s&oacute;c da hiệu quả tại nh&agrave; n&agrave;y th&igrave; c&aacute;c bạn kh&ocirc;ng cần tốn nhiều chi ph&iacute; v&agrave; mất qu&aacute; nhiều thời gian để c&oacute; một l&agrave;n da tươi tắn v&agrave; s&aacute;ng đẹp. C&ugrave;ng theo d&otilde;i v&agrave; thực hiện để c&oacute; một l&agrave;n da trắng s&aacute;ng v&agrave; mịn m&agrave;ng như mong muốn nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2>Quy tr&igrave;nh chăm s&oacute;c da mặt kỹ lưỡng</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 1: Tẩy trang</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu ti&ecirc;n cần rửa sạch tay trước khi thực hiện c&aacute;c bước chăm s&oacute;c da v&igrave; b&agrave;n tay của ch&uacute;ng ta qua một ng&agrave;y d&agrave;i hoạt động thường t&iacute;ch tụ rất nhiều bụi bẩn v&agrave; c&aacute;c loại vi khuẩn. V&igrave; vậy, rửa tay trước khi rửa mặt l&agrave; một điều cần thiết để bắt đầu qu&aacute; tr&igrave;nh l&agrave;m sạch da</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="Tatu Luxury - Mỹ Phẩm Sạch Nhật Bản" height="250" src="https://tatuluxury.com/upload/images/chia-se-bi-quyet-cham-soc-da-hoan-hao.jpg" width="445" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc tẩy trang l&agrave; rất quan trọng v&agrave; cần thiết bởi v&igrave; n&oacute; gi&uacute;p lấy sạch đi bụi bẩn, chất b&atilde; nhờn, vi khuẩn, mỹ phẩm trang điểm v&agrave; c&aacute;c tế b&agrave;o chết nhằm g&oacute;p phần phục hồi lại sức khỏe cho l&agrave;n da.</p>\r\n\r\n<p>Đối với da mặt khi tẩy trang cần d&ugrave;ng nước ấm để rửa mặt nhằm gi&uacute;p loại bỏ dễ hơn c&aacute;c chất nhờn, mỹ phẩm v&agrave; kh&ocirc;ng g&acirc;y kh&ocirc; da.</p>\r\n\r\n<p>Sử dụng một lượng vừa đủ sản phẩm tẩy trang ph&ugrave; hợp v&agrave;o l&ograve;ng b&agrave;n tay v&agrave; b&ocirc;i l&ecirc;n da mặt massage nhẹ nh&agrave;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 2: Tẩy tế b&agrave;o chết</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bước này thì các bạn kh&ocirc;ng c&acirc;̀n dùng thường xuy&ecirc;n. Cách 2 đ&ecirc;́n 3 ngày dùng 1 l&acirc;̀n là t&ocirc;́t nh&acirc;́t</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="Tatu Luxury - Mỹ Phẩm Sạch Nhật Bản" height="309" src="https://tatuluxury.com/upload/images/chia-se-bi-quyet-cham-soc-da-hoan-hao-2.jpg" width="550" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc tẩy da chết sẽ gi&uacute;p loại bỏ c&aacute;c tế b&agrave;o da cũ bị l&atilde;o h&oacute;a, gi&uacute;p lỗ ch&acirc;n l&ocirc;ng trở n&ecirc;n th&ocirc;ng tho&aacute;ng, k&iacute;ch th&iacute;ch c&aacute;c tế b&agrave;o da mới t&aacute;i tạo tốt hơn. Đồng thời gi&uacute;p hấp thụ c&aacute;c sản phẩm dưỡng da hiệu quả hơn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 3: Sữa rửa mặt</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt="Tatu Luxury - Mỹ Phẩm Sạch Nhật Bản" height="338" src="https://tatuluxury.com/upload/images/chia-se-bi-quyet-cham-soc-da-hoan-hao-3.jpg" width="600" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rửa mặt đều đặn từ 2-3 lần mỗi ng&agrave;y, bao gồm 1 lần v&agrave;o buổi s&aacute;ng sau khi thức dậy, 1 lần v&agrave;o buổi tối trước khi đi ngủ</p>\r\n\r\n<p>Rửa mặt c&oacute; t&aacute;c dụng loại bỏ c&aacute;c b&atilde; nhờn, dầu thừa, bụi bẩn v&agrave; vi khuẩn tr&ecirc;n da, gi&uacute;p lỗ ch&acirc;n l&ocirc;ng trở n&ecirc;n th&ocirc;ng tho&aacute;ng v&agrave; ngăn ngừa nguy cơ nổi mụn</p>\r\n\r\n<p>Cho sữa rửa mặt v&agrave;o l&ograve;ng b&agrave;n tay tạo bọt v&agrave; chuyển động tr&ograve;n trong khoảng 30 gi&acirc;y. Rửa sạch với nước ấm, cuối c&ugrave;ng d&ugrave;ng nước lạnh rửa để đ&oacute;ng lỗ ch&acirc;n l&ocirc;ng.</p>\r\n\r\n<p>Lựa chọn sửa rửa mặt ph&ugrave; hợp với loại da của m&igrave;nh</p>\r\n\r\n<p><strong><a href="https://tatuluxury.com/san-pham/sua-rua-mat-tatu-8.html">S</a>ữa Rửa Mặt Hoa Anh Đ&agrave;o</strong>&nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt="" height="680" src="/image/images/Sua-rua-mat-hoa-anh-dao-190k.png" width="680" /></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">TH&Agrave;NH PHẦN:</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">Nước tinh khiết, Glycerine, Trilon B, Alcohol Cetyl, Sodium Laureth sulfate, Sodium Benzoate, Methylparaben, Alpha Tocopherol, Fragrance, Triethanoilamine, Allantoine.</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">C&Ocirc;NG DỤNG:</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- L&agrave;m sạch s&acirc;u, loại bỏ c&aacute;c lớp trang điểm v&agrave; bụi bẩn tr&ecirc;n da</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- Loại bỏ tế b&agrave;o chết, c&acirc;n bằng sắc tố, cho l&agrave;n da trắng mịn tự nhi&ecirc;n</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- <span style="background-color:white">G&oacute;p phần gi&uacute;p da trắng s&aacute;ng, mịn m&agrave;ng, giữ da lu&ocirc;n tươi trẻ, chống l&atilde;o h&oacute;a</span>.</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">HƯỚNG DẪN SỬ DỤNG:</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- Bước 1: Rửa mặt với nước sạch, để ẩm</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- Bước 2: Lấy một lượng sữa rửa mặt vừa đủ, thoa đều l&ecirc;n mặt v&agrave; massage nhẹ nh&agrave;ng </span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">- Bước 3: Rửa sạch lại với nước.</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">BẢO QUẢN:</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify"><span style="font-size:12pt">Để nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></p>\r\n\r\n<p style="margin-left:0in; margin-right:0in; text-align:justify">H&atilde;y li&ecirc;n hệ ngay với&nbsp;<a href="http://myichy.vn/"><strong>myichy.vn&nbsp;</strong></a>để nhận được sự tư vấn kỹ c&agrave;ng v&agrave; chi tiết hơn về sản phẩm.</p>\r\n', '', '', '', '', '', 0, 'chia-se-bi-quyet-cham-soc-da-hoan-hao', 'Chia sẻ bí quyết chăm sóc da hoàn hảo', 'Chia sẻ bí quyết chăm sóc da hoàn hảo với sữa rửa mặt hoa anh đào myichy', 'myichy, my ichy, công ty dược mỹ phẩm myichy, công ty dược mỹ phẩm my ichy'),
