(41, 27, 'vn', 'Ủ Face Saffron & Cherry Nhật Bản', '<p><span style="font-size:12.0pt"><span style="color:black">Whitening Face Mask được chiết xuất từ tinh chất hoa Cherry v&agrave; Saffron c&ugrave;ng c&aacute;c vitamin <span style="background-color:white">gi&uacute;p cung cấp c&aacute;c dưỡng chất cần thiết để nu&ocirc;i dưỡng c&aacute;c tế b&agrave;o da, l&agrave;m sạch v&agrave; lấy đi lớp tế b&agrave;o chết tr&ecirc;n da</span></span></span></p>\r\n', '', '', '<div>\r\n<p style="text-align:justify"><span style="font-size:20px"><span style="font-family:Arial,Helvetica,sans-serif"><strong>Ủ Face Saffron &amp; Cherry Nhật Bản</strong></span></span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-size:18px"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="color:black">C&ocirc;ng dụng:</span></span></strong></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">-Whitening Face Mask được chiết xuất từ tinh chất hoa Cherry v&agrave; Saffron c&ugrave;ng c&aacute;c vitamin <span style="background-color:white">gi&uacute;p cung cấp c&aacute;c dưỡng chất cần thiết để nu&ocirc;i dưỡng c&aacute;c tế b&agrave;o da, l&agrave;m sạch v&agrave; lấy đi lớp tế b&agrave;o chết tr&ecirc;n da.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">G&oacute;p phần gi&uacute;p da trắng s&aacute;ng, mịn m&agrave;ng, giữ da lu&ocirc;n tươi trẻ, chống l&atilde;o h&oacute;a.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-size:18px"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="color:black">Th&agrave;nh phần</span></span></strong></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">Saffron, Purified Water, Aloe Vera Extract, Benzoyl Peroxid, Alcohol Cetearyl, Triethanolamine, Methyl Paraben, Sodium Benzoate, Glycerin, Ethanol, PEG400, Vitamin E, Lactic Acid, Vaseline, Glutathione, Allantoine.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-size:18px"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="color:black">Hướng dẫn sử dụng</span></span></strong></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><em><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Bộ sản phẩm gồm 02 g&oacute;i:</span></span></span></em></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>G&oacute;i 01:</strong> Kem face mask với th&agrave;nh phần ch&iacute;nh l&agrave; hoa Cherry</span></span></span></p>\r\n	</li>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>G&oacute;i 02: </strong>Kem face mask với th&agrave;nh phần ch&iacute;nh l&agrave; Saffron</span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>Bước 1:</strong> </span><span style="background-color:white"><span style="color:black">Rửa mặt (hoặc tẩy da chết) thật sạch, lau ẩm;</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black"><strong>Bước 2:</strong> Lấy 1 lượng kem vừa đủ ở g&oacute;i số 01 thoa đều v&agrave; massage mặt&nbsp; rồi ủ 15 ph&uacute;t. Sau đ&oacute; rửa sạch với nước, lau ẩm.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black"><strong>Bước 3:</strong> Lấy 1 lượng kem vừa đủ ở g&oacute;i số 02 thoa đều l&ecirc;n mặt&nbsp; v&agrave; massage nhẹ nh&agrave;ng, ủ trong 10 ph&uacute;t.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black"><strong>Bước 4:</strong> Rửa sạch mặt với nước v&agrave; cảm nhận sự thay đổi k&igrave; diệu của l&agrave;n da.</span></span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-size:18px"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="color:black">Bảo quản</span></span></strong></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">Để nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify">\r\n	<p><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">Chỉ ti&ecirc;u k&iacute;ch ứng: Kh&ocirc;ng đ&aacute;ng&nbsp; kể</span></span></span></span></p>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n</div>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'u-face-saffron-cherry-nhat-ban', 'Ủ Face Saffron & Cherry Nhật Bản', '', ''),
(42, 27, 'en', 'Rice KEO', '<p>Ngo&agrave;i t&aacute;c dụng điều chỉnh &aacute;nh s&aacute;ng cho căn ph&ograve;ng, r&egrave;m cửa (m&agrave;n cửa) c&ograve;n giữ ấm cho căn ph&ograve;ng khi m&ugrave;a đ&ocirc;ng đến, hạn chế c&aacute;i n&oacute;ng của m&ugrave;a h&egrave;, bụi v&agrave; tiếng ồn b&ecirc;n ngo&agrave;i.&nbsp;</p>\r\n', '', '', '<div>\r\n<h3>M&Ocirc; TẢ SẢN PHẨM</h3>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<p>Ngo&agrave;i t&aacute;c dụng điều chỉnh &aacute;nh s&aacute;ng cho căn ph&ograve;ng, r&egrave;m cửa (m&agrave;n cửa) c&ograve;n giữ ấm cho căn ph&ograve;ng khi m&ugrave;a đ&ocirc;ng đến, hạn chế c&aacute;i n&oacute;ng của m&ugrave;a h&egrave;, bụi v&agrave; tiếng ồn b&ecirc;n ngo&agrave;i. Kh&ocirc;ng chỉ c&oacute; thế, với kiểu d&aacute;ng v&agrave; m&agrave;u sắc th&iacute;ch hợp,&nbsp;r&egrave;m cửa&nbsp;c&ograve;n c&oacute; thể tạo khoảng kh&ocirc;ng ri&ecirc;ng tư hoặc th&ecirc;m ch&uacute;t vẻ đẹp sang trọng hay l&atilde;ng mạn cho căn ph&ograve;ng.<br />\r\n&nbsp;&nbsp;<br />\r\nChiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.<br />\r\n&nbsp;&nbsp;<br />\r\nC&oacute; rất nhiều loại r&egrave;m kh&aacute;c nhau như r&egrave;m v&eacute;n,&nbsp;r&egrave;m roman&nbsp;(hay c&ograve;n gọi l&agrave; r&egrave;m xếp lớp), r&egrave;m cuốn, r&egrave;m l&aacute;,... Bạn cũng c&oacute; thể d&ugrave;ng r&egrave;m tr&uacute;c, r&egrave;m kim loại nhẹ, r&egrave;m bằng nhựa tổng hợp hay r&egrave;m giấy để trang tr&iacute; nh&agrave; cửa. Tuy nhi&ecirc;n, loại r&egrave;m phổ biến vẫn l&agrave; r&egrave;m vải v&igrave; t&iacute;nh năng dễ giặt tẩy, dễ lắp đặt, m&agrave;u v&agrave; kiểu phong ph&uacute;, hợp với nhiều phong c&aacute;ch nội thất.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<h3>CHẤT LIỆU</h3>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<p>C&aacute;c loại vải may&nbsp;r&egrave;m cửa&nbsp;đang c&oacute; tr&ecirc;n thị trường rất đa dạng nhưng phần lớn l&agrave; cotton, polyester. Chất liệu thun, ren gần như kh&ocirc;ng c&ograve;n được ưa chuộng nữa v&igrave; dễ b&aacute;m bụi, mau bị sờn. &ETH;ược nhiều người chọn nhất vẫn l&agrave; c&aacute;c loại vải 65% cotton - 35% polyester hay loại 52% cotton - 48% polyester với nhiều nguồn gốc xuất xứ như Trung Quốc, H&agrave;n Quốc, Anh, Indonesia, Nga v&agrave; h&agrave;ng Việt Nam. Thị trường vải may r&egrave;m cửa kh&aacute; đa dạng với h&agrave;ng trăm mẫu hoa văn v&agrave; m&agrave;u sắc kh&aacute;c nhau. C&aacute;c loại r&egrave;m cửa bằng vải cotton, bố gấm, bố th&ocirc;, vải c&oacute; chất liệu chống thấm v&agrave; &iacute;t b&aacute;m bụi với c&aacute;c hoa văn đơn giản, c&aacute;c loại c&oacute; b&ocirc;ng ở ch&acirc;n may theo nếp gấp, li ống, li đứng đang được nhiều người ưa chuộng. C&aacute;c loại&nbsp;r&egrave;m&nbsp;cửa may theo kiểu b&egrave;o s&ograve; hay c&aacute;c kiểu rườm r&agrave; của một hai năm về trước đ&atilde; kh&ocirc;ng c&ograve;n được ưa chuộng. Xu hướng mới nhất năm nay l&agrave; dựa theo c&aacute;c mẫu đơn giản c&oacute; trong catologue nước ngo&agrave;i. Mẫu đang b&aacute;n chạy l&agrave; mẫu m&agrave;u kem với c&aacute;c hoa văn đơn giản. C&aacute;c loại hoa văn được xem l&agrave; mốt mới trong năm nay l&agrave; c&aacute;c h&igrave;nh lập thể, ca r&ocirc; v&agrave; c&aacute;c mẫu hoa nhỏ.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<h3>MẪU M&Atilde;, KIỂU D&Aacute;NG</h3>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<p>Ch&uacute;ng t&ocirc;i rất đa dạng về kiểu d&aacute;ng, sản phẩm của&nbsp;r&egrave;m nghệ thuật d&ugrave; mang phong c&aacute;ch cổ điển, hiện đại hay được s&aacute;ng tạo&nbsp;ph&aacute; c&aacute;ch v&agrave; đầy ngẫu hứng vẫn lu&ocirc;n đem đến cho người sử dụng cảm gi&aacute;c th&iacute;ch th&uacute; v&agrave; h&agrave;i l&ograve;ng nhất. Ph&aacute;t huy s&aacute;ng tạo vượt mọi r&agrave;o cản r&egrave;m nghệ thuật l&agrave; đỉnh cao của những s&aacute;ng tạo kh&ocirc;ng giới hạn, ch&uacute;ng t&ocirc;i mong muốn mang đến những sản phẩm r&egrave;m cửa tốt nhất, rẻ nhất gi&uacute;p cho bữa cơm gia đ&igrave;nh th&ecirc;m đầm ấm, hạnh ph&uacute;c!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Loại vải b&oacute;ng mượt, mềm mịn đem lại vẻ sang trọng như nhung, gấm, đũi, lụa, voan... thường một m&agrave;u hoặc mang hoa văn, hoạ tiết ch&igrave;m, trang nh&atilde;, hợp với ph&ograve;ng kh&aacute;ch. Những loại vải bề mặt th&ocirc; r&aacute;p cho cảm gi&aacute;c th&acirc;n mật, gần gũi thi&ecirc;n nhi&ecirc;n lại k&eacute;n phong c&aacute;ch b&agrave;i tr&iacute; như vải bạt, bố, th&ocirc;, thổ cẩm... chỉ n&ecirc;n sử dụng trong ph&ograve;ng b&agrave;y đồ đạc m&acirc;y, tre, gỗ mộc, thảm l&ocirc;ng th&uacute;. Vải b&ocirc;ng, lanh c&oacute; nhiều m&agrave;u hoa văn, hợp với nội thất b&igrave;nh thường, nhất l&agrave; ph&ograve;ng trẻ em, ph&ograve;ng ăn, ph&ograve;ng giải tr&iacute;...<br />\r\n<br />\r\nR&egrave;m (m&agrave;n) m&ugrave;a h&egrave; thường l&agrave; những loại&nbsp;r&egrave;m&nbsp;(m&agrave;n) c&oacute; chất liệu nhẹ nh&agrave;ng như: vải th&ocirc; mỏng, voan, lụa, lanh... c&ograve;n r&egrave;m m&ugrave;a đ&ocirc;ng lại cần tạo sự ấm &aacute;p, n&ecirc;n chất liệu chủ yếu l&agrave; nhung, gấm, th&ocirc; dầy v&agrave; thổ cẩm. Tuy nhi&ecirc;n l&agrave; m&ugrave;a đ&ocirc;ng hay m&ugrave;a h&egrave; việc lựa chọn chất liệu cũng phụ thuộc rất nhiều v&agrave;o mức độ chắn s&aacute;ng của &ocirc; cửa trong nh&agrave;.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<h3>GI&Aacute; TH&Agrave;NH</h3>\r\n\r\n<div>\r\n<div>\r\n<div>\r\n<p>G&iacute;a:</p>\r\n\r\n<p>&nbsp;Khoảng 760.000đ/ 1m ngang ho&agrave;n thiện trở l&ecirc;n (bao gồm c&ocirc;ng, phụ kiện lắp đặt,&nbsp;</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div>\r\n<h3>BẢO QUẢN V&Agrave; SỬ DỤNG</h3>\r\n</div>\r\n', '', '', '01', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'rice-keo', 'Rice KEO', 'Ngoài tác dụng điều chỉnh ánh sáng cho căn phòng, rèm cửa (màn cửa) còn giữ ấm cho căn phòng khi mùa đông đến, hạn chế cái nóng của mùa hè, bụi và tiếng ồn bên ngoài. ', 'Rèm vải'),
(43, 28, 'vn', 'Tắm trắng Bạch Hoa Tuyết', '<p><span style="font-size:12pt"><span style="font-size:13.0pt"><span style="color:black">Đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời mang lại l&agrave;n da trắng hồng vượt trội sau mỗi lần sử dụng. Hương hoa hồng kết hợp Lavender mang đến hương thơm tự nhi&ecirc;n tạo cảm gi&aacute;c thư gi&atilde;n v&agrave; thoải m&aacute;i.</span></span></span></p>\r\n', '', '', '<p style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong><span style="color:black">Tắm trắng </span></strong><strong>Bạch Hoa Tuyết</strong></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">C&ocirc;ng dụng</span></span></span></strong></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Đ&aacute;nh bay lớp tế b&agrave;o chết tr&ecirc;n da, cung cấp những dưỡng chất tuyệt vời mang lại l&agrave;n da trắng hồng vượt trội sau mỗi lần sử dụng. Hương hoa hồng kết hợp Lavender mang đến hương thơm tự nhi&ecirc;n tạo cảm gi&aacute;c thư gi&atilde;n v&agrave; thoải m&aacute;i.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Th&agrave;nh phần</span></span></span></strong></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Cabomer, Nước tinh khiết, Chiết xuất hoa hồng, Bột ngọc trai, Tinh chất Lavender, Alpha Abutin, Grycerin,&hellip;</span></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Hướng dẫn sử dụng</span></span></span></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Bộ sản phẩm gồm 03 g&oacute;i:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong> G&oacute;i 01:</strong> Dung dịch tắm trắng hoa hồng </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>G&oacute;i 02:</strong> Bột ngọc trai</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong> G&oacute;i 03:</strong> Serum Lavender</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>Bước 1:</strong> Tắm sơ qua với nước, để ẩm người. </span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>Bước 2:</strong> Lấy g&oacute;i 01 v&agrave; g&oacute;i 02 trộn đều với nhau tạo th&agrave;nh hỗn hợp nhuyễn mịn. Thoa đều hỗn hợp l&ecirc;n da, massage nhẹ nh&agrave;ng trong 15 ph&uacute;t. Sau đ&oacute; xả sạch lại với nước v&agrave; lau kh&ocirc;.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black"><strong>Bước 3:</strong> Thoa Serum Lavender v&agrave; kh&ocirc;ng cần tắm lại với nước. Dưỡng chất sẽ tự thẩm thấu v&agrave; nu&ocirc;i dưỡng c&aacute;c tế b&agrave;o da trắng hồng như &yacute;.</span></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="color:black">Bảo quản</span></span></span></strong></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></span></span></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white"><span style="color:black">Chỉ ti&ecirc;u k&iacute;ch ứng: Kh&ocirc;ng đ&aacute;ng&nbsp; kể</span></span></span></span></li>\r\n</ul>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'tam-trang-bach-hoa-tuyet', 'Tắm trắng Bạch Hoa Tuyết', '', ''),
(44, 28, 'en', 'Sữa RN', '<p>Ngo&agrave;i t&aacute;c dụng điều chỉnh &aacute;nh s&aacute;ng cho căn ph&ograve;ng, r&egrave;m cửa (m&agrave;n cửa) c&ograve;n giữ ấm cho căn ph&ograve;ng khi m&ugrave;a đ&ocirc;ng đến, hạn chế c&aacute;i n&oacute;ng của m&ugrave;a h&egrave;, bụi v&agrave; tiếng ồn b&ecirc;n ngo&agrave;i.&nbsp;</p>\r\n', '', '', '<h3>M&Ocirc; TẢ SẢN PHẨM</h3>\r\n\r\n<p>Ngo&agrave;i t&aacute;c dụng điều chỉnh &aacute;nh s&aacute;ng cho căn ph&ograve;ng, r&egrave;m cửa (m&agrave;n cửa) c&ograve;n giữ ấm cho căn ph&ograve;ng khi m&ugrave;a đ&ocirc;ng đến, hạn chế c&aacute;i n&oacute;ng của m&ugrave;a h&egrave;, bụi v&agrave; tiếng ồn b&ecirc;n ngo&agrave;i. Kh&ocirc;ng chỉ c&oacute; thế, với kiểu d&aacute;ng v&agrave; m&agrave;u sắc th&iacute;ch hợp,&nbsp;r&egrave;m cửa&nbsp;c&ograve;n c&oacute; thể tạo khoảng kh&ocirc;ng ri&ecirc;ng tư hoặc th&ecirc;m ch&uacute;t vẻ đẹp sang trọng hay l&atilde;ng mạn cho căn ph&ograve;ng.<br />\r\n&nbsp;&nbsp;<br />\r\nChiếc r&egrave;m cửa kh&ocirc;ng chỉ đơn giản l&agrave; vật chắn s&aacute;ng hay tạo khoảng kh&ocirc;ng ri&ecirc;ng tư, n&oacute; c&ograve;n tạo cho mỗi căn ph&ograve;ng một ấn tượng v&agrave; cảm x&uacute;c kh&aacute;c nhau, t&ugrave;y theo c&aacute;ch chọn lựa của chủ nh&acirc;n.<br />\r\n&nbsp;&nbsp;<br />\r\nC&oacute; rất nhiều loại r&egrave;m kh&aacute;c nhau như r&egrave;m v&eacute;n,&nbsp;r&egrave;m roman&nbsp;(hay c&ograve;n gọi l&agrave; r&egrave;m xếp lớp), r&egrave;m cuốn, r&egrave;m l&aacute;,... Bạn cũng c&oacute; thể d&ugrave;ng r&egrave;m tr&uacute;c, r&egrave;m kim loại nhẹ, r&egrave;m bằng nhựa tổng hợp hay r&egrave;m giấy để trang tr&iacute; nh&agrave; cửa. Tuy nhi&ecirc;n, loại r&egrave;m phổ biến vẫn l&agrave; r&egrave;m vải v&igrave; t&iacute;nh năng dễ giặt tẩy, dễ lắp đặt, m&agrave;u v&agrave; kiểu phong ph&uacute;, hợp với nhiều phong c&aacute;ch nội thất.</p>\r\n\r\n<h3>CHẤT LIỆU</h3>\r\n\r\n<p>C&aacute;c loại vải may&nbsp;r&egrave;m cửa&nbsp;đang c&oacute; tr&ecirc;n thị trường rất đa dạng nhưng phần lớn l&agrave; cotton, polyester. Chất liệu thun, ren gần như kh&ocirc;ng c&ograve;n được ưa chuộng nữa v&igrave; dễ b&aacute;m bụi, mau bị sờn. &ETH;ược nhiều người chọn nhất vẫn l&agrave; c&aacute;c loại vải 65% cotton - 35% polyester hay loại 52% cotton - 48% polyester với nhiều nguồn gốc xuất xứ như Trung Quốc, H&agrave;n Quốc, Anh, Indonesia, Nga v&agrave; h&agrave;ng Việt Nam. Thị trường vải may r&egrave;m cửa kh&aacute; đa dạng với h&agrave;ng trăm mẫu hoa văn v&agrave; m&agrave;u sắc kh&aacute;c nhau. C&aacute;c loại r&egrave;m cửa bằng vải cotton, bố gấm, bố th&ocirc;, vải c&oacute; chất liệu chống thấm v&agrave; &iacute;t b&aacute;m bụi với c&aacute;c hoa văn đơn giản, c&aacute;c loại c&oacute; b&ocirc;ng ở ch&acirc;n may theo nếp gấp, li ống, li đứng đang được nhiều người ưa chuộng. C&aacute;c loại&nbsp;r&egrave;m&nbsp;cửa may theo kiểu b&egrave;o s&ograve; hay c&aacute;c kiểu rườm r&agrave; của một hai năm về trước đ&atilde; kh&ocirc;ng c&ograve;n được ưa chuộng. Xu hướng mới nhất năm nay l&agrave; dựa theo c&aacute;c mẫu đơn giản c&oacute; trong catologue nước ngo&agrave;i. Mẫu đang b&aacute;n chạy l&agrave; mẫu m&agrave;u kem với c&aacute;c hoa văn đơn giản. C&aacute;c loại hoa văn được xem l&agrave; mốt mới trong năm nay l&agrave; c&aacute;c h&igrave;nh lập thể, ca r&ocirc; v&agrave; c&aacute;c mẫu hoa nhỏ.</p>\r\n\r\n<h3>MẪU M&Atilde;, KIỂU D&Aacute;NG</h3>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i rất đa dạng về kiểu d&aacute;ng, sản phẩm của&nbsp;r&egrave;m nghệ thuật d&ugrave; mang phong c&aacute;ch cổ điển, hiện đại hay được s&aacute;ng tạo&nbsp;ph&aacute; c&aacute;ch v&agrave; đầy ngẫu hứng vẫn lu&ocirc;n đem đến cho người sử dụng cảm gi&aacute;c th&iacute;ch th&uacute; v&agrave; h&agrave;i l&ograve;ng nhất. Ph&aacute;t huy s&aacute;ng tạo vượt mọi r&agrave;o cản r&egrave;m nghệ thuật l&agrave; đỉnh cao của những s&aacute;ng tạo kh&ocirc;ng giới hạn, ch&uacute;ng t&ocirc;i mong muốn mang đến những sản phẩm r&egrave;m cửa tốt nhất, rẻ nhất gi&uacute;p cho bữa cơm gia đ&igrave;nh th&ecirc;m đầm ấm, hạnh ph&uacute;c!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Loại vải b&oacute;ng mượt, mềm mịn đem lại vẻ sang trọng như nhung, gấm, đũi, lụa, voan... thường một m&agrave;u hoặc mang hoa văn, hoạ tiết ch&igrave;m, trang nh&atilde;, hợp với ph&ograve;ng kh&aacute;ch. Những loại vải bề mặt th&ocirc; r&aacute;p cho cảm gi&aacute;c th&acirc;n mật, gần gũi thi&ecirc;n nhi&ecirc;n lại k&eacute;n phong c&aacute;ch b&agrave;i tr&iacute; như vải bạt, bố, th&ocirc;, thổ cẩm... chỉ n&ecirc;n sử dụng trong ph&ograve;ng b&agrave;y đồ đạc m&acirc;y, tre, gỗ mộc, thảm l&ocirc;ng th&uacute;. Vải b&ocirc;ng, lanh c&oacute; nhiều m&agrave;u hoa văn, hợp với nội thất b&igrave;nh thường, nhất l&agrave; ph&ograve;ng trẻ em, ph&ograve;ng ăn, ph&ograve;ng giải tr&iacute;...<br />\r\n<br />\r\nR&egrave;m (m&agrave;n) m&ugrave;a h&egrave; thường l&agrave; những loại&nbsp;r&egrave;m&nbsp;(m&agrave;n) c&oacute; chất liệu nhẹ nh&agrave;ng như: vải th&ocirc; mỏng, voan, lụa, lanh... c&ograve;n r&egrave;m m&ugrave;a đ&ocirc;ng lại cần tạo sự ấm &aacute;p, n&ecirc;n chất liệu chủ yếu l&agrave; nhung, gấm, th&ocirc; dầy v&agrave; thổ cẩm. Tuy nhi&ecirc;n l&agrave; m&ugrave;a đ&ocirc;ng hay m&ugrave;a h&egrave; việc lựa chọn chất liệu cũng phụ thuộc rất nhiều v&agrave;o mức độ chắn s&aacute;ng của &ocirc; cửa trong nh&agrave;.</p>\r\n\r\n<h3>GI&Aacute; TH&Agrave;NH</h3>\r\n\r\n<p>G&iacute;a:</p>\r\n\r\n<p>&nbsp;Khoảng 760.000đ/ 1m ngang ho&agrave;n thiện trở l&ecirc;n (bao gồm c&ocirc;ng, phụ kiện lắp đặt,&nbsp;</p>\r\n\r\n<h3>BẢO QUẢN V&Agrave; SỬ DỤNG</h3>\r\n', '', '', '02', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'rem-vai-02', 'Rèm vải 02', 'Ngoài tác dụng điều chỉnh ánh sáng cho căn phòng, rèm cửa (màn cửa) còn giữ ấm cho căn phòng khi mùa đông đến, hạn chế cái nóng của mùa hè, bụi và tiếng ồn bên ngoài. ', 'Rèm vải'),
(47, 30, 'vn', 'Body Tuyết Lạnh', '<p style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Cung cấp c&aacute;c dưỡng chất cần thiết gi&uacute;p da trắng hồng tự nhi&ecirc;n.&nbsp;</span></span></span><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Kết hợp từ c&aacute;c th&agrave;nh phần thi&ecirc;n nhi&ecirc;n cao cấp gi&uacute;p phục hồi da hư tổn, trẻ h&oacute;a da từ s&acirc;u b&ecirc;n trong.&nbsp;</span></span></span><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Hương thơm dịu nhẹ tự nhi&ecirc;n mang lại cảm gi&aacute;c thoải m&aacute;i, thư gi&atilde;n</span></span></span></p>\r\n', '', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong>Body Tuyết Lạnh</strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px">TH&Agrave;NH PHẦN:</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Purified Water, Aloe Barbadensis Extract, Dimethicone, Titanium Dioxide, Alcohol Cetyl, Phenolxyethanol, Fragrance, Pearl Power, Lactic Acid, Glycolic Acid, Alpha-Arbutin, Allantoine, Polysorbate 80, Glyceryl Stearate and PEG-100, Propylene Glycol, Nicotinamide.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">C&Ocirc;NG DỤNG:</span></span></span></strong></p>\r\n\r\n<ul>\r\n	<li style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Cung cấp c&aacute;c dưỡng chất cần thiết gi&uacute;p da trắng hồng tự nhi&ecirc;n.</span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Kết hợp từ c&aacute;c th&agrave;nh phần thi&ecirc;n nhi&ecirc;n cao cấp gi&uacute;p phục hồi da hư tổn, trẻ h&oacute;a da từ s&acirc;u b&ecirc;n trong.</span></span></span></li>\r\n	<li style="text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Hương thơm dịu nhẹ tự nhi&ecirc;n mang lại cảm gi&aacute;c thoải m&aacute;i, thư gi&atilde;n</span></span></span></li>\r\n</ul>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">HƯỚNG DẪN SỬ DỤNG:</span></span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">L&agrave;m sạch da to&agrave;n th&acirc;n trước khi sử dụng kem. Thoa một lượng kem vừa đủ l&ecirc;n to&agrave;n bộ cơ thể (kh&ocirc;ng d&ugrave;ng cho mặt). Massage nhẹ nh&agrave;ng để c&aacute;c dưỡng chất thấm đều v&agrave;o da. Sử dụng được cho c&aacute;c thời điểm trong ng&agrave;y.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Sử dụng h&agrave;ng ng&agrave;y để đem lại hiệu quả cao nhất.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px">BẢO QUẢN:</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px">Để nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></p>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'body-tuyet-lanh', 'Body Tuyết Lạnh', '', ''),
(48, 30, 'en', 'Meat', '<p>Thịt lợn hữu cơ l&agrave; thịt của lợn được nu&ocirc;i dưỡng v&agrave; chăm s&oacute;c trong m&ocirc;i trường ho&agrave;n to&agrave;n tự nhi&ecirc;n. Cho ăn bằng những thức ăn ho&agrave;n to&agrave;n tự nhi&ecirc;n như c&aacute;m ng&ocirc;, c&aacute;m gạo, rau, củ&hellip; &nbsp;M&agrave; kh&ocirc;ng sử dụng bất k&igrave; loại hooc-m&ocirc;n tăng trưởng, tăng trọng, chất tạo nạc, hay c&aacute;c sản phẩm biến đổi gen n&agrave;o kh&aacute;c.&nbsp;Thịt hữu cơ l&agrave; g&igrave;? Thịt hữu cơ c&oacute; tốt hơn thịt thường kh&ocirc;ng?</p>\r\n', '', '', '<p>C&oacute; thể nhận thấy sự kh&aacute;c biệt giữa thịt lợn hữu cơ v&agrave; thịt lợn sạch l&agrave;: Thịt lợn hữu cơ bắt buộc l&agrave; thịt từ những con lợn được nu&ocirc;i trong điều kiện thức ăn ti&ecirc;u chuẩn, ho&agrave;n to&agrave;n tự nhi&ecirc;n v&agrave; hữu cơ. Trong khi đ&oacute;, thịt lợn sạch kh&ocirc;ng bắt buộc l&agrave; sản phẩm được nu&ocirc;i lớn trong điều kiện hữu cơ. M&agrave; c&oacute; thể cho ăn bằng nhiều nguồn thức ăn kh&aacute;c. Nhưng khi xuất chuồng v&agrave; đưa sản phẩm ra thị trường th&igrave; phải đ&aacute;p ứng được 3 ti&ecirc;u chuẩn L&iacute; &ndash; H&oacute;a &ndash; Sinh như tr&ecirc;n.&nbsp;Thịt hữu cơ l&agrave; g&igrave;? Thịt hữu cơ c&oacute; tốt hơn thịt thường kh&ocirc;ng?</p>\r\n\r\n<p>C&oacute; thể n&oacute;i, thịt heo hữu cơ bao gồm cả thịt sạch v&agrave; l&agrave; mức ti&ecirc;u chuẩn cao nhất đối với sản phẩm từ thịt.</p>\r\n\r\n<p>Do được nu&ocirc;i dưỡng v&agrave; chăm s&oacute;c trong m&ocirc;i trường ho&agrave;n to&agrave;n tự nhi&ecirc;n. Cho ăn bằng những thức ăn ho&agrave;n to&agrave;n tự nhi&ecirc;n: c&aacute;m ng&ocirc;,c&aacute;m gạo, rau, củ&hellip; M&agrave; kh&ocirc;ng sử dụng bất k&igrave;, loại hooc-m&ocirc;n tăng trưởng, tăng trọng, chất tạo nạc, hay c&aacute;c sản phẩm biến đổi gen n&agrave;o kh&aacute;c. N&ecirc;n thịt hữu cơ vị ngon, chắc thịt, thơm thịt, hơn bất k&igrave; loại thịt n&agrave;o kh&aacute;c, h&agrave;m lượng dinh dưỡng cao. Gi&agrave;u protein v&agrave; c&aacute;c nguy&ecirc;n tố vi lượng: vitamin v&agrave; kho&aacute;ng chất&hellip;. &nbsp;Đảm bảo mọi ti&ecirc;u chuẩn về thịt sạch v&agrave; vệ sinh an to&agrave;n thực phẩm.Thịt hữu cơ l&agrave; g&igrave;? Thịt hữu cơ c&oacute; tốt hơn thịt thường kh&ocirc;ng?</p>\r\n', '', '', '01', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'meat', 'Meat', 'Ngoài tác dụng điều chỉnh ánh sáng cho căn phòng, rèm cửa (màn cửa) còn giữ ấm cho căn phòng khi mùa đông đến, hạn chế cái nóng của mùa hè, bụi và tiếng ồn bên ngoài. ', 'Rèm vải'),
(73, 43, 'vn', 'Face nám Hoàng Cung', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:12pt"><span style="background-color:white">Ức chế qu&aacute; tr&igrave;nh sản sinh ra sắc tố melanin trong tế b&agrave;o.&nbsp;</span></span><span style="font-size:12pt"><span style="background-color:white">L&agrave;m s&aacute;ng da, mờ c&aacute;c vết th&acirc;m n&aacute;m, t&agrave;n nhang v&agrave; bổ sung c&aacute;c dưỡng chất cho l&agrave;n da mềm mịn v&agrave; tươi trẻ.</span></span></p>\r\n', '', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong>Face n&aacute;m Ho&agrave;ng Cung</strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">TH&Agrave;NH PHẦN:</span></span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Nước tinh khiết, chất nhũ h&oacute;a, Vaseline, Alpha Arbutin, Vitamin C, L&aacute; c&acirc;y Việt quất.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">C&Ocirc;NG DỤNG:</span></span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Ức chế qu&aacute; tr&igrave;nh sản sinh ra sắc tố melanin trong tế b&agrave;o.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">L&agrave;m s&aacute;ng da, mờ c&aacute;c vết th&acirc;m n&aacute;m, t&agrave;n nhang v&agrave; bổ sung c&aacute;c dưỡng chất cho l&agrave;n da mềm mịn v&agrave; tươi trẻ.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">HƯỚNG DẪN SỬ DỤNG :</span></span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong>- </strong><span style="background-color:white"><strong>Bước 1: </strong>L&agrave;m sạch da, lau kh&ocirc; v&agrave; lăn thanh collagen l&ecirc;n v&ugrave;ng da c&oacute; n&aacute;m, t&agrave;n nhang;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong>- </strong><span style="background-color:white"><strong>Bước 2:</strong> Lấy 1 lượng kem vừa đủ thoa đều l&ecirc;n v&ugrave;ng da c&oacute; n&aacute;m t&agrave;n nhanh, massage nhẹ nh&agrave;ng.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Sử dụng 2 lần/ng&agrave;y v&agrave;o buổi s&aacute;ng v&agrave; tối.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><strong><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">BẢO QUẢN:</span></span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></span></p>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'face-nam-hoang-cung', 'Face nám Hoàng Cung', '', ''),
(74, 43, 'en', 'english version Face nám Hoàng Cung', 'english version <p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">Ức chế qu&aacute; tr&igrave;nh sản sinh ra sắc tố melanin trong tế b&agrave;o.&nbsp;</span></span></span><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">L&agrave;m s&aacute;ng da, mờ c&aacute;c vết th&acirc;m n&aacute;m, t&agrave;n nhang v&agrave; bổ sung c&aacute;c dưỡng chất cho l&agrave;n da mềm mịn v&agrave; tươi trẻ.</span></span></span></p>\r\n', '', '', 'english version <p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><strong>Face n&aacute;m Ho&agrave;ng Cung</strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">TH&Agrave;NH PHẦN:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Nước tinh khiết, chất nhũ h&oacute;a, Vaseline, Alpha Arbutin, Vitamin C, L&aacute; c&acirc;y Việt quất.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">C&Ocirc;NG DỤNG:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Ức chế qu&aacute; tr&igrave;nh sản sinh ra sắc tố melanin trong tế b&agrave;o.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">L&agrave;m s&aacute;ng da, mờ c&aacute;c vết th&acirc;m n&aacute;m, t&agrave;n nhang v&agrave; bổ sung c&aacute;c dưỡng chất cho l&agrave;n da mềm mịn v&agrave; tươi trẻ.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">HƯỚNG DẪN SỬ DỤNG :</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px">- <span style="background-color:white">Bước 1: L&agrave;m sạch da, lau kh&ocirc; v&agrave; lăn thanh collagen l&ecirc;n v&ugrave;ng da c&oacute; n&aacute;m, t&agrave;n nhang;</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px">- <span style="background-color:white">Bước 2: Lấy 1 lượng kem vừa đủ thoa đều l&ecirc;n v&ugrave;ng da c&oacute; n&aacute;m t&agrave;n nhanh, massage nhẹ nh&agrave;ng.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Sử dụng 2 lần/ng&agrave;y v&agrave;o buổi s&aacute;ng v&agrave; tối.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">BẢO QUẢN:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-family:Arial,Helvetica,sans-serif"><span style="font-size:16px"><span style="background-color:white">Nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></span></p>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'en-face-nam-hoang-cung', 'Face nám Hoàng Cung', '', ''),
(75, 44, 'vn', 'Face Đào Tiên Sứ', '<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="background-color:white">Cung cấp c&aacute;c dưỡng chất cần thiết cho da, gi&uacute;p da mềm mịn, hồng h&agrave;o khoẻ mạnh.&nbsp;</span></span><span style="font-size:16px">Bổ sung dưỡng chất gi&uacute;p trẻ h&oacute;a da, cải thiện v&agrave; dưỡng da trắng mịn.&nbsp;</span><span style="font-size:16px">Hương thơm tự nhi&ecirc;n dịu nhẹ gi&uacute;p thoải m&aacute;i v&agrave; sảng kho&aacute;i hơn.</span></p>\r\n', '', '', '<p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:16px"><strong><span style="background-color:white">Face Đ&agrave;o Ti&ecirc;n Sứ</span></strong></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><strong><span style="font-size:16px"><span style="background-color:white">TH&Agrave;NH PHẦN:</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="background-color:white">Nước tinh khiết, Nha đam, Vaseline, Vitamin E.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><strong><span style="font-size:16px"><span style="background-color:white">C&Ocirc;NG DỤNG:</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="background-color:white">- Cung cấp c&aacute;c dưỡng chất cần thiết cho da, gi&uacute;p da mềm mịn, hồng h&agrave;o khoẻ mạnh.</span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px">- Bổ sung dưỡng chất gi&uacute;p trẻ h&oacute;a da, cải thiện v&agrave; dưỡng da trắng mịn.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="background-color:white">- </span>Hương thơm tự nhi&ecirc;n dịu nhẹ gi&uacute;p thoải m&aacute;i v&agrave; sảng kho&aacute;i hơn.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><strong><span style="font-size:16px"><span style="background-color:white">HƯỚNG DẪN SỬ DỤNG :</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><strong>Bước 1:</strong> Rửa sạch mặt, lau ẩm.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><strong>Bước 2:</strong> Thoa một lớp mỏng kem l&ecirc;n mặt v&agrave; massage nhẹ nh&agrave;ng theo h&igrave;nh xoắn ốc.Sử dụng h&agrave;ng ng&agrave;y s&aacute;ng v&agrave; tối để đem lại hiệu quả tốt nhất.</span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><strong><span style="font-size:16px"><span style="background-color:white">BẢO QUẢN:</span></span></strong></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="background-color:white">Để nơi kh&ocirc; tho&aacute;ng, tr&aacute;nh &aacute;nh nắng trực tiếp. Để xa tầm tay trẻ em.</span></span></p>\r\n', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', 0, 'face-dao-tien-su', 'Face Đào Tiên Sứ', '', ''),
(76, 44, 'en', 'english version Face Đào Tiên Sứ', 'english version <p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">Cung cấp c&aacute;c dưỡng chất cần thiết cho da, gi&uacute;p da mềm mịn, hồng h&agrave;o khoẻ mạnh.&nbsp;</span></span></span><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif">Bổ sung dưỡng chất gi&uacute;p trẻ h&oacute;a da, cải thiện v&agrave; dưỡng da trắng mịn.&nbsp;</span></span><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif">Hương thơm tự nhi&ecirc;n dịu nhẹ gi&uacute;p thoải m&aacute;i v&agrave; sảng kho&aacute;i hơn.</span></span></p>\r\n', '', '', 'english version <p style="margin-left:0cm; margin-right:0cm; text-align:justify"><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif"><strong><span style="background-color:white">Face Đ&agrave;o Ti&ecirc;n Sứ</span></strong></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm; text-align:justify">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">TH&Agrave;NH PHẦN:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">Nước tinh khiết, Nha đam, Vaseline, Vitamin E.</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm">&nbsp;</p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="font-family:&quot;Times New Roman&quot;,serif"><span style="background-color:white">C&Ocirc;NG DỤNG:</span></span></span></p>\r\n\r\n<p style="margin-left:0cm; margin-right:0cm"><span style="font-size:16px"><span style="font-fam